INSERT INTO `wm_postmeta` VALUES("1391", "174", "_SnS", "a:2:{s:7:\"scripts\";a:1:{s:7:\"scripts\";s:2285:\"     if(\"bgimg\" in localStorage){\r\n       //  alert(localStorage.bgimg);\r\n       $(\'#container\').css(\'background-image\',\'url(\"\'+localStorage.bgimg+\'\")\');\r\n     }else{\r\n       //  alert(\"no\");\r\n     }\r\n\r\n		function readURL(input) {\r\n            if (input.files && input.files[0]) {\r\n                var reader = new FileReader();\r\n\r\n                reader.onload = function (e) {\r\n					localStorage.bgimg = e.target.result;\r\n                    $(\'#blah\').attr(\'src\', e.target.result);\r\n                    $(\'#container\').css(\'background-image\',\'url(\"\'+e.target.result+\'\")\');\r\n                };\r\n\r\n                reader.readAsDataURL(input.files[0]);\r\n            }\r\n        }\r\n       \r\n\r\n/// INIT CANVAS\r\nvar stage = new Konva.Stage({\r\n    container : \"container\",\r\n    width : 3000,\r\n    height : 500\r\n});\r\nvar layer = new Konva.Layer();\r\nstage.add(layer);\r\n\r\nconsole.log(\"start\");\r\n\r\n/////MENU\r\nvar pics = [\"http://konvajs.github.io/assets/lion.png\", \"http://konvajs.github.io/assets/monkey.png\",];\r\n\r\nvar $menu = $(\'#menu\');\r\n$.each(pics, function( index, value ) {\r\n    $(\"<img/>\")    // create image\r\n        .attr(\'src\', value) // set src to image link\r\n        .appendTo($menu)\r\n        .on(\'click\', function() {\r\n            // this here is image object\r\n            var src = this.src;\r\n            console.log(\"on\");\r\n            // create new Konva.Image from src attribute\r\n            Konva.Image.fromURL(src, function(image) {\r\n                console.log(\"click\");\r\n                // make it draggable\r\n                image.setAttrs({\r\n                    draggable: true\r\n                });\r\n                // append to layer\r\n                layer.add(image);\r\n                // update layer\r\n                layer.draw();\r\n            })\r\n\r\n        });\r\n});\r\n\r\nfunction toDraggable(src){\r\n\r\n    Konva.Image.fromURL(src.attr(\"src\"), function(image) {\r\n                console.log(\"click\");\r\n                // make it draggable\r\n                image.setAttrs({\r\n                    draggable: true,\r\n                    width: src.width(),\r\n                    height:  src.height()\r\n                });\r\n                // append to layer\r\n                layer.add(image);\r\n                // update layer\r\n                layer.draw();\r\n            })\r\n}\r\n\";}s:6:\"styles\";a:1:{s:6:\"styles\";s:444:\"  #container{\r\n    background-image:url(\"https://i.gr-assets.com/images/S/compressed.photo.goodreads.com/hostedimages/1516482298i/24895171._SX540_.jpg\") ;\r\n    /* Center and scale the image nicely */\r\n    background-position: center;\r\n    background-repeat: no-repeat;\r\n    background-size: cover;\r\n\r\n    border:solid 1px;\r\n    width:100%;\r\n    height:500px;\r\n}\r\n\r\n.select{\r\n    text-align: center;\r\n    height:500px;\r\n    overflow-y:scroll;\r\n}\";}}");
