INSERT INTO `wm_posts` VALUES("178", "1", "2018-04-23 16:36:53", "2018-04-23 16:36:53", "\r\n    <script src=\"https://ajax.googleapis.com/ajax/libs/jquery/3.3.1/jquery.min.js\"></script>\r\n    <script src=\"https://cdn.rawgit.com/konvajs/konva/0.9.5/konva.min.js\"></script>\r\n  <style>\r\n\r\n  #container{\r\n    background-image:url(\"https://hsto.org/files/e0d/e4d/e3a/e0de4de3acac4bd4b716e6c62ed17fba.jpg\") ;\r\n    /* Center and scale the image nicely */\r\n    background-position: center;\r\n    background-repeat: no-repeat;\r\n    background-size: cover;\r\n\r\n    border:solid 1px;\r\n    width:100%;\r\n    height:500px;\r\n}\r\n  </style>\r\n\r\n    <div id=\"menu\"></div>\r\n    <div id=\"container\"></div>\r\n    \r\n\r\n  <script>\r\n/// INIT CANVAS\r\nvar stage = new Konva.Stage({\r\n    container : \"container\",\r\n    width : 3000,\r\n    height : 500\r\n});\r\nvar layer = new Konva.Layer();\r\nstage.add(layer);\r\n\r\nconsole.log(\"start\");\r\n\r\n/////MENU\r\nvar pics = [\"http://konvajs.github.io/assets/lion.png\", \"http://konvajs.github.io/assets/monkey.png\"];\r\n\r\nvar $menu = $(\'#menu\');\r\n$.each(pics, function( index, value ) {\r\n    $(\"<img/>\")    // create image\r\n        .attr(\'src\', value) // set src to image link\r\n        .appendTo($menu)\r\n        .on(\'click\', function() {\r\n            // this here is image object\r\n            var src = this.src;\r\n            console.log(\"on\");\r\n            // create new Konva.Image from src attribute\r\n            Konva.Image.fromURL(src, function(image) {\r\n                console.log(\"click\");\r\n                // make it draggable\r\n                image.setAttrs({\r\n                    draggable: true\r\n                });\r\n                // append to layer\r\n                layer.add(image);\r\n                // update layer\r\n                layer.draw();\r\n            })\r\n        });\r\n});\r\n\r\n\r\n  </script>", "coba", "", "inherit", "closed", "closed", "", "174-revision-v1", "", "", "2018-04-23 16:36:53", "2018-04-23 16:36:53", "", "174", "http://localhost/wcmarket/2018/04/23/174-revision-v1/", "0", "revision", "", "0");
INSERT INTO `wm_posts` VALUES("179", "1", "2018-04-23 16:38:49", "2018-04-23 16:38:49", "<script src=\"https://ajax.googleapis.com/ajax/libs/jquery/3.3.1/jquery.min.js\"></script>\r\n<script src=\"https://cdn.rawgit.com/konvajs/konva/0.9.5/konva.min.js\"></script>\r\n\r\n<style>\r\n  #container{\r\n    background-image:url(\"https://hsto.org/files/e0d/e4d/e3a/e0de4de3acac4bd4b716e6c62ed17fba.jpg\") ;\r\n    /* Center and scale the image nicely */\r\n    background-position: center;\r\n    background-repeat: no-repeat;\r\n    background-size: cover;\r\n\r\n    border:solid 1px;\r\n    width:100%;\r\n    height:500px;\r\n}\r\n  </style>\r\n<div id=\"menu\"></div>\r\n<div id=\"container\"></div>\r\n<script>\r\n/// INIT CANVAS\r\nvar stage = new Konva.Stage({\r\n    container : \"container\",\r\n    width : 3000,\r\n    height : 500\r\n});\r\nvar layer = new Konva.Layer();\r\nstage.add(layer);\r\n\r\nconsole.log(\"start\");\r\n\r\n/////MENU\r\nvar pics = [\"http://konvajs.github.io/assets/lion.png\", \"http://konvajs.github.io/assets/monkey.png\"];\r\n\r\nvar $menu = $(\'#menu\');\r\n$.each(pics, function( index, value ) {\r\n    $(\"<img/>\")    // create image\r\n        .attr(\'src\', value) // set src to image link\r\n        .appendTo($menu)\r\n        .on(\'click\', function() {\r\n            // this here is image object\r\n            var src = this.src;\r\n            console.log(\"on\");\r\n            // create new Konva.Image from src attribute\r\n            Konva.Image.fromURL(src, function(image) {\r\n                console.log(\"click\");\r\n                // make it draggable\r\n                image.setAttrs({\r\n                    draggable: true\r\n                });\r\n                // append to layer\r\n                layer.add(image);\r\n                // update layer\r\n                layer.draw();\r\n            })\r\n        });\r\n});\r\n\r\n\r\n  </script>", "coba", "", "inherit", "closed", "closed", "", "174-revision-v1", "", "", "2018-04-23 16:38:49", "2018-04-23 16:38:49", "", "174", "http://localhost/wcmarket/2018/04/23/174-revision-v1/", "0", "revision", "", "0");
