INSERT INTO `wp_lipistoptions` VALUES("472", "_transient_dash_v2_88ae138922fe95674369b1cb3d215a2b", "<div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'https://wordpress.org/news/2018/08/wordpress-4-9-8-maintenance-release/\'>WordPress 4.9.8 Maintenance Release</a></li></ul></div><div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'https://wptavern.com/gutenberg-3-7-released-introduces-spotlight-mode\'>WPTavern: Gutenberg 3.7 Released, Introduces Spotlight Mode</a></li><li><a class=\'rsswidget\' href=\'https://wptavern.com/wpweekly-episode-329-gutenberg-forks-and-wordpress-development-cycles\'>WPTavern: WPWeekly Episode 329  Gutenberg, Forks, and WordPress Development Cycles</a></li><li><a class=\'rsswidget\' href=\'https://wptavern.com/distributor-plugin-now-publicly-available-adds-gutenberg-support\'>WPTavern: Distributor Plugin Now Publicly Available, Adds Gutenberg Support</a></li></ul></div>", "no");
