INSERT INTO `wp_options` VALUES("185", "_transient_feed_d117b5738fbd35bd8c0391cda1f2b5d9", "a:4:{s:5:\"child\";a:1:{s:0:\"\";a:1:{s:3:\"rss\";a:1:{i:0;a:6:{s:4:\"data\";s:3:\"\n\n\n\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:7:\"version\";s:3:\"2.0\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:1:{s:0:\"\";a:1:{s:7:\"channel\";a:1:{i:0;a:6:{s:4:\"data\";s:61:\"\n	\n	\n	\n	\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:1:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:16:\"WordPress Planet\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:28:\"http://planet.wordpress.org/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"language\";a:1:{i:0;a:5:{s:4:\"data\";s:2:\"en\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:47:\"WordPress Planet - http://planet.wordpress.org/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"item\";a:50:{i:0;a:6:{s:4:\"data\";s:13:\"\n	\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:34:\"WPTavern: Gutenberg 3.5.0 Released\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"https://wptavern.com/?p=83084\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:45:\"https://wptavern.com/gutenberg-3-5-0-released\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:1260:\"<p><a href=\"https://wordpress.org/plugins/gutenberg/\">Gutenberg</a> 3.5.0 <a href=\"https://make.wordpress.org/core/2018/08/09/whats-new-in-gutenberg-9th-august/\">is available</a> for download and polishes existing features. This release adds an edit button to embed blocks that allows users to edit the source URL. </p>\n\n<img />Edit URL Button on Embed Blocks\n\n<p>The contrast has been increased for input fields and check boxes have visually more distinct states as the following video shows. </p>\n\n\n\nMore Distinct States for Check boxes\n\n<p>One notable change is the <a href=\"https://github.com/WordPress/gutenberg/pull/8640\">addition of a warning</a> that displays if Cloudflare blocks REST API requests. This issue <a href=\"https://github.com/WordPress/gutenberg/issues/2704\">was reported</a> last September and it turns out that the PUT request is sometimes blocked by Cloudflare.</p>\n\n<p>Gary Pendergast <a href=\"https://github.com/WordPress/gutenberg/issues/2704#issuecomment-410563259\">reached out</a> to Cloudflare and the company deployed a fix earlier this week. </p>\n\n<p>To see a full list of changes in this release, check out <a href=\"https://make.wordpress.org/core/2018/08/09/whats-new-in-gutenberg-9th-august/\">the changelog</a>. </p>\n\n<p></p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Fri, 10 Aug 2018 21:16:15 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:13:\"Jeff Chandler\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:1;a:6:{s:4:\"data\";s:13:\"\n	\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:71:\"WPTavern: Ephox, Creators of TinyMCE, Rebrand to Tiny Technologies Inc.\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"https://wptavern.com/?p=83078\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:79:\"https://wptavern.com/ephox-creators-of-tinymce-rebrand-to-tiny-technologies-inc\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:1948:\"<p>Ephox, the creators of the <a href=\"https://www.tiny.cloud/features/\">TinyMCE editor</a> library that&#8217;s been used by WordPress for more than 10 years, has <a href=\"https://go.tiny.cloud/blog/a-tiny-name-change/\">changed its company name</a> to Tiny Technologies Incorporated. </p>\n\n<p>The new name provides an at-a-glance view of what the company does and establishes a tighter relationship with its flagship product. </p>\n\n<blockquote class=\"wp-block-quote\"><p>To make it easier for you to understand who we are and what we are working on, we have decided to put all of our efforts behind the Tiny brand. Renaming our company to Tiny Technologies Inc. demonstrates the efforts we are making to have TinyMCE the best choice for online rich text editing.</p><cite>Andrew Roberts, CEO and Co-founder of Tiny<br /></cite></blockquote>\n\n<p>In addition to the name change, the company has migrated its web presence to a new domain, <a href=\"https://www.tiny.cloud/\">tiny.cloud</a>. The Cloud top-level domain is managed byAruba PEC SpA, a wholly owned subsidiary of <a href=\"https://www.aruba.it/en/home.aspx\">Aruba S.p.A.</a>, one of the largest webhosting providers in Europe. </p>\n\n<p>According to Andrew Roberts, CEO and Co-founder of Tiny, existing customers and vendors of Ephox Corporation don&#8217;t need to do anything different since the company is registered to do business under both names. </p>\n\n<p>Roberts also shared insight into the work being done on <a href=\"https://textbox.io/\">Textbox.io</a>, the company&#8217;s mobile-first text editor. &#8220;We continue to support and release updates to our mobile-first online rich text editor, Textbox.io,&#8221; he said. &#8220;By the end of 2019, we do see both TinyMCE and Textbox.io coming together but for now, they are independent projects.&#8221;</p>\n\n<p>TinyMCE is an <a href=\"https://www.tiny.cloud/download/\">open source project</a> that is licensed under LGPL 2.1. </p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Thu, 09 Aug 2018 23:07:20 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:13:\"Jeff Chandler\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:2;a:6:{s:4:\"data\";s:13:\"\n	\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:89:\"WPTavern: Yoast SEO Team is Testing Gutenberg Integration Coming in Version 8.0 Next Week\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"https://wptavern.com/?p=83059\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:100:\"https://wptavern.com/yoast-seo-team-is-testing-gutenberg-integration-coming-in-version-8-0-next-week\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:5899:\"<p>Joost de Valk tweeted a sneak peek of the Gutenberg integration the Yoast SEO team is currently testing. The <a href=\"https://wordpress.org/plugins/wordpress-seo/\" rel=\"noopener noreferrer\" target=\"_blank\">plugin</a> has more than 5 million active installations and is one that packs a lot into its wide meta box. Users have been curious about what compatibility with the new editor will look like for Yoast SEO. de Valk tweeted a preview of the plugin working inside the Gutenberg UI:</p>\n<blockquote class=\"twitter-tweet\">\n<p lang=\"en\" dir=\"ltr\">Officially still on holiday&#8230; But, testing our <a href=\"https://twitter.com/yoast?ref_src=twsrc%5Etfw\">@yoast</a> SEO Gutenberg integration as the first real integration piece is due next week, this is making me happy (and extremely proud of our team):<a href=\"https://twitter.com/hashtag/gutenberg?src=hash&ref_src=twsrc%5Etfw\">#gutenberg</a> <a href=\"https://twitter.com/hashtag/wordpress?src=hash&ref_src=twsrc%5Etfw\">#wordpress</a> <a href=\"https://twitter.com/hashtag/yoast?src=hash&ref_src=twsrc%5Etfw\">#yoast</a> <a href=\"https://t.co/UHFAdH0wAt\">pic.twitter.com/UHFAdH0wAt</a></p>\n<p>&mdash; Joost de Valk (@jdevalk) <a href=\"https://twitter.com/jdevalk/status/1027284212511109131?ref_src=twsrc%5Etfw\">August 8, 2018</a></p></blockquote>\n<p></p>\n<p>For the past several months the team has been making progress on the plugin&#8217;s <a href=\"https://github.com/Yoast/wordpress-seo/issues/9189\" rel=\"noopener noreferrer\" target=\"_blank\">Gutenberg integration roadmap</a>. Yoast SEO 8.0 is coming next week with the first round of compatibility in place.</p>\n<p>&#8220;8.0 is coming on Tuesday with most of it, some bits are relying on the integration of an annotations API in Gutenberg, which is not entirely under our control,&#8221; de Valk said. &#8220;We need that to do markings like we do in the classic editor to highlight where your errors are. Feature complete is quite a while off &#8211; we want to do a LOT. Gutenberg is opening a whole new playing field for us in terms of more in context, actionable feedback.&#8221;</p>\n<p>Future versions of Yoast SEO will offer more features inline, instead of housing them in a single meta box.</p>\n<p>&#8220;We started by breaking down all our features, and seeing where we could integrate them into Gutenberg,&#8221; de Valk said in a <a href=\"https://yoast.com/gutenberg-integration/\" rel=\"noopener noreferrer\" target=\"_blank\">post</a> outlining Yoast SEO&#8217;s planned approach back in October 2017. &#8220;We dont think holding on to a single, massive box below the editor will best serve our customers. Wed much rather integrate right where the action happens, and Gutenberg offers us that chance.&#8221;</p>\n<p>For example, readability analysis can be shown on a per-block basis to provide more fine-grained feedback:</p>\n<a href=\"https://i2.wp.com/wptavern.com/wp-content/uploads/2018/08/readability-analysis.jpg?ssl=1\"><img /></a>image credit: <a href=\"https://yoast.com/gutenberg-integration/\">Yoast SEO</a>\n<p>&#8220;Youll get actionable feedback in context,&#8221; de Valk said. &#8220;You dont have to scroll down to a meta box to see the advice and scroll up again to the place where you should implement it. If we give feedback per block, you will get a better understanding of all the factors that influence SEO.&#8221;</p>\n<p>The Yoast SEO team has been enthusiastic about what the Gutenberg era will bring to WordPress. In a recent <a href=\"https://twitter.com/jdevalk/status/1026084237508788225\" rel=\"noopener noreferrer\" target=\"_blank\">Twitter thread</a>, de Valk summarized his thoughts on the project and described the direction the Yoast SEO plugin is headed:</p>\n<blockquote><p>[I&#8217;ve] been thinking about Gutenberg more lately, as discussion about it is growing now that it&#8217;s near to WordPress core inclusion. What is most important in what we&#8217;re doing is something I think most people don&#8217;t see yet: The new &#8216;blocks&#8217; that Gutenberg introduces allow us to, much more easily, make content items instead of web pages the smallest particle of the web.</p>\n<p>Questions and answers, how to&#8217;s, recipes, suddenly all of them can much more easily have metadata, and be reused. This is particularly important to SEOs: the search engines, driven by voice search requirements, are searching for answers, more than for &#8216;just&#8217; URLs to send traffic to, and blocks allow us to give them those answers, in a format both they and users can understand. While doing this, it allows us to keep the separation of content and design, something we definitely need to think more about.</p></blockquote>\n<p>With Gutenberg compatibility on its way from widely-used plugins like ACF and Yoast SEO, users can have confidence more plugins they depend on will follow suit. These major players are the first cracks in the ice across the ecosystem that will soon make Gutenberg-support the standard for any product that wants to compete.</p>\n<p>de Valk encouraged users to look at the advent of Gutenberg as a door to new opportunities during this transition time.</p>\n<p>&#8220;Gutenberg is not &#8216;done,\'&#8221; de Valk said. &#8220;I think it&#8217;s ready to ship, but I also think it opens up a world of new opportunities and discussions. It&#8217;s not all &#8216;right&#8217; yet either, so there will have to be changes as more people use it and get used to it. I do realize this is a drastic change in some ways. But it&#8217;s also not half as bad as people think it is. I have seen people use it for the first time, most people get used to it very quickly. Last but not least: you don&#8217;t have to switch now. The classic editor plugin exists for a reason. You can put off your switch for a year, or even two. But eventually I think everyone will see the power of the new editor and will switch.&#8221;</p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Thu, 09 Aug 2018 21:54:16 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:13:\"Sarah Gooding\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:3;a:6:{s:4:\"data\";s:13:\"\n	\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:100:\"WPTavern: Advanced Custom Fields to Add Gutenberg Compatibility in Version 5.0, Slated for September\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"https://wptavern.com/?p=83016\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:110:\"https://wptavern.com/advanced-custom-fields-to-add-gutenberg-compatibility-in-version-5-0-slated-for-september\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:4880:\"<p><a href=\"https://i2.wp.com/wptavern.com/wp-content/uploads/2018/08/Screen-Shot-2018-08-08-at-10.50.38-PM.png?ssl=1\"><img /></a></p>\n<p>The <a href=\"https://wordpress.org/plugins/advanced-custom-fields/\" rel=\"noopener noreferrer\" target=\"_blank\">Advanced Custom Fields</a> (ACF) team published an <a href=\"https://www.advancedcustomfields.com/blog/the-state-of-acf-in-a-gutenberg-world/\" rel=\"noopener noreferrer\" target=\"_blank\">announcement</a> today, assuring users that Gutenberg compatibility is in the works and will be available in version 5.0 of the free version in September 2018. The plugin, created by Elliot Condon, makes it easy for developers to add custom fields to WordPress edit screens, including posts, users, taxonomy terms, and media.</p>\n<p>ACF is widely used with more than a million active installations. Developers have been concerned about whether or not their custom fields would continue to work on their clients&#8217; websites after the new editor makes its debut in WordPress 5.0.</p>\n<p>Gutenberg treats legacy metaboxes as second class citizens in the interface, stuffed at the bottom of the new edit screen. Metaboxes should, however, continue to work as expected. The ACF announcement lets developers know what to expect for how their metaboxes will appear inside Gutenberg:</p>\n<blockquote><p>By default, our beloved metaboxes are pushed all the way to the bottom of the screen in an awkward attempt to retain compatibility. This placement feels very much like an afterthought from the Gutenberg developers and creates a disjointed editing experience for those of us (1+ million awesome ACF users) who extend the edit screens with extra fields.</p>\n<p>While this setup isnt ideal, we will continue to work within the system to ensure that ACF is integrated into the Gutenberg UI to the fullest extent possible.</p></blockquote>\n<p>The free version of ACF will introduce Gutenberg compatibility in its version 5 release, which is slated for early September 2018. The compatibility update will only apply to version 5 and later, so the ACF team urges developers to running 4.x to upgrade to 5 as soon as it becomes available. Developers can also elect to upgrade early by turning on ACF&#8217;s <a href=\"https://www.advancedcustomfields.com/resources/upgrade-guide-version-5/\" rel=\"noopener noreferrer\" target=\"_blank\">Early Access</a> feature.</p>\n<p>The most surprising news is that ACF is introducing its own &#8220;ACF Blocks,&#8221; an <code>acf_register_block()</code> function that allows developers to register their own custom blocks without having to learn JavaScript.</p>\n<blockquote><p>One of the big selling points of Gutenberg is the ability for developers to create custom blocks. The challenge is that the process is very JavaScript-intensive and not so friendly to PHP developers. But never fear, because ACF Blocks is here to turn that narrative upside down!</p>\n<p>We have been hard at work building a PHP friendly game-changer for you to quickly create new block types using ACF fields to power PHP templates!</p></blockquote>\n<p>ACF Blocks may be an unexpected development for those using the plugin, as the ACF team&#8217;s recent tweets have frequently communicated their disappointment with Gutenberg.</p>\n<blockquote class=\"twitter-tweet\">\n<p lang=\"en\" dir=\"ltr\">Just spent the day working with <a href=\"https://twitter.com/hashtag/gutenberg?src=hash&ref_src=twsrc%5Etfw\">#gutenberg</a>&#8230; <img src=\"https://s.w.org/images/core/emoji/11/72x72/1f915.png\" alt=\"\" class=\"wp-smiley\" /><img src=\"https://s.w.org/images/core/emoji/11/72x72/1f624.png\" alt=\"\" class=\"wp-smiley\" /><img src=\"https://s.w.org/images/core/emoji/11/72x72/1f626.png\" alt=\"\" class=\"wp-smiley\" /> Frustrated, annoyed and surprised at how buggy the editing experience is.</p>\n<p>&mdash; Advanced Custom Fields (@wp_acf) <a href=\"https://twitter.com/wp_acf/status/1026724444327178240?ref_src=twsrc%5Etfw\">August 7, 2018</a></p></blockquote>\n<p></p>\n<p>The availability of the ACF Blocks function is a strategic move that continues to make ACF indispensable for users who may not have made the time to improve their technical skills with regards to extending Gutenberg.</p>\n<p>&#8220;Using ACF to make custom blocks was something I thought could happen, but not necessarily would happen,&#8221; WordPress developer Roy Sivan <a href=\"https://twitter.com/royboy789/status/1027199624669327360\" rel=\"noopener noreferrer\" target=\"_blank\">said</a>. &#8220;They proved me wrong, and its a big deal.&#8221;</p>\n<p>The news should come as a relief for developers who have used ACF liberally on client sites and feel under the gun with Gutenberg&#8217;s proposed time frame for inclusion in core. The ACF team is working to make sure developers&#8217; customizations continue to work as seamlessly as possible when WordPress 5.0 is released.</p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Thu, 09 Aug 2018 04:57:18 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:13:\"Sarah Gooding\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:4;a:6:{s:4:\"data\";s:13:\"\n	\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:67:\"WPTavern: WPWeekly Episode 326  Long Live the Fields API Project\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:58:\"https://wptavern.com?p=83037&preview=true&preview_id=83037\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:74:\"https://wptavern.com/wpweekly-episode-326-long-live-the-fields-api-project\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:1684:\"<p>In this episode, <a href=\"http://jjj.me\">John James Jacoby</a> and I are joined by <a href=\"https://www.scottkclark.com/\">Scott Kingsley Clark</a>, lead developer of the <a href=\"http://pods.io/\">Pods framework</a>. Clark describes what it was like to lead the Fields API project, the difficulties of working hard on a project and not getting core buy-in, and why building the Fields API into core first would have made the transition to Gutenberg easier for users and developers.</p>\n<p>He looks back and describes what he possibly could have done differently and provides an update on Pods Framework&#8217;s compatibility with Gutenberg. To wrap up the show, Clark played us a song on his electric ukulele. To hear more tunes from Clark, check out <a href=\"https://soundcloud.com/soft-charisma\">his channel on Spotify</a>.</p>\n<h2>Stories Discussed:</h2>\n<p><a href=\"https://wptavern.com/wordpress-core-fields-api-project-is-seeking-new-leadership\">WordPress Core Fields API Project is Seeking New Leadership</a></p>\n<h2>WPWeekly Meta:</h2>\n<p><strong>Next Episode:</strong> Wednesday, August 15th 3:00 P.M. Eastern</p>\n<p>Subscribe to <a href=\"https://itunes.apple.com/us/podcast/wordpress-weekly/id694849738\">WordPress Weekly via Itunes</a></p>\n<p>Subscribeto <a href=\"https://www.wptavern.com/feed/podcast\">WordPress Weekly via RSS</a></p>\n<p>Subscribeto <a href=\"http://www.stitcher.com/podcast/wordpress-weekly-podcast?refid=stpr\">WordPress Weekly via Stitcher Radio</a></p>\n<p>Subscribeto <a href=\"https://play.google.com/music/listen?u=0#/ps/Ir3keivkvwwh24xy7qiymurwpbe\">WordPress Weekly via Google Play</a></p>\n<p><strong>Listen To Episode #326:</strong><br />\n</p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Thu, 09 Aug 2018 02:34:03 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:13:\"Jeff Chandler\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:5;a:6:{s:4:\"data\";s:13:\"\n	\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:78:\"WPTavern: Mythic: A WordPress Starter Theme by Justin Tadlock Now in Open Beta\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"https://wptavern.com/?p=83029\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:88:\"https://wptavern.com/mythic-a-wordpress-starter-theme-by-justin-tadlock-now-in-open-beta\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:4765:\"<p><a href=\"http://justintadlock.com/\">Justin Tadlock</a>, founder of <a href=\"https://themehybrid.com/\">Theme Hybrid</a>, has <a href=\"https://themehybrid.com/weblog/mythic-your-new-starting-point-for-theme-design\">released Mythic</a>, a starter theme that provides modern tools to get theme developers started on the right foot. </p>\n\n<blockquote class=\"wp-block-quote  is-style-large\"><p>Theming in 2018 is much different than theming in 2008. Without the right tools, it can be overwhelming to simply get started building even the most basic theme.</p><cite>Justin Tadlock</cite></blockquote>\n\n<p>While starter themes are nothing new in the WordPress space, Mythic and <a href=\"https://wptavern.com/wp-rig-a-wordpress-starter-theme-and-build-process-in-one\">WP Rig</a>take things to the next level and relatively share the same goal of providing a modern foundation to develop on top of. </p>\n\n<p>Part of the inspiration to build Mythic began two years ago. In trying to revamp the <a href=\"https://wordpress.org/themes/news/\">News theme</a>, Tadlock became frustrated with how difficult it was to use old coding methods and dropped the project.<br />><br /><br /><br /><br /><br />&#8220;In a lot of ways, it was the catalyst that started me down this road toward Mythic,&#8221;Tadlock said. &#8220;I didn&#8217;t realize it at the time. But, that&#8217;s where some of my frustration began with modern theme building.&#8221;</p>\n\n<p>Mythic supports PHP 5.6+ although Tadlock is strongly pushing developers towards PHP 7+. &#8220;Anything earlier than 5.6 means for clunky code thats just a headache to maintain,&#8221; he said. &#8220;WordPress, as a community, needs to be pushing people to update.&#8221;</p>\n\n<p>Support for SASS, LESS, CSS, and Stylus are built-in and developers can choose which language they prefer for builds. ES6+ was chosen for JavaScript and is commonly used for building Gutenberg blocks. </p>\n\n<p>Mythic comes with <a href=\"http://getbem.com/\">BEM</a> or Block-Element-Modifier. BEM is a methodology that enables developers to create reusable elements and sharing code in front-end environments. </p>\n\n<p>&#8220;BEM is a popular solution because it goes hand-in-hand with modern CSS pre-processors,&#8221; Tadlock said. &#8220;It also allows you to keep your styles flat and not get lost in <em>specificity hell</em>. This means smaller, faster stylesheets that are easier to override when you, a child theme author, or user need to do something custom.&#8221; The starter theme uses <a href=\"https://webpack.js.org/\">Webpack</a> in combination with <a href=\"https://github.com/JeffreyWay/laravel-mix\">Laravel Mix</a> to manage assets and modules.<br /></p>\n\n<p>Mythic has an <a href=\"https://themehybrid.com/weblog/working-with-the-view-class\">extended View class</a> that allows theme authors to use their preferred folder structure. Theme authors can also add custom data to theme templates. According to Tadlock, both features are not part of WordPress&#8217; native templating system. </p>\n\n<p>In addition to Mythic, Tadlock has continued to work on the <a href=\"https://themehybrid.com/hybrid-core\">Hybrid Core </a>framework. Hybrid Core is a required dependency that is added via Composer. </p>\n\n<p>Using Mythic with Hybrid Core exposes developers to features of the framework that they otherwise may not discover. &#8220;I rewrote nearly all of HC5 from scratch,&#8221; he said. &#8220;As a result, its leaner, more organized, and more cohesive.&#8221; The starter theme is also Gutenberg-ready.<br /></p>\n\n<h2>Tadlock Experiments with Sponsorship Pricing Model<br /></h2>\n\n<p>Mythic is in open beta and available for free via <a href=\"https://github.com/justintadlock/mythic\">GitHub</a>. The <a href=\"https://themehybrid.com/themes/mythic\">pricing model</a> is an honor system experiment. Tadlock is asking those who build projects for clients and generating a profit, to make a $99 sponsorship purchase. For commercially-sold themes, he is asking for $199. Both packages come with one year of support and access to the company&#8217;s Slack channel. <br /></p>\n\n<p>&#8220;I&#8217;m still taking feedback on the payment system,&#8221; Tadlock said. &#8220;It could change. There have been a few suggestions more of a lifetime/flat fee. I&#8217;d prefer to just get some generous sponsors and keep it all $free. We&#8217;ll see where that goes in this next month of the beta process.&#8221;</p>\n\n<p>Mythic&#8217;s beta ends on September 3rd in which he&#8217;ll release version 1.0. Until then, he is trying to get as much feedback as possible from developers. To file issues, submit feedback, and contribute, visit the <a href=\"https://github.com/justintadlock/mythic\">project&#8217;s GitHub page</a>. <br /></p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Thu, 09 Aug 2018 00:40:09 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:13:\"Jeff Chandler\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:6;a:6:{s:4:\"data\";s:13:\"\n	\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:61:\"WPTavern: Watch the Top 10 Sessions from WordCamp Europe 2018\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"https://wptavern.com/?p=82962\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:72:\"https://wptavern.com/watch-the-top-10-sessions-from-wordcamp-europe-2018\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:3937:\"<a href=\"https://i0.wp.com/wptavern.com/wp-content/uploads/2018/07/WCEU-2018.jpg?ssl=1\"><img /></a>photo credit: <a href=\"https://www.flickr.com/photos/wceu/41176987410/\">Val Vesa</a>\n<p>The results of the <a href=\"https://2018.europe.wordcamp.org/2018/08/07/results-of-the-wceu-2018-attendee-survey/\" rel=\"noopener noreferrer\" target=\"_blank\">WCEU 2018 Attendee Survey</a> are in: 98% of the 566 respondents from 49 countries said they would recommend WCEU to a friend and 90% are very likely to attend the next event in Berlin. Of those who participated in the survey, 49% were attending WCEU for the first time and 37% of them were attending their first WordCamp. Overall, attendees reported overwhelmingly positive experiences, which is a big affirmation of the organizing team that continues to lift the bar higher for WordCamps around the world.</p>\n<p>If you missed out on the WordCamp, the good news is that you can catch all of the sessions on WordPress.tv. The top 10 sessions have been curated by attendees who responded to the survey. These sessions cover some of the most important topics for WordPress professionals in 2018 and beyond:</p>\n<ol>\n<li><a href=\"https://wordpress.tv/2018/07/04/matt-mullenweg-a-summertime-update-keynote-and-qa/\" rel=\"noopener noreferrer\" target=\"_blank\">Keynote and Q&#038;A</a>  Matt Mullenweg</li>\n<li><a href=\"https://wordpress.tv/2018/07/11/joost-de-valk-technical-seo-to-grow-your-wordpress-business/\" rel=\"noopener noreferrer\" target=\"_blank\">Technical SEO to grow your WordPress business</a>  Joost de Valk</li>\n<li><a href=\"https://wordpress.tv/2018/07/06/morten-rand-hendriksen-the-ethics-of-web-design/\" rel=\"noopener noreferrer\" target=\"_blank\">The Ethics of Web Design</a>  Morten Rand-Hendriksen</li>\n<li><a href=\"https://wordpress.tv/2018/07/09/matias-ventura-beyond-gutenberg/\" rel=\"noopener noreferrer\" target=\"_blank\">Beyond Gutenberg</a>  Matas Ventura</li>\n<li><a href=\"https://wordpress.tv/2018/07/08/alberto-medina-thierry-muller-progressive-wordpress-themes/\" rel=\"noopener noreferrer\" target=\"_blank\">Progressive WordPress Themes</a>  Alberto Medina, Thierry Muller</li>\n<li><a href=\"https://wordpress.tv/2018/07/08/noel-tock-wordpress-in-2019/\" rel=\"noopener noreferrer\" target=\"_blank\">WordPress in 2019</a>  Noel Tock</li>\n<li><a href=\"https://wordpress.tv/2018/07/05/maja-benke-accessible-design-3/\" rel=\"noopener noreferrer\" target=\"_blank\">Accessible Design</a>  Maja Benke</li>\n<li><a href=\"https://wordpress.tv/2018/07/09/adam-silverstein-javascript-apis-in-wordpress/\" rel=\"noopener noreferrer\" target=\"_blank\">JavaScript APIs in WordPress</a>  Adam Silverstein</li>\n<li><a href=\"https://wordpress.tv/2018/07/06/john-maeda-wordpress-and-inclusive-design-2/\" rel=\"noopener noreferrer\" target=\"_blank\">WordPress and Inclusive Design</a>  John Maeda</li>\n<li><a href=\"https://wordpress.tv/2018/07/08/tammie-lister-anatomy-of-a-block-gutenberg-design-patterns/\" rel=\"noopener noreferrer\" target=\"_blank\">Anatomy of a block: Gutenberg design patterns</a>  Tammie Lister</li>\n</ol>\n<p>Attendees were also big fans of the workshops introduced at this event. 78% of respondents said they would like to see them again next year. A couple of the workshop recordings have yet to make it to WordPress.tv but may be available at a later time. The three most popular workshops attendees identified were all related to Gutenberg:</p>\n<ul>\n<li>(Gutenberg) Block Development with React  Zac Gordon, Julien Melissas</li>\n<li><a href=\"https://wordpress.tv/2018/08/07/lara-schenck-lets-build-a-gutenberg-block/\" rel=\"noopener noreferrer\" target=\"_blank\">Lets Build a Gutenberg Block</a>  Lara Schenck</li>\n<li>Roundtable with the Gutenberg team  The Gutenberg Team</li>\n</ul>\n<p>WordCamp Europe 2019 organizers are working on providing a more efficient registration process for workshops, as well as a greater capacity for attendees.</p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Wed, 08 Aug 2018 18:33:50 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:13:\"Sarah Gooding\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:7;a:6:{s:4:\"data\";s:13:\"\n	\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:78:\"WPTavern: First Look at Live Demo of the Gutenberg Content Editor for Drupal 8\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"https://wptavern.com/?p=82899\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:89:\"https://wptavern.com/first-look-at-live-demo-of-the-gutenberg-content-editor-for-drupal-8\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:9601:\"<p><a href=\"https://i0.wp.com/wptavern.com/wp-content/uploads/2018/08/drupal-gutenberg-project-e1533679727741.png?ssl=1\"><img /></a></p>\n<p><a href=\"https://www.drupaleurope.org/\" rel=\"noopener noreferrer\" target=\"_blank\">Drupal Europe</a> has just announced its <a href=\"https://www.drupaleurope.org/program/sessions\" rel=\"noopener noreferrer\" target=\"_blank\">program</a> for the upcoming event in Darmstadt, Germany, September 10-14. The conference will host more than 1,600 Drupal professionals and enthusiasts for 162 hours of sessions and 9 in-depth workshops.</p>\n<p>Automattic is sponsoring the <a href=\"https://www.drupaleurope.org/program/open-web-lounge\" rel=\"noopener noreferrer\" target=\"_blank\">Open Web Lounge</a> networking space and Matt Mullenweg will be joining Dries Buytaert and Barb Palser on a panel discussing <a href=\"https://www.drupaleurope.org/session/future-open-web-and-open-source\" rel=\"noopener noreferrer\" target=\"_blank\">the future of the open web and open source</a>.</p>\n<p>One interesting WordPress-related session on the program is titled <a href=\"https://www.drupaleurope.org/session/introducing-gutenberg-content-editor-drupal-8\" rel=\"noopener noreferrer\" target=\"_blank\">Introducing the Gutenberg content editor for Drupal 8</a>. This new module ports WordPress&#8217; Gutenberg editor over to Drupal. It was created by Per Andr Rnsen and his team <a href=\"https://www.frontkom.no/\" rel=\"noopener noreferrer\" target=\"_blank\">Frontkom</a>, a digital services agency based in Norway.</p>\n<p>The <a href=\"https://drupalgutenberg.org/\" rel=\"noopener noreferrer\" target=\"_blank\">Drupal Gutenberg</a> project aims to provide a new publishing experience based on WordPress&#8217; Gutenberg editor. The <a href=\"https://drupalgutenberg.org/demo\" rel=\"noopener noreferrer\" target=\"_blank\">live demo</a> on the site currently provides a frontend interactive implementation of Gutenberg inside Drupal, similar to WordPress&#8217; &#8220;Frontenberg&#8221; demo on <a href=\"https://wordpress.org/gutenberg/\" rel=\"noopener noreferrer\" target=\"_blank\">wordpress.org/gutenberg</a>. The latest implementation of the module will appear there but Rnsen said it is currently being revamped, as lots of things have happened with Gutenberg in the past few weeks.</p>\n<p><a href=\"https://i0.wp.com/wptavern.com/wp-content/uploads/2018/08/drupal-gutenberg.png?ssl=1\"><img /></a></p>\n<h3>How the Drupal Gutenberg Project was Born</h3>\n<p>Rnsen told us he first saw Gutenberg in 2017 and was inspired to use it for one of Frontkom&#8217;s media clients.</p>\n<p>&#8220;The thought of forking it came mid-2017, as we explored using Gutenberg as a lib for making a front page builder sponsored by the Google Digital News Initiative,&#8221; Rnsen said. &#8220;We ended up using it, and the Drupal module is a natural spin off.&#8221;</p>\n<p>Frontkom tailored the Gutenberg-based page builder for the media industry. It adds extra functionality to Gutenberg that allows users to position and tweak articles for the front page of an online newspaper. Rnsen said the project went well and his team plans to open source it but it&#8217;s too early to share anything right now.</p>\n<p>Following this successful experience using Gutenberg for a client, Frontkom began work on the Drupal Gutenberg project in early January 2018.</p>\n<p>&#8220;The main part of the work was to make Gutenberg more CMS agnostic,&#8221; Rnsen said. &#8220;First we forked it and maintained a fork. Later we started using Gutenberg as a dependency, testing a more maintainable approach.&#8221;</p>\n<p>Rnsen opened a ticket on GitHub, requesting that the Gutenberg team consider <a href=\"https://github.com/WordPress/gutenberg/issues/2780\" rel=\"noopener noreferrer\" target=\"_blank\">structuring the project to be more CMS-agnostic</a>. The ticket was closed fairly soon with the <a href=\"https://github.com/WordPress/gutenberg/issues/2780#issuecomment-342569414\" rel=\"noopener noreferrer\" target=\"_blank\">explanation</a> that it &#8220;doesn&#8217;t relate directly to the work going on with the project and its goals.&#8221;</p>\n<p>&#8220;We have very little, if any, upstream commits so far, as the WP core interest has been to just cater to WP needs so far,&#8221; Rnsen said. &#8220;But that just changed.&#8221;</p>\n<p>Gutenberg is moving towards making it easier to use outside of WordPress. The team is working on <a href=\"https://github.com/WordPress/gutenberg/issues/3955\" rel=\"noopener noreferrer\" target=\"_blank\">publishing all the React modules as npm packages</a>. Rnsen said he anticipates that decreasing the number of globals needed to make Gutenberg work will be a huge help for other CMSs.</p>\n<p>So far the Drupal Gutenberg project has not been shared widely but Rnsen said his team has seen a lot of interest from the Drupal tech community.</p>\n<p>&#8220;We have a blog post coming in English with more detail; we kind of saved it for after holidays,&#8221; Rnsen said. &#8220;But then it blew up, and devs keep contacting us wanting to help out.&#8221;</p>\n<p>Frontkom has been involved in the Drupal community for more than 10 years and WordPress for the past three years. Rnsen and his team believe that Gutenberg fills a gap in the Drupal space.</p>\n<p>&#8220;Drupal doesn&#8217;t have all those fancy WordPress page builders,&#8221; he said. &#8220;So that is really more interesting than just a new post edit UI  we want a unified way to build complex layouts.&#8221;</p>\n<p>Drupal already has an initiative for revamping its admin UI using React, and Rnsen said he thinks Gutenberg might contribute to how this can happen in Drupal core. In an ideal world, many open source projects could greatly benefit from a CMS-agnostic Gutenberg, with all of them contributing together towards its improvement.</p>\n<p>&#8220;To us as a company, it is extremely interesting to build out front-ends that can easily be moved between CMSs,&#8221; Rnsen said. &#8220;For open source CMSs in general, I think there will be a lot to learn from the implementation process. For example, D8 has the concept of &#8216;everything is a block.&#8217; This has made it easy for us to make Drupal core blocks available in Gutenberg  we just need to do some magic to expose block settings.&#8221;</p>\n<p>The Frontkom team are not only enthusiastic fans of Gutenberg&#8217;s interface, but also appreciate the way the project is run. Rnsen hasn&#8217;t been following the <a href=\"https://wptavern.com/gutenberg-plugin-garners-mixed-reactions-from-new-wave-of-testers\" rel=\"noopener noreferrer\" target=\"_blank\">community reaction to the latest round of Gutenberg testing</a> but said he thinks WordPress users will love it long term, since &#8220;most of the page builders out there have issues.&#8221;</p>\n<p>&#8220;Drupal can learn a lot from WordPress, the way they actually built the Gutenberg project  a transparent design process, lots of collaboration, and still highly efficient,&#8221; Rnsen said. &#8220;So, WordPress is definitely ahead of Drupal with regards to their admin UI/JS work. Since there is already a lot of interest in the Drupal community to do something similar, it is very refreshing to see someone go through it and succeed (hopefully).&#8221;</p>\n<p><a href=\"https://twitter.com/stevector\" rel=\"noopener noreferrer\" target=\"_blank\">Steve Persch</a>, lead developer advocate at Pantheon, spoke at DrupalCon in Nashville 2018 about <a href=\"https://events.drupal.org/nashville2018/sessions/whats-possible-wordpress-50\" rel=\"noopener noreferrer\" target=\"_blank\">what&#8217;s possible with WordPress 5.0</a> and also gave a session at Twin Cities Drupal Camp titled &#8220;<a href=\"https://2018.tcdrupal.org/session/everything-block-how-wordpress-rewrote-wysiwyg\" rel=\"noopener noreferrer\" target=\"_blank\">Everything is a Block: How WordPress Rewrote the WYSIWYG</a>.&#8221; In response to buzz on Twitter about the Drupal Gutenberg session, Persch <a href=\"https://twitter.com/stevector/status/1026461967630983169\" rel=\"noopener noreferrer\" target=\"_blank\">said</a> Drupal needs an editorial UX improvement to stay competitive, but not necessarily by adopting Gutenberg.</p>\n<p>&#8220;We have in-progress initiatives toward that end,&#8221; Persch said. &#8220;Gutenberg itself could turn into a distraction from that work. In both [presentations] I also mentioned that Paragraphs or Layout Builder could be better data model starting points for Gutenberg-like functionality. Delivering exactly the same thing as WordPress would be self-defeating. Structured content is the heart of Drupal. I see Gutenberg (or something like it) as a better UI for the corner of the data model (the Paragraphs part) that Drupal sites have had for years.&#8221;</p>\n<p>It will be interesting to see how different open source communities approach Gutenberg and how more diverse feedback from outside of WordPress could impact the project.</p>\n<p>The <a href=\"https://www.drupal.org/sandbox/marcofernandes/2981601\" rel=\"noopener noreferrer\" target=\"_blank\">Drupal Gutenberg module</a> can be downloaded from the project page. It&#8217;s currently marked as an experimental module and is recommended for developer use only. The <a href=\"https://drupalgutenberg.org/demo\" rel=\"noopener noreferrer\" target=\"_blank\">live demo</a> for the project will soon be revamped to reflect the latest updates with Gutenberg. You can also <a href=\"https://github.com/front/drupal-gutenberg\" rel=\"noopener noreferrer\" target=\"_blank\">follow the project&#8217;s development on GitHub</a>.</p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Tue, 07 Aug 2018 22:28:18 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:13:\"Sarah Gooding\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:8;a:6:{s:4:\"data\";s:13:\"\n	\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:75:\"WPTavern: Gutenberg Plugin Garners Mixed Reactions from New Wave of Testers\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"https://wptavern.com/?p=82891\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:86:\"https://wptavern.com/gutenberg-plugin-garners-mixed-reactions-from-new-wave-of-testers\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:15811:\"<a href=\"https://i0.wp.com/wptavern.com/wp-content/uploads/2017/10/ship-bottle.jpg?ssl=1\"><img /></a>photo credit: KaylaKandzorra <a href=\"http://www.flickr.com/photos/48077358@N02/4952091078\">i miss you grampa.</a> &#8211; <a href=\"https://creativecommons.org/licenses/by/2.0/\">(license)</a>\n<p><a href=\"https://wptavern.com/wordpress-4-9-8-released\" rel=\"noopener noreferrer\" target=\"_blank\">WordPress 4.9.8</a> was released as an automatic update last week, featuring the &#8220;Try Gutenberg&#8221; callout. The goal of the prompt is to get more users testing the new editor and to raise awareness. Within the first 72 hours of the prompt going into user dashboards, sites with Gutenberg installed have shot up to more than 80,000, a 300% increase. The plugin has been downloaded more than 147,000 times in the past week.</p>\n<p>Gutenberg feedback is pouring in from a wave of new testers, most of whom are finding out about the new editor for the first time. Reactions have varied widely across multiple social networks. The announcement on <a href=\"https://www.facebook.com/WordPress/posts/10156301634992911\" rel=\"noopener noreferrer\" target=\"_blank\">WordPress&#8217; official Facebook account</a> attracted many drive-by style comments that included negative reactions, confusion, and questions, with a few positive comments peppered in between.</p>\n<blockquote><p>Blocks arent doing it for me. Will classic continue to be available permanently? If I want a block visual editor I promise I will install one voluntarily.</p></blockquote>\n<blockquote><p>Im hoping gutenberg will be totally optional.</p></blockquote>\n<blockquote><p>I see only few users who are exited from Gutenberg. The rest is for leaving as it is. Why the guys from WP still want to implement it in the core? Make it a plugin. Why not a plugin whyyyyyy?</p></blockquote>\n<p>Twitter users trying Gutenberg for the first time were more enthusiastic about the potential of the new editor.</p>\n<blockquote class=\"twitter-tweet\">\n<p lang=\"en\" dir=\"ltr\">Absolutely love the new Medium-like Gutenberg WordPress editor. <img src=\"https://s.w.org/images/core/emoji/11/72x72/1f60d.png\" alt=\"\" class=\"wp-smiley\" /><img src=\"https://s.w.org/images/core/emoji/11/72x72/1f60d.png\" alt=\"\" class=\"wp-smiley\" /></p>\n<p>&mdash; Content Marketing Consultant (@Ebun_Oluwole) <a href=\"https://twitter.com/Ebun_Oluwole/status/1025827102401200128?ref_src=twsrc%5Etfw\">August 4, 2018</a></p></blockquote>\n<p></p>\n<blockquote class=\"twitter-tweet\">\n<p lang=\"es\" dir=\"ltr\">me encant Gutenberg, grande <a href=\"https://twitter.com/WordPress?ref_src=twsrc%5Etfw\">@WordPress</a> !!</p>\n<p>&mdash; Alex Zamorano (@alex_zamorano) <a href=\"https://twitter.com/alex_zamorano/status/1025752512593358848?ref_src=twsrc%5Etfw\">August 4, 2018</a></p></blockquote>\n<p></p>\n<blockquote class=\"twitter-tweet\">\n<p lang=\"en\" dir=\"ltr\">Hey <a href=\"https://twitter.com/WordPress?ref_src=twsrc%5Etfw\">@WordPress</a> Gutenberg is dope I approve <img src=\"https://s.w.org/images/core/emoji/11/72x72/1f44d-1f3fe.png\" alt=\"\" class=\"wp-smiley\" /></p>\n<p>&mdash; Bodega Hive <img src=\"https://s.w.org/images/core/emoji/11/72x72/1f3a8.png\" alt=\"\" class=\"wp-smiley\" /> (@breezeSZN) <a href=\"https://twitter.com/breezeSZN/status/1025538848439394304?ref_src=twsrc%5Etfw\">August 4, 2018</a></p></blockquote>\n<p></p>\n<p>While some testers are quietly <a href=\"https://github.com/WordPress/gutenberg/issues\" rel=\"noopener noreferrer\" target=\"_blank\">reporting issues on GitHub</a>, others have written long missives in the plugin&#8217;s reviews, begging WordPress not to force the new editor on users. Gutenberg reviews are currently at a 2.4-star average, slipping from the 2.7-star average it held prior to the 4.9.8 release. Those who have written reviews tend to have reactions at both ends of the spectrum.</p>\n<p><a href=\"https://i2.wp.com/wptavern.com/wp-content/uploads/2018/08/Screen-Shot-2018-08-06-at-3.26.57-PM.png?ssl=1\"><img /></a></p>\n<p>At this point in the testing phase, the reviews in the official directory paint a grim picture of WordPress dragging its users kicking and screaming into the Gutenberg era. However, there are many reasons why a tester might be motivated to write a negative review. Some may have ignored the advice not to use it in production, some may be testing it with incompatible plugins, some may not want any changes to their established workflow, and some may simply not like the interface.</p>\n<p><a href=\"https://i2.wp.com/wptavern.com/wp-content/uploads/2018/08/Screen-Shot-2018-08-05-at-10.24.03-PM.png?ssl=1\"><img /></a></p>\n<p>While it may seem that the plugin has been widely panned by new testers, those who are the most critical tend to be more motivated to write a review. Those who are pleasantly surprised at something that works for them don&#8217;t tend to comment publicly. Reviews cannot possibly tell the whole tale, but they are important to monitor for feedback that could help Gutenberg succeed.</p>\n<blockquote class=\"twitter-tweet\">\n<p lang=\"en\" dir=\"ltr\">Month 14 of Gutenberg on <a href=\"https://t.co/zWh3po88LS\">https://t.co/zWh3po88LS</a> and I will keep the tradition of posting a summary of the reviews. Sadly neither time or quantity of reviews is not helping the score. Are the review biased? Is this something users like but admins hate? <a href=\"https://t.co/OMvIdUgfP4\">pic.twitter.com/OMvIdUgfP4</a></p>\n<p>&mdash; Andrew Roberts (@andrew_roberts) <a href=\"https://twitter.com/andrew_roberts/status/1025452116024807424?ref_src=twsrc%5Etfw\">August 3, 2018</a></p></blockquote>\n<p></p>\n<h2>&#8220;Try Gutenberg&#8221; Callout Succeeds at Bringing More Testers and Feedback</h2>\n<p>WordPress 4.9.8&#8217;s &#8220;Gutenprompt&#8221; is doing exactly what it was intended to do &#8211; bring out more testers. The invitation has already succeeded at pulling out some quality feedback if you can sort through all the casual, angry one-liners.</p>\n<p>Steven Peters opened a lengthy <a href=\"https://wordpress.org/support/topic/in-one-word-no-please-read-full-review/\" rel=\"noopener noreferrer\" target=\"_blank\">review</a> with the following observations about the more cumbersome and unintuitive aspects of Gutenberg&#8217;s interface:</p>\n<blockquote><p>The interface is not cohesive in its design. For example, To place a block of a paragraph, a heading, a subheading, a bullet list and every other block, the user must click the + sign every single time, making it that much harder to go with the flow of writing, and is cumbersome and time-consuming. Time-consuming: a click for each block instead of just writing. More clicks equal wasted time.</p></blockquote>\n<p>In a review titled &#8220;<a href=\"https://wordpress.org/support/topic/lots-of-potential-but-too-soon-for-core/\" rel=\"noopener noreferrer\" target=\"_blank\">Lots of potential but too soon for core</a>,&#8221; Mark Wilkinson details several specific usability issues:</p>\n<blockquote><p>The interface I find confusing  I think it is because it is too minimal. I found that it was all too easy to add a block by mistake and then not knowing what the block was or why it was there.</p>\n<p>There is too much reliance on hover effects, with things appearing and disappearing all the time. I also find it hard to know where the focus is on the screen as it just uses a faint grey border.</p></blockquote>\n<p>Several reviewers were candid about their distaste for the concept of putting content into blocks. The Gutenberg team has readily communicated its vision for the block-based editor, but this tends to speak more to developers.</p>\n<p>&#8220;Why does every little thing have to be in a separate block?&#8221; one reviewer <a href=\"https://wordpress.org/support/topic/im-not-change-resistant-but-this-has-a-way-to-go/\" rel=\"noopener noreferrer\" target=\"_blank\">asked</a> before describing multiple usability issues with current block behavior. &#8220;That is a feature I think I actually detest. I do not want 50 zillion little fussy blocks on a page. I had entire blocks just disappear on me and a lot of the time, I was initially unsure of exactly what I did to make them disappear. This is bad. Some of the time it was placing a block, like an image block, and then deleting the image  the entire block went, which meant I had to go through the motions to add the block, then add an image block again, and then add my image again. Lame.&#8221;</p>\n<p>Multiple reviewers commented that previously simple tasks are much more complicated in Gutenberg. Others said after reading official replies to reviews, they felt that leaving specific feedback was a waste of time. Canned responses from the Gutenberg team gave some the impression that their feedback wasn&#8217;t heard or valued.</p>\n<blockquote><p>My feeling while reading the WordPress developer responses is that that they have no intention of either stopping or postponing Gutenberg no matter what bug or problem is given to their attention. &#8211; <a href=\"https://wordpress.org/support/topic/can-i-just-interject-here/\" rel=\"noopener noreferrer\" target=\"_blank\">@lauritasita</a></p></blockquote>\n<blockquote><p>I have read all the reviews (and also the Gutenberg-lovers replies) and it seems that it is not really a discussion. The question on what is missing in this editor is useless if people simply do not want such a feature in their install. All you do is trying to convince people that it would only take time to get used to a modern technology. In my eyes this is bullshit. I myself love new features  if they are useful. &#8211; <a href=\"https://wordpress.org/support/topic/time-to-respect-your-users-matt/\" rel=\"noopener noreferrer\" target=\"_blank\">@peg20</a></p></blockquote>\n<p>Based on the responses to reviews, it isn&#8217;t clear to testers whether the Gutenberg team is willing to make major changes to re-design features that are not easy to use or whether they are simply combing the reviews for feedback on bugs with the existing interface.</p>\n<h3>Classic Editor Plugin Installations are on the Rise as Users Prepare for WordPress 5.0</h3>\n<p>In the days following WordPress 4.9.8&#8217;s release, active installations of the <a href=\"https://wordpress.org/plugins/classic-editor/\" rel=\"noopener noreferrer\" target=\"_blank\">Classic Editor</a> plugin have jumped from 7,000 to 60,000, a 757% increase. However, none of these figures in isolation can tell the whole story of users&#8217; experience with the new editor.</p>\n<p>In <a href=\"https://www.facebook.com/groups/advancedwp/permalink/1986704551391749/?comment_id=1986914094704128&reply_comment_id=1986943451367859&comment_tracking=%7B%22tn%22%3A%22R9%22%7D\" rel=\"noopener noreferrer\" target=\"_blank\">response to criticism</a> on the Advanced WordPress Facebook group, Gutenberg contributor Gary Pendergast said he doesn&#8217;t see users installing the Classic Editor plugin as a negative reaction. &#8220;People ensuring that their site is ready for WordPress 5.0 is absolutely a good thing,&#8221; Pendergast said. &#8220;For a lot of sites, that means sticking with their current workflows for now.&#8221;</p>\n<p>Pendergast also said the growing number of Classic Editor installs is a good indication that WordPress users are &#8220;proactively maintaining their sites, and a reasonable indication that they intend to upgrade to WordPress 5.0.&#8221;</p>\n<p>&#8220;People certainly have strong feelings about it, and &#8216;fear of change&#8217; is absolutely a legitimate feeling: we need to provide the right tools to empower everyone who uses WordPress,&#8221; Pendergast said. &#8220;Some people will jump right into the brave new block-based world, some people will prefer to use the Classic Editor plugin as a way to keep their existing workflows. When WordPress 5.0 lands, neither way should be seen as the &#8216;correct&#8217; way &#8211; they&#8217;re both entirely legitimate.&#8221;</p>\n<p>One major theme in both positive and negative reviews of the plugin is the desire for Gutenberg to be opt-in for WordPress 5.0, instead of having it auto-enabled for all users.</p>\n<blockquote class=\"twitter-tweet\">\n<p lang=\"en\" dir=\"ltr\">The \"Try Gutenberg\" callout in WordPress 4.9.8 looks great! Would love to see this be the official approach when WordPress 5.0 is released vs auto-enabling. Give the users time to check it out before forcing it on them. <a href=\"https://t.co/QBiPh7IROe\">pic.twitter.com/QBiPh7IROe</a></p>\n<p>&mdash; Brad Williams (@williamsba) <a href=\"https://twitter.com/williamsba/status/1025025636149157889?ref_src=twsrc%5Etfw\">August 2, 2018</a></p></blockquote>\n<p></p>\n<p>Developers and agencies are expected to control that experience for their clients by installing the <a href=\"https://wordpress.org/plugins/classic-editor/\" rel=\"noopener noreferrer\" target=\"_blank\">Classic Editor</a> or <a href=\"https://wordpress.org/plugins/gutenberg-ramp/\" rel=\"noopener noreferrer\" target=\"_blank\">Gutenberg Ramp</a> for installations that may have compatibility issues. For millions of other WordPress users without their own developers or engineering teams, discovering Gutenberg auto-enabled after updating to WordPress 5.0 will be a moment of reckoning. On the other hand, phasing the new editor in over time may severely limit adoption and extinguish the ecosystem&#8217;s impetus to offer Gutenberg-compatible products.</p>\n<blockquote class=\"twitter-tweet\">\n<p lang=\"en\" dir=\"ltr\">Not auto-enabling it should be strongly considered for 5.0 and also why setting a specific release number for it was a mistake. These reviews have been absolutely brutal and the uptick in 1 stars has been from average users and not disgruntled or vocal developers.</p>\n<p>&mdash; Carl Hancock <img src=\"https://s.w.org/images/core/emoji/11/72x72/1f680.png\" alt=\"\" class=\"wp-smiley\" /> (@carlhancock) <a href=\"https://twitter.com/carlhancock/status/1026542036936056832?ref_src=twsrc%5Etfw\">August 6, 2018</a></p></blockquote>\n<p></p>\n<p>The development community, along with thousands of WordPress users who now have Gutenberg on their radar, will be watching to see how the team evaluates feedback from this new wave of testing. Up until this point, only the most curious and motivated WordPress users have taken Gutenberg for a spin. Widespread testing has just begun and we&#8217;re not yet seeing a complete picture of how well the new editor will be received.</p>\n<p>My <a href=\"https://wptavern.com/wordpress-new-gutenberg-editor-now-available-as-a-plugin-for-testing\" rel=\"noopener noreferrer\" target=\"_blank\">initial impression</a>, after first trying it a year ago, was that Gutenberg is the most exciting thing to happen to WordPress in a long time. After reading hundreds of negative reviews, I still believe in the block concept but am convinced that Gutenberg needs to deliver a beautiful writing experience in order to win people over.</p>\n<p>If WordPress is my home for writing on the web, I want to feel at home in the interface. I don&#8217;t want to have to hunt for actions buried two-clicks deep. When I use the editor I want to have the feeling of &#8220;I love writing here.&#8221; Gutenberg doesn&#8217;t deliver that yet.</p>\n<p>WordPress has an opportunity to provide the best writing experience on the web, instead of relegating it to the <a href=\"https://wptavern.com/bear-app-users-want-wordpress-publishing-integration\" rel=\"noopener noreferrer\" target=\"_blank\">myriad of dedicated writing apps</a> that don&#8217;t have publishing capabilities. A supremely well-designed editor for writing posts, in recognition of the platform&#8217;s blogging roots, would be the promise that hooks users to willingly sign on for years of dealing with Gutenberg&#8217;s shortcomings as it matures into a full-fledged site builder.</p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Tue, 07 Aug 2018 03:23:57 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:13:\"Sarah Gooding\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:9;a:6:{s:4:\"data\";s:13:\"\n	\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:75:\"WPTavern: WordCamp Ann Arbor Cancelled, Organizers Set Sights on 2019 Event\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"https://wptavern.com/?p=82921\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:85:\"https://wptavern.com/wordcamp-ann-arbor-cancelled-organizers-set-sights-on-2019-event\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:1284:\"<p><a href=\"https://2018.annarbor.wordcamp.org/\">WordCamp Ann Arbor MI</a>, is an annual Fall tradition that takes place each October. This year however, the event won&#8217;t be taking place. In an email sent out August 3rd, organizers explained why they cancelled this year&#8217;s event. </p>\n\n<blockquote class=\"wp-block-quote\"><p>Why did we decide to cancel WordCamp Ann Arbor this year? It&#8217;s a long story. The most significant factors involve issues with our venues. Then eventually finding ourselves with too little time to put together an event meeting the standards we strive for. None of us were interested in throwing together a last minute, half-baked event. So we made the call early and turned our attention to 2019.</p><cite>Kyle Maurer &#8211; Co-organizer of WordCamp Ann Arbor</cite></blockquote>\n\n<p>Organizers are already in the planning stages for WordCamp Ann Arbor 2019. Jackson, MI, about 45 minutes west of Ann Arbor is hosting a <a href=\"https://2018.jackson.wordcamp.org/\">WordCamp</a> this weekend. Tickets are $20 each with 35 remaining. </p>\n\n<p>For a detailed look into what it&#8217;s like to organize WordCamp Ann Arbor, check out <a href=\"http://kyleblog.net/2016/11/wordcamp-ann-arbor-2016-recap/\">Maurer&#8217;s post</a> from 2016. <br /></p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Mon, 06 Aug 2018 21:01:21 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:13:\"Jeff Chandler\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:10;a:6:{s:4:\"data\";s:13:\"\n	\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:92:\"Post Status: Considerations for eCommerce merchants, with Andrew Youderian of eCommerce Fuel\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"https://poststatus.com/?p=46845\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:102:\"https://poststatus.com/considerations-for-ecommerce-merchants-with-andrew-youderian-of-ecommerce-fuel/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:1768:\"<p>Welcome to the Post Status <a href=\"https://poststatus.com/category/draft\">Draft podcast</a>, which you can find <a href=\"https://itunes.apple.com/us/podcast/post-status-draft-wordpress/id976403008\">on iTunes</a>, <a href=\"https://play.google.com/music/m/Ih5egfxskgcec4qadr3f4zfpzzm?t=Post_Status__Draft_WordPress_Podcast\">Google Play</a>, <a href=\"http://www.stitcher.com/podcast/krogsgard/post-status-draft-wordpress-podcast\">Stitcher</a>, and <a href=\"http://simplecast.fm/podcasts/1061/rss\">via RSS</a> for your favorite podcatcher. Post Status Draft is hosted by Brian Krogsgard and co-host Brian Richards.</p>\n<p>In this episode, I bring on <a href=\"https://twitter.com/youderian\">Andrew Youderian</a>. Andrew runs eCommerce Fuel &#8212; a great website geared toward eCommerce store owners, specifically those making high six figures or seven figures in revenue per year.</p>\n<p>Andrew keeps his ear low to the ground in the eCommerce landscape and carries no specific WordPress bias. If anything his experience is in other platforms &#8212; making a discussion with him both on platforms and also just eCommerce in general particularly valuable to me.</p>\n<p></p>\n<h3>Episode Links</h3>\n<ul>\n<li><a href=\"https://ecommercefuel.com\">eCommerce Fuel</a></li>\n<li><a href=\"https://www.ecommercefuel.com/ecommerce-report-2018/#info-anchor\">State of the merchant</a></li>\n</ul>\n<h3>Sponsor: SiteGround</h3>\n<p>Engineered for speed, built for security, crafted for WordPress. <a href=\"https://www.siteground.com/poststatus\">SiteGround</a> offers feature-rich managed WordPress hosting with premium support, and is officially recommended by WordPress.org. Thanks to <a href=\"https://www.siteground.com/poststatus\">SiteGround</a> for being a Post Status partner.</p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Fri, 03 Aug 2018 21:17:20 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:15:\"Brian Krogsgard\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:11;a:6:{s:4:\"data\";s:13:\"\n	\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:34:\"WPTavern: WordPress 4.9.8 Released\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"https://wptavern.com/?p=82870\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:45:\"https://wptavern.com/wordpress-4-9-8-released\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:1203:\"<p>WordPress 4.9.8 is <a href=\"https://wordpress.org/news/2018/08/wordpress-4-9-8-maintenance-release/\">available for download</a> and is a maintenance release. Headlining this version is the &#8220;Try Gutenberg&#8221; callout. Note that not everyone will see the callout. Its visibility is determined <a href=\"https://wptavern.com/wordpress-4-9-8-rc-3-released-limits-try-gutenberg-callout-visibility\">based on certain criteria.</a></p>\n\n<img />Gutenberg Callout in WordPress 4.9.8\n\n<p>WordPress 4.9.8 continues to improve the foundation set forth by the privacy improvements that went into core earlier this year. For example, the type of request that is being confirmed is now included in the subject line for privacy confirmation emails. </p>\n\n<p>In total, this release has 46 bug fixes from more than 50 contributors. It was lead by <a href=\"https://profiles.wordpress.org/pbiron\">Paul Biron</a> and <a href=\"https://profiles.wordpress.org/joshuawold\">Joshua Wold</a>. Since 4.9.8 is a maintenance release, sites that are able will update automatically. To see a full list of changes, check out the <a href=\"https://make.wordpress.org/core/2018/08/02/wordpress-4-9-8/\">detailed changelog</a>. </p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Fri, 03 Aug 2018 00:12:05 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:13:\"Jeff Chandler\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:12;a:6:{s:4:\"data\";s:13:\"\n	\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:87:\"WPTavern: WordPress.com Partners with Pexels to Offer Diverse, Free Stock Photo Library\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"https://wptavern.com/?p=82856\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:97:\"https://wptavern.com/wordpress-com-partners-with-pexels-to-offer-diverse-free-stock-photo-library\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:2952:\"<a href=\"https://i2.wp.com/wptavern.com/wp-content/uploads/2018/08/pexels-photo-1181618.jpeg?ssl=1\"><img /></a>image credit: <a href=\"https://www.pexels.com/photo/three-women-in-front-of-desk-1181618/\">Christina Morillo from Pexels</a>\n<p>WordPress.com has partnered with <a href=\"https://www.pexels.com/\" rel=\"noopener noreferrer\" target=\"_blank\">Pexels</a>, a popular stock photo library, to offer convenient access to stock photos inside the post editor. Hundreds of high quality free stock photo sites have cropped up on the web over the past few years, but Pexels is differentiating itself with a commitment to hosting diversity-focused images and videos that represent a wide range of experiences. WordPress.com is working with them and other partners to <a href=\"https://wordpress.com/read/feeds/25823/posts/1945569908\" rel=\"noopener noreferrer\" target=\"_blank\">make more diverse images available to users</a>:</p>\n<blockquote><p>Stock-image libraries have historically struggled to represent all experiences, and often excluded photos of people of color, people with disabilities, or non-binary individuals. Pexels is working to change that, and since partnering with them weve helped incorporate diversity-focused libraries to their collection. Ultimately, we believe its on us to help find a solution to this problem, and avoid generic stock images that often perpetuate stereotypes.</p></blockquote>\n<p>WordPress.com users can access the images via the Add Media button. Self-hosted site owners with Jetpack-enabled sites can also access the library when composing new posts on WordPress.com. Automattic happiness engineer Anne McCarthy posted a demo of where to find the new images:</p>\n<p><a href=\"https://cloudup.com/c-ZzCzFS1Rn\"><img src=\"https://i2.wp.com/cldup.com/mJOr5iicyD.gif?resize=627%2C348&ssl=1\" alt=\"Media\" width=\"627\" height=\"348\" /></a></p>\n<p>More than 1,000 developers and companies are using the <a href=\"https://www.pexels.com/api/\" rel=\"noopener noreferrer\" target=\"_blank\">Pexels API</a>. The site regularly hosts diversity-focused photo <a href=\"https://www.pexels.com/challenges/\" rel=\"noopener noreferrer\" target=\"_blank\">challenges</a> with prizes for the best submissions. Photographers who are inspired to contribute can submit their work to the library but should be aware of its <a href=\"https://www.pexels.com/photo-license\" rel=\"noopener noreferrer\" target=\"_blank\">open license</a>.</p>\n<p>&#8220;I like the idea, and, as a photoblogger, Im open to sharing my images,&#8221; one reader commented on the announcement. &#8220;But I [think] the license rules are too open (for me). Im happy to share, attribution would be nice (not necessary), but editing of my photos seems a step too far.&#8221;</p>\n<p>Feedback on the library has been overwhelmingly positive so far and WordPress.com users have already downloaded more than 1 million images since the free stock image library was introduced.</p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Thu, 02 Aug 2018 23:45:58 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:13:\"Sarah Gooding\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:13;a:6:{s:4:\"data\";s:13:\"\n	\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:45:\"Dev Blog: WordPress 4.9.8 Maintenance Release\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:34:\"https://wordpress.org/news/?p=6165\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:71:\"https://wordpress.org/news/2018/08/wordpress-4-9-8-maintenance-release/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:9492:\"<p>We are pleased to announce the immediate availability of WordPress 4.9.8. This maintenance release fixes 46 bugs, enhancements and blessed tasks, including updating the Twenty Seventeen bundled theme.</p>\n<p><span>Following are the highlights of what is now available.</span></p>\n<h2>Try Gutenberg callout</h2>\n<p><span>Most users will now be presented with a notice in their WordPress dashboard. This</span>Try Gutenberg is an opportunity for users to use the Gutenberg block editor before it is released in WordPress 5.0.</p>\n<p><img class=\"alignnone wp-image-6168 size-full\" src=\"https://i0.wp.com/wordpress.org/news/files/2018/08/Screen-Shot-2018-08-02-at-11.42.39-am.png?resize=632%2C392&ssl=1\" alt=\"\" width=\"632\" height=\"392\" /></p>\n<p>In WordPress 4.9.8, the callout will be shown to the following users:</p>\n<ul>\n<li>If Gutenberg<em>is not</em>installed or activated, the callout will be shown to Admin users on single sites, and Super Admin users on multisites.</li>\n<li>If Gutenberg<em>is</em>installed and activated, the callout will be shown to Contributor users and above.</li>\n<li>If the Classic Editor plugin is installed and activated, the callout will be hidden for all users.</li>\n</ul>\n<p class=\"entry-title\"><span>You can learn more by reading</span><a href=\"https://make.wordpress.org/core/2018/08/02/try-gutenberg-callout-in-wordpress-4-9-8/\">Try Gutenberg Callout in WordPress 4.9.8</a>.</p>\n<h2>Privacy fixes/enhancements</h2>\n<p>This release includes 18 Privacy fixes focused on ensuring consistency and flexibility in the new personal data tools <span>that were</span>added in 4.9.6, including:</p>\n<ul>\n<li>The type of request being confirmed is now included in the subject line for all privacy confirmation emails.</li>\n<li>Improved consistency with site name being used for privacy emails in multisite.</li>\n<li>Pagination for Privacy request admin screens can now be adjusted.</li>\n<li>Increased the test coverage for several core privacy functions.</li>\n</ul>\n<p><a href=\"https://make.wordpress.org/core/2018/08/02/wordpress-4-9-8/\">This post has more information about all of the issues fixed in 4.9.8 if you&#8217;d like to learn more</a>.</p>\n<p><a href=\"https://wordpress.org/download/\">Download WordPress 4.9.8</a>or venture over to Dashboard  Updates and click &#8220;Update Now.&#8221; Sites that support automatic background updates are already beginning to update automatically.</p>\n<p>Thank you to everyone who contributed to WordPress 4.9.8:</p>\n<p><a href=\"https://profiles.wordpress.org/1naveengiri/\">1naveengiri</a>, <a href=\"https://profiles.wordpress.org/aaroncampbell/\">Aaron D. Campbell</a>, <a href=\"https://profiles.wordpress.org/jorbin/\">Aaron Jorbin</a>, <a href=\"https://profiles.wordpress.org/abdullahramzan/\">Abdullah Ramzan</a>, <a href=\"https://profiles.wordpress.org/alejandroxlopez/\">alejandroxlopez</a>, <a href=\"https://profiles.wordpress.org/allendav/\">Allen Snook</a>, <a href=\"https://profiles.wordpress.org/afercia/\">Andrea Fercia</a>, <a href=\"https://profiles.wordpress.org/azaozz/\">Andrew Ozz</a>, <a href=\"https://profiles.wordpress.org/andrewtaylor-1/\">Andrew Taylor</a>, <a href=\"https://profiles.wordpress.org/aryamaaru/\">Arun</a>, <a href=\"https://profiles.wordpress.org/ayeshrajans/\">Ayesh Karunaratne</a>, <a href=\"https://profiles.wordpress.org/birgire/\">Birgir Erlendsson (birgire)</a>, <a href=\"https://profiles.wordpress.org/bph/\">Birgit Pauli-Haack</a>, <a href=\"https://profiles.wordpress.org/bjornw/\">BjornW</a>, <a href=\"https://profiles.wordpress.org/boonebgorges/\">Boone Gorges</a>, <a href=\"https://profiles.wordpress.org/kraftbj/\">Brandon Kraft</a>, <a href=\"https://profiles.wordpress.org/burhandodhy/\">Burhan Nasir</a>, <a href=\"https://profiles.wordpress.org/chetan200891/\">Chetan Prajapati</a>, <a href=\"https://profiles.wordpress.org/chrislema/\">Chris Lema</a>, <a href=\"https://profiles.wordpress.org/coreymckrill/\">Corey McKrill</a>, <a href=\"https://profiles.wordpress.org/danielbachhuber/\">Daniel Bachhuber</a>, <a href=\"https://profiles.wordpress.org/danieltj/\">Daniel James</a>, <a href=\"https://profiles.wordpress.org/dlh/\">David Herrera</a>, <a href=\"https://profiles.wordpress.org/dd32/\">Dion Hulse</a>, <a href=\"https://profiles.wordpress.org/ocean90/\">Dominik Schilling (ocean90)</a>, <a href=\"https://profiles.wordpress.org/dontstealmyfish/\">dontstealmyfish</a>, <a href=\"https://profiles.wordpress.org/dyrer/\">dyrer</a>, <a href=\"https://profiles.wordpress.org/felipeelia/\">Felipe Elia</a>, <a href=\"https://profiles.wordpress.org/flixos90/\">Felix Arntz</a>, <a href=\"https://profiles.wordpress.org/fclaussen/\">Fernando Claussen</a>, <a href=\"https://profiles.wordpress.org/garetharnold/\">Gareth</a>, <a href=\"https://profiles.wordpress.org/garrett-eclipse/\">Garrett Hyder</a>, <a href=\"https://profiles.wordpress.org/pento/\">Gary Pendergast</a>, <a href=\"https://profiles.wordpress.org/soulseekah/\">Gennady Kovshenin</a>, <a href=\"https://profiles.wordpress.org/gm_alex/\">GM_Alex</a>, <a href=\"https://profiles.wordpress.org/idea15/\">Heather Burns</a>, <a href=\"https://profiles.wordpress.org/iandunn/\">Ian Dunn</a>, <a href=\"https://profiles.wordpress.org/ianbelanger/\">ibelanger</a>, <a href=\"https://profiles.wordpress.org/imath/\">imath</a>, <a href=\"https://profiles.wordpress.org/audrasjb/\">Jb Audras</a>, <a href=\"https://profiles.wordpress.org/jpry/\">Jeremy Pry</a>, <a href=\"https://profiles.wordpress.org/johnjamesjacoby/\">JJJ</a>, <a href=\"https://profiles.wordpress.org/joemcgill/\">Joe McGill</a>, <a href=\"https://profiles.wordpress.org/joen/\">Joen Asmussen</a>, <a href=\"https://profiles.wordpress.org/johnbillion/\">John Blackbourn</a>, <a href=\"https://profiles.wordpress.org/desrosj/\">Jonathan Desrosiers</a>, <a href=\"https://profiles.wordpress.org/spacedmonkey/\">Jonny Harris</a>, <a href=\"https://profiles.wordpress.org/chanthaboune/\">Josepha</a>, <a href=\"https://profiles.wordpress.org/joshuawold/\">JoshuaWold</a>, <a href=\"https://profiles.wordpress.org/joyously/\">Joy</a>, <a href=\"https://profiles.wordpress.org/jrf/\">jrf</a>, <a href=\"https://profiles.wordpress.org/kadamwhite/\">K. Adam White</a>, <a href=\"https://profiles.wordpress.org/khaihong/\">khaihong</a>, <a href=\"https://profiles.wordpress.org/kjellr/\">kjellr</a>, <a href=\"https://profiles.wordpress.org/xkon/\">Konstantinos Xenos</a>, <a href=\"https://profiles.wordpress.org/laurelfulford/\">laurelfulford</a>, <a href=\"https://profiles.wordpress.org/lbenicio/\">lbenicio</a>, <a href=\"https://profiles.wordpress.org/leanderiversen/\">Leander Iversen</a>, <a href=\"https://profiles.wordpress.org/leemon/\">leemon</a>, <a href=\"https://profiles.wordpress.org/macbookandrew/\">macbookandrew</a>, <a href=\"https://profiles.wordpress.org/clorith/\">Marius L. J.</a>, <a href=\"https://profiles.wordpress.org/matveb/\">Matias Ventura</a>, <a href=\"https://profiles.wordpress.org/melchoyce/\">Mel Choyce</a>, <a href=\"https://profiles.wordpress.org/mensmaximus/\">mensmaximus</a>, <a href=\"https://profiles.wordpress.org/mermel/\">mermel</a>, <a href=\"https://profiles.wordpress.org/metalandcoffee/\">metalandcoffee</a>, <a href=\"https://profiles.wordpress.org/michelleweber/\">michelleweber</a>, <a href=\"https://profiles.wordpress.org/dimadin/\">Milan Dini</a>, <a href=\"https://profiles.wordpress.org/xpertone/\">Muhammad Kashif</a>, <a href=\"https://profiles.wordpress.org/nao/\">Naoko Takano</a>, <a href=\"https://profiles.wordpress.org/nathanatmoz/\">Nathan Johnson</a>, <a href=\"https://profiles.wordpress.org/ov3rfly/\">Ov3rfly</a>, <a href=\"https://profiles.wordpress.org/palmiak/\">palmiak</a>, <a href=\"https://profiles.wordpress.org/pbiron/\">Paul Biron</a>, <a href=\"https://profiles.wordpress.org/pmbaldha/\">Prashant Baldha</a>, <a href=\"https://profiles.wordpress.org/presstigers/\">PressTigers</a>, <a href=\"https://profiles.wordpress.org/programmin/\">programmin</a>, <a href=\"https://profiles.wordpress.org/rafsuntaskin/\">Rafsun Chowdhury</a>, <a href=\"https://profiles.wordpress.org/redcastor/\">redcastor</a>, <a href=\"https://profiles.wordpress.org/littlerchicken/\">Robin Cornett</a>, <a href=\"https://profiles.wordpress.org/sergeybiryukov/\">Sergey Biryukov</a>, <a href=\"https://profiles.wordpress.org/pross/\">Simon Prosser</a>, <a href=\"https://profiles.wordpress.org/skoldin/\">skoldin</a>, <a href=\"https://profiles.wordpress.org/spyderbytes/\">spyderbytes</a>, <a href=\"https://profiles.wordpress.org/subrataemfluence/\">Subrata Sarkar</a>, <a href=\"https://profiles.wordpress.org/sebastienserre/\">Sbastien SERRE</a>, <a href=\"https://profiles.wordpress.org/karmatosed/\">Tammie Lister</a>, <a href=\"https://profiles.wordpress.org/tharsheblows/\">tharsheblows</a>, <a href=\"https://profiles.wordpress.org/thomasplevy/\">Thomas Patrick Levy</a>, <a href=\"https://profiles.wordpress.org/timbowesohft/\">timbowesohft</a>, <a href=\"https://profiles.wordpress.org/timothyblynjacobs/\">Timothy Jacobs</a>, <a href=\"https://profiles.wordpress.org/tz-media/\">Tobias Zimpel</a>, <a href=\"https://profiles.wordpress.org/tobifjellner/\">Tor-Bjorn Fjellner</a>, <a href=\"https://profiles.wordpress.org/itowhid06/\">Towhidul Islam</a>, <a href=\"https://profiles.wordpress.org/usmankhalid/\">Usman Khalid</a>, <a href=\"https://profiles.wordpress.org/warmlaundry/\">warmlaundry</a>, <a href=\"https://profiles.wordpress.org/earnjam/\">William Earnhardt</a>, <a href=\"https://profiles.wordpress.org/fierevere/\">Yui</a>, and <a href=\"https://profiles.wordpress.org/yuriv/\">YuriV</a>.</p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Thu, 02 Aug 2018 21:25:41 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:10:\"Paul Biron\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:14;a:6:{s:4:\"data\";s:13:\"\n	\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:84:\"WPTavern: Official Gutenberg Information Site Updated with Interactive Frontend Demo\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"https://wptavern.com/?p=82821\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:95:\"https://wptavern.com/official-gutenberg-information-site-updated-with-interactive-frontend-demo\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:2132:\"<p>The official <a href=\"https://wordpress.org/gutenberg/\" rel=\"noopener noreferrer\" target=\"_blank\">Gutenberg information site</a> has been updated to feature an interactive demo that can be manipulated on the frontend. It is based on <a href=\"https://wptavern.com/frontenberg-lets-users-test-gutenberg-on-the-frontend\" rel=\"noopener noreferrer\" target=\"_blank\">Frontenberg</a>, a <a href=\"https://testgutenberg.com/\" rel=\"noopener noreferrer\" target=\"_blank\">site</a> created by Tom Nowell, VIP Wrangler at Automattic. It loads an instance of WordPress with Gutenberg on the frontend so visitors don&#8217;t have to login or create a test site to try it.</p>\n<p><a href=\"https://i1.wp.com/wptavern.com/wp-content/uploads/2018/08/gutenberg-demo.png?ssl=1\"><img /></a></p>\n<p>Gutenberg&#8217;s new demo <a href=\"https://web.archive.org/web/20180730083424/https://wordpress.org/gutenberg/\" rel=\"noopener noreferrer\" target=\"_blank\">replaces the walls of text that were there previously</a>, which described the vision and approach for the new editor and explained blocks in depth with screenshots. Instead, the updated page features shorter, more succinct explanations of the new editor as an interactive part of demo.</p>\n<p>The demo is also live on all Rosetta sites, like <a href=\"http://fr.wordpress.org/gutenberg\" rel=\"noopener noreferrer\" target=\"_blank\">fr.wordpress.org/gutenberg</a>, so WordPress users from around the world can view it in their own languages.</p>\n<p><a href=\"https://i1.wp.com/wptavern.com/wp-content/uploads/2018/08/gutenberg-demo-rosetta-sites.png?ssl=1\"><img /></a></p>\n<p>WordPress contributors are feverishly working to prepare for the &#8220;Try Gutenberg&#8221; callout that will ship with WordPress 4.9.8, which is scheduled for Thursday, August 2nd. It will be many users&#8217; first exposure to the new editor and the demo gives them a chance to experience it in a hands-on way before making a decision about installing it. Instead of dismissing Gutenberg out of hand based on a quick glance at the screenshots and text, users have the opportunity to test drive it without breaking anything.</p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Thu, 02 Aug 2018 05:01:31 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:13:\"Sarah Gooding\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:15;a:6:{s:4:\"data\";s:13:\"\n	\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:68:\"WPTavern: WPWeekly Episode 325  A Different Facebook for Everyone\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:58:\"https://wptavern.com?p=82830&preview=true&preview_id=82830\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:75:\"https://wptavern.com/wpweekly-episode-325-a-different-facebook-for-everyone\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:2387:\"<p>In this episode, <a href=\"http://jjj.me\">John James Jacoby</a> and I discuss what&#8217;s new in Gutenberg 3.4 and share our recent experiences with the editor. We talk about Facebook&#8217;s decision to shut down its API for apps to publish to user&#8217;s profiles on their behalf. This leads to a side rant of our user experience with Facebook and the history between it and Twitter.</p>\n<p>We talk about what&#8217;s new in WordPress 4.9.8 RC 3 and when you can expect a final release. Last but not least, we discuss Slack&#8217;s acquisition of HipChat and share our reasons for joining Twitter.</p>\n<h2>Stories Discussed:</h2>\n<p><a href=\"https://wptavern.com/gutenberg-3-4-adds-new-warning-to-classic-editor-when-editing-posts-containing-blocks\">Gutenberg 3.4 Adds New Warning to Classic Editor When Editing Posts Containing Blocks</a></p>\n<p><a href=\"https://wptavern.com/wordpress-4-9-8-rc-3-released-limits-try-gutenberg-callout-visibility\">WordPress 4.9.8 RC 3 Released, Limits Try Gutenberg Callout Visibility</a></p>\n<p><a href=\"https://wptavern.com/facebook-shuts-down-api-for-publishing-to-user-timelines-impacts-jetpacks-publicize-feature\">Facebook Shuts Down API for Publishing to User Timelines, Impacts Jetpacks Publicize Feature</a></p>\n<p><a href=\"https://wptavern.com/wordpress-coding-standards-1-0-0-released\">WordPress Coding Standards 1.0.0 Released</a></p>\n<p><a href=\"https://wptavern.com/woosesh-virtual-woocommerce-conference-to-be-held-october-18-19\">WooSesh Virtual WooCommerce Conference to be Held October 18-19</a></p>\n<p><a href=\"https://wptavern.com/slack-acquires-hipchat-moves-blog-from-medium-to-wordpress\">Slack Acquires HipChat, Moves Blog from Medium to WordPress</a></p>\n<h2>WPWeekly Meta:</h2>\n<p><strong>Next Episode:</strong> Wednesday, August 8th 3:00 P.M. Eastern</p>\n<p>Subscribe to <a href=\"https://itunes.apple.com/us/podcast/wordpress-weekly/id694849738\">WordPress Weekly via Itunes</a></p>\n<p>Subscribeto <a href=\"https://www.wptavern.com/feed/podcast\">WordPress Weekly via RSS</a></p>\n<p>Subscribeto <a href=\"http://www.stitcher.com/podcast/wordpress-weekly-podcast?refid=stpr\">WordPress Weekly via Stitcher Radio</a></p>\n<p>Subscribeto <a href=\"https://play.google.com/music/listen?u=0#/ps/Ir3keivkvwwh24xy7qiymurwpbe\">WordPress Weekly via Google Play</a></p>\n<p><strong>Listen To Episode #325:</strong><br />\n</p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Thu, 02 Aug 2018 02:02:31 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:13:\"Jeff Chandler\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:16;a:6:{s:4:\"data\";s:13:\"\n	\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:82:\"WPTavern: Easy Digital Downloads Substantially Reduces Prices for Extension Passes\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"https://wptavern.com/?p=82825\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:93:\"https://wptavern.com/easy-digital-downloads-substantially-reduces-prices-for-extension-passes\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:3693:\"<p>Pippin Williamson, founder of <a href=\"https://wordpress.org/plugins/easy-digital-downloads/\">Easy Digital Downloads</a>, published <a href=\"https://easydigitaldownloads.com/blog/we-have-lowered-prices-for-extension-access-passes/?utm_source=Twitter&utm_medium=social&utm_term=we-have-lowered-prices-for-extension-access-passes&utm_content=0&utm_campaign=PostPromoterPro\">an apology</a> on the company&#8217;s blog today admitting that it made a mistake in its pricing model for extension passes. </p>\n\n<p>In mid-June, the <a href=\"https://easydigitaldownloads.com/blog/easy-digital-downloads-pricing-options/\">company introduced</a> a new set of pricing options or passes. The passes come with a single license that provide access to a group of extensions. The prices for each pass were as follows:</p>\n\n<ul><li>Personal Pass: $199</li><li>Extended Pass: $399</li><li>Professional Pass: $699</li><li>All Access Pass: $899</li></ul>\n\n<p>After six weeks, Williamson noticed that customers were continuing to purchase individual extensions instead of the passes. </p>\n\n<p>&#8220;Our expectation and intent in creating the passes was for store owners to be able to purchase access to multiple individual extensions in a more economical way, but thats not what happened,&#8221; he said. </p>\n\n<p>&#8220;Instead, store owners continued to purchase only the individual extensions they needed because the price tags of the passes were still too high. It is clear that we made a mistake in our pass pricing, and so today wed like to apologize for that and announce new, lower prices.&#8221;</p>\n\n<p>After two weeks of testing, the prices of each pass have been significantly reduced to the following amounts:</p>\n\n<ul><li>Personal Pass: <strong>$99</strong></li><li>Extended Pass: <strong>$199</strong></li><li>Professional Pass: <strong>$299</strong></li><li>All Access Pass: <strong>$499</strong><strong></strong><strong></strong></li></ul>\n\n<p>In addition to the pricing changes, customers who purchased a pass between June 1st and August 1st are eligible for a refund. The refund is equal to the difference of the purchased price and the new price. Customers have until August 15th, to <a href=\"https://easydigitaldownloads.com/support\">request a refund</a>. <br /></p>\n\n<p>Customers who purchased passes before the reduction will have their renewal amounts automatically changed to the lower price. </p>\n\n<p>Pricing WordPress products and services is tough and requires experimentation. There are numerous <a href=\"https://wordpress.tv/?s=pricing\">WordCamp sessions</a>, <a href=\"https://kinsta.com/blog/how-to-price-a-product-wordpress/\">articles</a>, and <a href=\"https://premium.wpmudev.org/blog/pricing-your-wordpress-services/\">in-depth guides</a> on the subject but there&#8217;s not one answer that&#8217;s best for every business. </p>\n\n<p>Publicly admitting to a pricing mistake is one thing, but offering refunds to try to get customers on equal footing is going the extra mile.</p>\n\n<p>The company could have handled things in a way that&#8217;s not as beneficial to customers, but they chose what I think is a commendable way. Williamson&#8217;s closing statement speaks about the way he leads his company and how he makes these types of decisions.</p>\n\n<blockquote class=\"wp-block-quote\"><p>While we are far from perfect, as this pricing mistake clearly indicates, we are dedicated to doing right by our customers. To each of you, we would like to extend our most sincere thanks for sticking with us as we work through these changes</p><cite>Pippin Williamson</cite></blockquote>\n\n<p>It&#8217;s a luxury to have business owners like Williamson in the WordPress economy. </p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Thu, 02 Aug 2018 01:00:41 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:13:\"Jeff Chandler\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:17;a:6:{s:4:\"data\";s:13:\"\n	\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:95:\"WPTavern: Gutenberg 3.4 Adds New Warning to Classic Editor When Editing Posts Containing Blocks\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"https://wptavern.com/?p=82772\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:106:\"https://wptavern.com/gutenberg-3-4-adds-new-warning-to-classic-editor-when-editing-posts-containing-blocks\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:2614:\"<p><a href=\"https://make.wordpress.org/core/2018/07/31/whats-new-in-gutenberg-30th-july/\" rel=\"noopener noreferrer\" target=\"_blank\">Gutenberg 3.4</a> was released yesterday with fixes intended to prepare the plugin for converting existing content to blocks. A wave of new users will soon be testing the new plugin as the result of the &#8220;Try Gutenberg&#8221; prompt in WordPress 4.9.8. The release date has been <a href=\"https://wptavern.com/wordpress-4-9-8-rc-3-released-limits-try-gutenberg-callout-visibility\" rel=\"noopener noreferrer\" target=\"_blank\">pushed back to Thursday, August 2nd</a>.</p>\n<p>In early July, the Gutenberg team froze new features on the plugin and shifted to focus on bugs, enhancements, compatibility, and API stability. An overview of the <a href=\"https://github.com/WordPress/gutenberg/issues/4894\" rel=\"noopener noreferrer\" target=\"_blank\">major functionality shipping in Gutenberg</a> is available as a list on GitHub. Gutenberg 3.4 continues refinements on these features.</p>\n<p>In preparation for users switching back and forth between the new and old editors, this release <a href=\"https://github.com/WordPress/gutenberg/pull/8247\" rel=\"noopener noreferrer\" target=\"_blank\">adds a warning in the classic editor</a> when users attempt to edit posts that contain blocks.</p>\n<p><a href=\"https://i0.wp.com/wptavern.com/wp-content/uploads/2018/07/Screen-Shot-2018-08-01-at-11.16.41-AM.png?ssl=1\"><img /></a></p>\n<p>Shared blocks have been renamed to &#8220;<a href=\"https://github.com/WordPress/gutenberg/pull/8123\" rel=\"noopener noreferrer\" target=\"_blank\">Reusable blocks</a>&#8221; to better convey their function.</p>\n<p>Theme developers can now <a href=\"https://github.com/WordPress/gutenberg/pull/6628\" rel=\"noopener noreferrer\" target=\"_blank\">configure font sizes</a> that show in the editor.</p>\n<p>This release also adds other little enhancements, including an <a href=\"https://github.com/WordPress/gutenberg/pull/8084\" rel=\"noopener noreferrer\" target=\"_blank\">edit button</a> for embed blocks, the ability to create a video block by <a href=\"https://github.com/WordPress/gutenberg/pull/8122\" rel=\"noopener noreferrer\" target=\"_blank\">dropping a video on an insertion point</a>, <a href=\"https://github.com/WordPress/gutenberg/pull/8187\" rel=\"noopener noreferrer\" target=\"_blank\">RTL CSS</a>, and accessibility improvements.</p>\n<p>Check out the release post for a full list of all the enhancements and bugs fixes in <a href=\"https://make.wordpress.org/core/2018/07/31/whats-new-in-gutenberg-30th-july/\" rel=\"noopener noreferrer\" target=\"_blank\">Gutenberg 3.4</a>.</p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Wed, 01 Aug 2018 16:46:25 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:13:\"Sarah Gooding\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:18;a:6:{s:4:\"data\";s:13:\"\n	\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:43:\"Dev Blog: The Month in WordPress: July 2018\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:34:\"https://wordpress.org/news/?p=6158\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:68:\"https://wordpress.org/news/2018/08/the-month-in-wordpress-july-2018/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:6410:\"<p>With WordPress 5.0 coming closer, theres lots of work going on all across the project. Read on to learn about how we progressed in July.</p>\n\n<hr class=\"wp-block-separator\" />\n\n<h2>Release of WordPress 4.9.7</h2>\n\n<p>On July 5, <a href=\"https://wordpress.org/news/2018/07/wordpress-4-9-7-security-and-maintenance-release/\">WordPress 4.9.7 was released</a>, fixing one security issue and 17 other bugs across the platform.<br /></p>\n\n<p>While this is a minor release, incremental fixes are essential to keep WordPress running smoothly. Everyone is encouraged to update as soon as possible and to make sure that automatic updates are switched on.<br /></p>\n\n<p>Would you like to get involved in building WordPress Core? Follow <a href=\"https://make.wordpress.org/core/\">the Core team blog</a> and join the #core channel in the <a href=\"https://make.wordpress.org/chat/\">Making WordPress Slack group</a>.</p>\n\n<h2>The New WordPress Editor</h2>\n\n<p>In the upcoming minor release of WordPress, 4.9.8, a new section in the dashboard will feature Gutenberg, the upcoming content editor for WordPress.<br /></p>\n\n<p>While the official release of Gutenberg <a href=\"https://wordpress.org/news/2018/07/update-on-gutenberg/\">is scheduled</a> for the coming months, you can already install it as <a href=\"https://wordpress.org/plugins/gutenberg/\">a plugin</a> to test it out right now. Additionally, <a href=\"https://wordpress.org/gutenberg/\">a brand new demo page</a> is now available  play around with the many features the editor has to offer, without installing it on your own site.<br /></p>\n\n<p>Would you like to help build or test Gutenberg? Follow <a href=\"https://make.wordpress.org/core/\">the Core team blog</a> and join the #core-editor channel in the <a href=\"https://make.wordpress.org/chat/\">Making WordPress Slack group</a>.</p>\n\n<h2>Page Design Updates on WordPress.org</h2>\n\n<p>Bit by bit were refreshing the design of WordPress.org. The latest pages to get a new treatment have been <a href=\"https://wordpress.org/download/\">the Download page</a> and <a href=\"https://profiles.wordpress.org/matt/\">user profiles</a>.<br /></p>\n\n<p>The Meta and Design teams worked hard to make these new designs a reality, with notable contributions from <a href=\"https://profiles.wordpress.org/melchoyce/\" class=\"mention\"><span class=\"mentions-prefix\">@</span>melchoyce</a>, <a href=\"https://profiles.wordpress.org/obenland/\" class=\"mention\"><span class=\"mentions-prefix\">@</span>obenland</a>, <a href=\"https://profiles.wordpress.org/mapk/\" class=\"mention\"><span class=\"mentions-prefix\">@</span>mapk</a>, and <a href=\"https://profiles.wordpress.org/kjellr/\" class=\"mention\"><span class=\"mentions-prefix\">@</span>kjellr</a>. The new designs enhance the overall look of the site and provide more relevant information to those searching.<br /></p>\n\n<p>Would you like to get involved in the design refresh? Follow the <a href=\"https://make.wordpress.org/meta/\">Meta</a> and <a href=\"https://make.wordpress.org/design/\">Design</a> team blogs and join the #meta and #design channels in the <a href=\"https://make.wordpress.org/chat/\">Making WordPress Slack group</a>.</p>\n\n<h2>The First WP-CLI Hack Day</h2>\n\n<p>On Friday July 20, the WP-CLI team <a href=\"https://make.wordpress.org/cli/2018/07/04/wp-cli-hack-day/\">held their first hack day</a>  a global event encouraging people to contribute to the official command line tool for WordPress.<br /></p>\n\n<p>Run by <a href=\"https://profiles.wordpress.org/schlessera/\" class=\"mention\"><span class=\"mentions-prefix\">@</span>schlessera</a>, the event <a href=\"https://make.wordpress.org/cli/2018/07/21/wp-cli-hack-day-results/\">was a great success</a>. Twelve pull requests were merged and another 13 submitted. It also included a video chat to give all contributors a space to meet each other and connect directly.<br /></p>\n\n<p>Would you like to get involved in contributing to WP-CLI? Follow <a href=\"https://make.wordpress.org/cli/\">the team blog</a> and join the #cli channel in the <a href=\"https://make.wordpress.org/chat/\">Making WordPress Slack group</a>.</p>\n\n<hr class=\"wp-block-separator\" />\n\n<h2>Further Reading:</h2>\n\n<ul><li>The WordPress Foundation is <a href=\"https://wordpressfoundation.org/2018/call-for-organizers-introduction-to-open-source-workshops-for-2018/\">looking for local community organizers to run introductory open-source workshops</a> in 2018.</li><li><a href=\"https://profiles.wordpress.org/chanthaboune/\" class=\"mention\"><span class=\"mentions-prefix\">@</span>chanthaboune</a> compiled <a href=\"https://wordpress.org/news/2018/07/quarterly-updates-q2-2018/\">updates for the last quarter</a> from the contribution teams all across the WordPress project.</li><li>In a great move for internationalization, <a href=\"https://en.blog.wordpress.com/2018/03/29/the-wordpress-mobile-apps-now-support-right-to-left-languages/\">the WordPress Mobile Apps now support right-to-left languages</a>.</li><li><a href=\"https://make.wordpress.org/community/2018/07/10/stripe-is-now-available-to-all-wordcamps/\">WordCamp events can now accept payment via Stripe</a>  PayPal remains an alternative option.</li><li>The WP-CLI team will soon<a href=\"https://make.wordpress.org/cli/2018/07/19/details-on-the-upcoming-major-release/\">release v2.0</a> of the official WordPress command line tool.</li><li>The Fields API project in WordPress Core <a href=\"https://wptavern.com/wordpress-core-fields-api-project-is-seeking-new-leadership\">is looking for a new lead</a> to drive it forward.</li><li>In WordPress 4.9.8, <a href=\"https://make.wordpress.org/core/2018/07/27/registering-metadata-in-4-9-8/\">it will be possible</a> for developers to fully register the meta fields used by their plugins and themes.</li><li>After many years of hard work, <a href=\"https://github.com/WordPress-Coding-Standards/WordPress-Coding-Standards\">v1.0.0 of the WordPress Coding Standards for PHP_CodeSniffer</a> has been released.</li><li>The Mobile team <a href=\"https://make.wordpress.org/mobile/2018/07/31/call-for-testing-wordpress-for-ios-10-6/\">is looking for people to help test</a> v10.6 of WordPress for iOS.</li></ul>\n\n<p><em>If you have a story we should consider including in the next Month in WordPress post, please </em><a href=\"https://make.wordpress.org/community/month-in-wordpress-submissions/\"><em>submit it here</em></a><em>.</em></p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Wed, 01 Aug 2018 09:11:02 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:15:\"Hugh Lashbrooke\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:19;a:6:{s:4:\"data\";s:13:\"\n	\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:33:\"HeroPress: Begin at the Beginning\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:56:\"https://heropress.com/?post_type=heropress-essays&p=2601\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:118:\"https://heropress.com/essays/begin-at-the-beginning/#utm_source=rss&utm_medium=rss&utm_campaign=begin-at-the-beginning\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:10624:\"<img width=\"960\" height=\"480\" src=\"https://s20094.pcdn.co/wp-content/uploads/2018/08/080118-1024x512.jpg\" class=\"attachment-large size-large wp-post-image\" alt=\"Pull Quote: I owe it all to a little bit of gumption and to the amazing community of WordPressers who opened their world to me.\" /><p>With this story, as with most things in my life, I never really know where to start. Because of that strange sense of time and action Ive always been quite fond of the quote from Lewis Carrolls Alice in Wonderland</p>\n<p>Begin at the beginning and go on till you come to the end: then stop.<br />\nBut where, for this story, is the beginning?</p>\n<p>It was a lifetime of choices and serendipitous encounters that led me down the path to WordPress and eventually Automattic. And those same types of choices and encounters that keep me here.</p>\n<h2>Begin at the Beginning&#8230;</h2>\n<p>I was a youngish mother of a youngish child and a full time stay at home parent. I didnt work outside the home; I was raising my kid. That was both a privilege and a problem. I realized that, as a mother consumed with caring for this new human, I was beginning to lose the very strong sense of identity that Id always had.</p>\n<p>This was my first experience with not knowing who I was. I always knew who I was, even if I didnt know who I wanted to be. As a mother though, most of who I am was sucked into caring for my family. Not sleeping. Not taking proper care of myself. Not taking time for my interests. Putting the others in my house before me and saying that it was what I wanted.</p>\n<p><em>Narrator: It wasnt.</em></p>\n<p>There was a pivot point. A moment before my daughter turned three. When I realized that if she were to choose to become a mother I would not want her to go down the same path of self denial that I had. I would want her to be who she is first and a mother second so that she could be the best self and mother she could be.</p>\n<p>And I realized that was something I could only show by example. But how to start? Id always been a writer. I often say that I only learned to read so that I could write. Journaling, short stories, poetry. Sometimes just long strings of thoughts and observations that wouldnt leave my head until I wrote them down. I would write out long stories on MySpace about my experiences and realizations. I would scrawl poetry on scrap paper laying around the house. None of it was enough.</p>\n<p>Also, as I understand is fairly typical for a stay at home parent, I had no one to talk to most of the time except for my small child. And while she was super awesome and receptive to any conversation I may want to start, they all tended to transition to discussion of My Little Ponies or a request for ice cream or screen time. So I looked for something more. For a place to discuss the mommified version of myself with no one and everyone. I started a blog.</p>\n<p>But it wasnt on WordPress.</p>\n<h2>Go On</h2>\n<p>I wrote and I wrote and I wrote. My experiences, my kids experiences. Dreams and hopes and bad poetry. And I found a group of people who shared with me their struggles and dreams as they dove into mine. A web of women and men who got me. Who got what I was going through. Who got what I was doing. A community of individuals, not all like minded, but all understanding.</p>\n<p>And then one day I was asked to write for a city blog, and then another. And as my writing began to expand I found other communities. My sense of self was first preserved but then invigorated and it grew and I stretched my arms and I stretched my wings and I realized I was beginning to feel whole for the first time in a long while.</p>\n<p>But using multiple platforms for my blogging gave me the understanding that the platform I was using was restrictive and unsatisfying. Clunky.</p>\n<p>And thats when a friend mentioned WordCamp Portland. It was this new thing. We were doing it for the first time. A bunch of bloggers getting together to talk about WordPress and blogging and stuff.</p>\n<p>I had no idea what WordPress was. But my friend said they needed some volunteers to help out, and Im always one to support my friends, my community. So I asked what I needed to do to help.</p>\n<p>Move your blog to WordPress</p>\n<p>And so I did. And something clicked. Once I had moved to a WordPress site I found my passion for blogging was something more. I played with themes, I made headers that felt like art pieces, I helped others transfer their content, set up their sites. I introduced them to a community that I was just beginning to get to know.</p>\n<p>And then there was a cascade of activity. More blogs, podcasting, events, community, and every year there was WordCamp Portland. And I continued to learn and grow my skills as a writer, as a blogger, as a podcaster, and as a WordPresser. I continued to volunteer, and then to speak, and to help organize.</p>\n<h2>And on</h2>\n<p>But life changes. Sometimes for the better sometimes for the worse. But change is hard. People change or stay the same. Relationships change. Life changes. Sometimes all of the above. So I ended a marriage and found myself stumped. I knew who I was, but who I happened to be was a stay at home mom who hadnt worked outside the home in eight years and had no marketable skills that I could find that would also allow me to continue to be home for my kid.</p>\n<p>And I panicked and I procrastinated and then I found an opening. A small startup I knew through my interactions with the community needed a customer support person who also knew WordPress.</p>\n<p>A light went on. And I had a job for the first time in eight years. And I knew what I was doing. And I was good at it. And I continued to blog. A little. And I stuck to the little tiny fringes of the WordPress community I knew. Still not realizing how big the rest of it was.</p>\n<p>The Portland WordPress folks were like a river feeding into the ocean of the worldwide community. But Id only ever stood on the shores of the river and dipped my toes in. All that was about to change.</p>\n<h2>Till You Come to the End</h2>\n<p>But as I said at the beginning, its not that simple. There is no end. There is just more. More stories, more people, more learning, more community. And thats where we begin the part of the story that tells us where I am today.</p>\n<p>This part begins with an email from a community friend with a link to a job posting. And a new beginning.</p>\n<p>I was working happily at that small startup, spreading my wings. But starting to feel a little bit like my passion wasnt being put to use when that email arrived. A link to a job posting working as a sponsored volunteer within the WordPress community and question. It was a simple question. Do you know anyone who would kick ass at this job?</p>\n<p>DID I? I was reeling from the possibility and doubled over with imposter syndrome and fear. Fear that I wasnt good enough. Fear that they wouldnt like me. Fear that Id be leaving my boss in the lurch.</p>\n<p>So I did what I always did at that time. And what I still do to this day. I procrastinated. I talked. I thought. I woke up in the middle of the night in a panic thinking I had forgotten something. Thinking I had done something wrong.</p>\n<p>But as I sat there one day tinkering on the startups site, scheduling posts, checking in on our customers all while getting my daughter ready for school that morning I realized that it was time to set aside those fears. To look that imposter syndrome right in the eye and say not today and I started putting together my resume and working on the cover email.</p>\n<p>No one was more surprised than I when I found myself two months later starting my first day at Automattic.</p>\n<p>Its been about 5 and a half years since that email landed in my inbox. Its been more than a decade since WordPress landed in my heart and Im proud to make my living in the wide wonderful world of WordPress. As I write this story for all of you to readnot a cautionary tale I hope, but an inspirational oneIm just a few days away from the end of my three-month sabbatical (a benefit that all Automatticians are eligible for after 5 years.)</p>\n<p>Its been a time of rest, readjustment, relaxation. Of edification. But its also been a renewal. A time to look back at how I got where I am and remember how lucky I am to be here today. And I owe it all to a little bit of gumption and to the amazing community of WordPressers who opened their world to me. And also to everyone else who is willing to be a part of this great big community founded on open source. On WordPress.</p>\n<div class=\"rtsocial-container rtsocial-container-align-right rtsocial-horizontal\"><div class=\"rtsocial-twitter-horizontal\"><div class=\"rtsocial-twitter-horizontal-button\"><a title=\"Tweet: Begin at the Beginning\" class=\"rtsocial-twitter-button\" href=\"https://twitter.com/share?text=Begin%20at%20the%20Beginning&via=heropress&url=https%3A%2F%2Fheropress.com%2Fessays%2Fbegin-at-the-beginning%2F\" rel=\"nofollow\" target=\"_blank\"></a></div></div><div class=\"rtsocial-fb-horizontal fb-light\"><div class=\"rtsocial-fb-horizontal-button\"><a title=\"Like: Begin at the Beginning\" class=\"rtsocial-fb-button rtsocial-fb-like-light\" href=\"https://www.facebook.com/sharer.php?u=https%3A%2F%2Fheropress.com%2Fessays%2Fbegin-at-the-beginning%2F\" rel=\"nofollow\" target=\"_blank\"></a></div></div><div class=\"rtsocial-linkedin-horizontal\"><div class=\"rtsocial-linkedin-horizontal-button\"><a class=\"rtsocial-linkedin-button\" href=\"https://www.linkedin.com/shareArticle?mini=true&url=https%3A%2F%2Fheropress.com%2Fessays%2Fbegin-at-the-beginning%2F&title=Begin+at+the+Beginning\" rel=\"nofollow\" target=\"_blank\" title=\"Share: Begin at the Beginning\"></a></div></div><div class=\"rtsocial-pinterest-horizontal\"><div class=\"rtsocial-pinterest-horizontal-button\"><a class=\"rtsocial-pinterest-button\" href=\"https://pinterest.com/pin/create/button/?url=https://heropress.com/essays/begin-at-the-beginning/&media=https://heropress.com/wp-content/uploads/2018/08/080118-150x150.jpg&description=Begin at the Beginning\" rel=\"nofollow\" target=\"_blank\" title=\"Pin: Begin at the Beginning\"></a></div></div><a rel=\"nofollow\" class=\"perma-link\" href=\"https://heropress.com/essays/begin-at-the-beginning/\" title=\"Begin at the Beginning\"></a></div><p>The post <a rel=\"nofollow\" href=\"https://heropress.com/essays/begin-at-the-beginning/\">Begin at the Beginning</a> appeared first on <a rel=\"nofollow\" href=\"https://heropress.com\">HeroPress</a>.</p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Wed, 01 Aug 2018 08:00:57 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:9:\"Cami Kaos\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:20;a:6:{s:4:\"data\";s:13:\"\n	\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:86:\"WPTavern: WordPress 4.9.8 RC 3 Released, Limits Try Gutenberg Callout Visibility\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"https://wptavern.com/?p=82803\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:90:\"https://wptavern.com/wordpress-4-9-8-rc-3-released-limits-try-gutenberg-callout-visibility\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:1701:\"<p>WordPress 4.9.8 Release Candidate 3 <a href=\"https://make.wordpress.org/core/2018/07/31/wordpress-4-9-8-release-candidate-3/\">is available</a> for testing. This version includes one important enhancement that limits the visibility of the <a href=\"https://wptavern.com/first-look-at-try-gutenberg-prompt-in-wordpress-4-9-8-beta-2\">&#8216;Try Gutenberg&#8217; callout</a>. </p>\n\n<p>If Gutenberg is not installed or activated on a site, the callout will be displayed to users with the <a href=\"https://codex.wordpress.org/Roles_and_Capabilities#install_plugins\">install_plugins</a> capability. If Gutenberg is activated, the callout will be shown to users with the <a href=\"https://codex.wordpress.org/Roles_and_Capabilities#edit_posts\">edit_posts</a> capability. </p>\n\n<p>Limiting the visibility of the callout to a specific subset of users will avoid inundating the WordPress.org support forums with Gutenberg support requests. A concern that was <a href=\"https://core.trac.wordpress.org/ticket/41316#comment:98\">expressed by</a> Daniel Bachhuber four months ago. </p>\n\n<p>&#8220;There&#8217;s a good amount of fit and finish to be desired before rolling out to a large number of unacquainted users,&#8221; Bachhuber said. &#8220;We&#8217;re not quite over the hump on the issues we do already know about. There&#8217;s not a ton of sense in having 10,000 users re-report them.&#8221;</p>\n\n<p>Other than this change, the <a href=\"https://make.wordpress.org/core/2018/07/24/wordpress-4-9-8-release-candidate-1/#change-log\">change log</a> from WordPress 4.9.8 RC 1 remains the same. WordPress 4.9.8 was originally scheduled to be released today, but has been rescheduled for Thursday, August 2nd. </p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Wed, 01 Aug 2018 00:14:22 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:13:\"Jeff Chandler\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:21;a:6:{s:4:\"data\";s:13:\"\n	\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:105:\"WPTavern: Facebook Shuts Down API for Publishing to User Timelines, Impacts Jetpacks Publicize Feature\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"https://wptavern.com/?p=82768\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:112:\"https://wptavern.com/facebook-shuts-down-api-for-publishing-to-user-timelines-impacts-jetpacks-publicize-feature\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:4690:\"<p><a href=\"https://i0.wp.com/wptavern.com/wp-content/uploads/2018/07/logo-facebook-1600.png?ssl=1\"><img /></a></p>\n<p>In the aftermath of the <a href=\"https://en.wikipedia.org/wiki/Facebook%E2%80%93Cambridge_Analytica_data_scandal\" rel=\"noopener noreferrer\" target=\"_blank\">Cambridge Analytica data scandal</a>, Facebook is tightening its control on third-party applications and will no longer allow apps to post to user profiles. In April, the platform <a href=\"https://developers.facebook.com/blog/post/2018/04/24/new-facebook-platform-product-changes-policy-updates/\" rel=\"noopener noreferrer\" target=\"_blank\">announced</a> sweeping changes to the publish_actions permission, which allowed apps to publish to users&#8217; timeline on their behalf.</p>\n<blockquote><p>On August 1st, 2018, the Live API publish_actions permission, which allows an app to publish on behalf of its Users, will be reserved for approved partners. A new permission model that allows apps to publish Videos to their User&#8217;s Groups and Timeline will be created instead.</p>\n<p>Access to the Pages APIs requires re-submission of the application for review before August 1, 2018. This will be required to continue publishing live and VOD video to Pages, as well as reading insights.</p></blockquote>\n<p>Facebook is notorious for swiftly changing its APIs in ways that break apps (sometimes without warning), often sending developers scrambling. For a long time, apps auto-posting to user timelines was part of the wild west of app permissions granted without much oversight from Facebook. Users often unknowingly gave permission to apps that would collect data and spam their Facebook connections with posts made on their behalf.</p>\n<p>Those days are over, but an unfortunate byproduct of this restriction is that apps like WordPress.com and Jetpack&#8217;s Publicize feature can no longer automatically publish posts to user timelines. This change also adversely affects apps like Buffer and Hootsuite that allow users to schedule and publish posts to their social accounts.</p>\n<p><a href=\"https://jetpack.com/2018/07/30/jetpack-6-3-3-facebook-publicize/\" rel=\"noopener noreferrer\" target=\"_blank\">Jetpack 6.3.3</a> removes the ability for users to select Facebook Profile connections and displays a notice regarding existing connections, so users will be aware of which auto-posting connections they are losing. Besides Jetpack, this Facebook API change affects tens of thousands of users who have this functionality implemented through one of many other plugins on WordPress.org.</p>\n<p>Users are now required to manually share their posts to their timelines. They can no longer schedule content to be shared to Facebook at specific times for different audiences.</p>\n<p>Auto-posting to <a href=\"https://www.facebook.com/help/175644189234902/\" target=\"_blank\" rel=\"noopener noreferrer\">Facebook Pages</a> still works, and one option users have is to convert their Profile to a Page or set up a new page. This may not be a suitable alternative for bloggers and those whose writing is not attached to a business or an organization.</p>\n<p>In a recent <a href=\"https://ma.tt/2018/07/my-recode-decode-interview-with-kara-swisher/\" rel=\"noopener noreferrer\" target=\"_blank\">post</a> on his blog, Automattic CEO Matt Mullenweg commented on Facebook&#8217;s decision to turn off auto-posting to profiles.</p>\n<p>&#8220;As it turns out, Facebook also is turning off the ability for WordPress sites  and all websites  to post directly to users profile pages,&#8221; Mullenweg said. &#8220;The decision to shut down the API is ostensibly to fight propaganda and misinformation on the platform, but I think its a big step back for their embrace of the open web. I hope they change their minds.&#8221;</p>\n<p>If only a select few &#8220;approved partners&#8221; are allowed to automatically broadcast to user timelines, it puts smaller players at a disadvantage, requiring manual sharing each time they publish. Facebook is setting itself up as a gatekeeper that enables news from a small selection of partners to keep pumping through the platform on schedule. Individual voices on smaller websites are no longer able to syndicate to the Facebook platform unless they decide to create a Page.</p>\n<p>Put a different way, the only syndicated content allowed on Facebook will be through channels the company can monetize &#8211; business/organization Pages or partners who are approved to post to user timelines. Users who care about retaining their Facebook audiences will need to remember to manually post their content to the social network after August 1, 2018, when the API changes go into effect.</p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Tue, 31 Jul 2018 22:11:30 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:13:\"Sarah Gooding\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:22;a:6:{s:4:\"data\";s:13:\"\n	\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:50:\"Matt: My Recode Decode Interview with Kara Swisher\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:22:\"https://ma.tt/?p=48262\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:67:\"https://ma.tt/2018/07/my-recode-decode-interview-with-kara-swisher/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:1780:\"<blockquote class=\"wp-block-quote\"><p>&#8220;We want to make the best tools in the world, and we want to do it for decades to come. I&#8217;ve been doing WordPress for 15 years, I want to do it the rest of my life.&#8221; <br /></p></blockquote>\n\n<p>The last time I chatted with <a href=\"https://twitter.com/karaswisher?ref_src=twsrc%5Egoogle%7Ctwcamp%5Eserp%7Ctwgr%5Eauthor\">Kara</a> was in 2013 <a href=\"https://www.wsj.com/video/wordpress-mullenweg-on-the-future-of-blogging/51B2A49C-EE8D-42EC-911D-370064A4829F.html\">in the back of a pedicab in Austin</a>. This time I got to <a href=\"https://art19.com/shows/recode-decode/episodes/3d56208e-a4b9-4c74-861e-8fec105a4ee8\">sit in the red chair</a> at Vox headquarters in San Francisco, and per usual Kara was thoughtful, thorough and to the point: we talked about WordPress and the future of the open web, the moral imperative of user privacy, and how it all relates to what&#8217;s going on at Facebook. </p>\n\n<p>(As it turns out, Facebook also is <a href=\"https://en.blog.wordpress.com/2018/07/23/sharing-options-from-wordpress-com-to-facebook-are-changing/\">turning off the ability for WordPress sites &#8212; and all websites</a> &#8212; to post directly to users&#8217; profile pages. The decision to shut down the API is ostensibly to fight propaganda and misinformation on the platform, but I think it&#8217;s a big step back for their embrace of the open web. I hope they change their minds.)<br /></p>\n\n<p>Kara and I also talked about distributed work, Automattic&#8217;s <a href=\"https://ma.tt/2018/06/atavist-automattic/\">acquisition of Atavist</a> and <a href=\"https://longreads.com/\">Longreads</a>, and why every tech company should have an editorial team. Thanks again to Kara and the Recode team for having me.</p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Tue, 31 Jul 2018 08:31:15 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:4:\"Matt\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:23;a:6:{s:4:\"data\";s:13:\"\n	\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:51:\"WPTavern: WordPress Coding Standards 1.0.0 Released\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"https://wptavern.com/?p=82761\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:62:\"https://wptavern.com/wordpress-coding-standards-1-0-0-released\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:2215:\"<p>After nine years <a href=\"https://github.com/WordPress-Coding-Standards/WordPress-Coding-Standards#project-history\">since the project began</a>, version 1.0.0 of the <a href=\"https://github.com/WordPress-Coding-Standards/WordPress-Coding-Standards\">WordPress Coding Standards</a> is available for download. The WordPress Coding Standards is a collection of <a href=\"https://github.com/squizlabs/PHP_CodeSniffer\">PHP_CodeSniffer</a> rules or sniffs to validate code developed for WordPress. It ensures code quality and adherence to coding conventions, including the official <a href=\"https://make.wordpress.org/core/handbook/best-practices/coding-standards/\">WordPress Coding Standards</a>.</p>\n\n<p>In addition to being a big milestone, 1.0.0 contains breaking changes. &#8220;A number of sniffs have been moved between categories and the old sniff names have been deprecated,&#8221; <a href=\"https://github.com/jrfnl\">Juliette Reinders Folmer</a>, a significant contributor to the project, said.</p>\n\n<p>&#8220;If you selectively include any of these sniffs in your custom ruleset or set custom property values for these sniffs, your custom ruleset will need to be updated.&#8221;</p>\n\n<p>The WordPress-VIP ruleset has been deprecated as well. &#8220;This ruleset has not been valid for some time, as we have <a href=\"https://github.com/Automattic/VIP-Coding-Standards\">our own VIP coding standards</a>, available for public use,&#8221; David Artiss, a member of the WordPress.com VIP support team, said.</p>\n\n<p>&#8220;If you are a VIP client and you are not using the alternative rulesets, then we would strongly recommend switching to these. If you used the WordPress-VIP ruleset for any other reason, you should use WordPress-Extra or WordPress instead.&#8221;</p>\n\n<p>Those who use the WordPress Coding Standards Sniffs are strongly encouraged to read the <a href=\"https://github.com/WordPress-Coding-Standards/WordPress-Coding-Standards/releases/tag/1.0.0\">changelog</a> before updating. WordPress Coding Standards is a free, open source project, that&#8217;s <a href=\"https://github.com/WordPress-Coding-Standards/WordPress-Coding-Standards\">available on GitHub</a> where contributions are welcomed.</p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Mon, 30 Jul 2018 23:43:43 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:13:\"Jeff Chandler\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:24;a:6:{s:4:\"data\";s:13:\"\n	\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:101:\"WPTavern: David Needham Chats about Venturing Outside WordCamp to Visit Other Open Source Communities\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"https://wptavern.com/?p=82750\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:112:\"https://wptavern.com/david-needham-chats-about-venturing-outside-wordcamp-to-visit-other-open-source-communities\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:1439:\"<p>While at WordCamp Europe I had the chance to chat with <a href=\"http://davidneedham.me\" rel=\"noopener noreferrer\" target=\"_blank\">David Needham</a>, a developer advocate at Pantheon, about his session titled &#8220;<a href=\"https://davidneedham.me/wceu2018/\" rel=\"noopener noreferrer\" target=\"_blank\">Intro to Drupal for WordPress Folks</a>.&#8221; Needham frequently travels between the WordPress and Drupal communities, representing his company as a developer liaison. He has spoken at various Drupal camps and WordCamps and is also one of the organizers for WordCamp US 2018 in Nashville.</p>\n<p>During our chat, Needham discussed some of the more notable differences between the WordPress and Drupal communities and how the two can inspire each other in various ways. He encouraged attendees at his session to venture outside of WordCamps and said he hopes to see more collaboration across the platforms in the future. From his unique vantage point, Needham said he doesn&#8217;t think CMS rivalries are as big of a deal as they used to be.</p>\n<p>&#8220;We&#8217;re realizing that we&#8217;re really not competing,&#8221; Needham said. &#8220;The internet is a big place and there&#8217;s plenty of room for all of our communities to work together &#8211; especially since our values are so closely aligned already. If there is a rivalry, it feels a little bit more like a friendly sibling rivalry than anything.&#8221;</p>\n<p></p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Mon, 30 Jul 2018 21:50:38 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:13:\"Sarah Gooding\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:25;a:6:{s:4:\"data\";s:13:\"\n	\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:83:\"WPTavern: WordPress Developers: Learn How to Convert Shortcodes to Gutenberg Blocks\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"https://wptavern.com/?p=82737\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:93:\"https://wptavern.com/wordpress-developers-learn-how-to-convert-shortcodes-to-gutenberg-blocks\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:2208:\"<p>Gutenberg contributor <a href=\"http://pento.net\" rel=\"noopener noreferrer\" target=\"_blank\">Gary Pendergast</a> has published a handy <a href=\"https://gist.github.com/pento/cf38fd73ce0f13fcf0f0ae7d6c4b685d\" rel=\"noopener noreferrer\" target=\"_blank\">sample plugin</a> that demonstrates how to convert shortcode functionality to a Gutenberg block.</p>\n<blockquote class=\"twitter-tweet\">\n<p lang=\"en\" dir=\"ltr\">Do you have a bunch of shortcodes that youd like to really easily turn into blocks? Heres a sample plugin showing how to re-use your exisiting shortcode functionality in a block!<a href=\"https://t.co/bppHcumBlB\">https://t.co/bppHcumBlB</a></p>\n<p>&mdash; Gary (@GaryPendergast) <a href=\"https://twitter.com/GaryPendergast/status/1023560781617487872?ref_src=twsrc%5Etfw\">July 29, 2018</a></p></blockquote>\n<p></p>\n<p>The first file shows a basic example of how to register a block with JavaScript and add block inspector controls to the sidebar. The second file is the PHP code for the plugin that converts the existing shortcode logic into a block that will work inside the new editor.</p>\n<p>&#8220;This sample uses the ServerSideRender element,&#8221; Pendergast said. &#8220;Its critical to remember that ServerSideRender is a stepping stone to a full block editing experience: having to call back to the server to re-render is a worse UX than native JS rendering. Use ServerSideRender to get your existing functionality ready for WordPress 5.0 now, and plan to phase it out over time.&#8221;</p>\n<p>With WordPress 4.9.8&#8217;s <a href=\"https://wptavern.com/first-look-at-try-gutenberg-prompt-in-wordpress-4-9-8-beta-2\" rel=\"noopener noreferrer\" target=\"_blank\">&#8220;Try Gutenberg&#8221; callout</a> just around the corner, this sample plugin may be helpful for developers who have created custom shortcode plugins for clients. If you&#8217;re not sure where to start, Pendergast&#8217;s sample plugin makes Gutenberg block creation more approachable. The <a href=\"https://wordpress.org/gutenberg/handbook/\" rel=\"noopener noreferrer\" target=\"_blank\">Gutenberg handbook</a> has more in-depth documentation for developers who want to improve their blocks beyond this basic example.</p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Mon, 30 Jul 2018 13:47:55 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:13:\"Sarah Gooding\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:26;a:6:{s:4:\"data\";s:13:\"\n	\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:52:\"WPTavern: Plugin Review: Theme Support for Gutenberg\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"https://wptavern.com/?p=82710\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:62:\"https://wptavern.com/plugin-review-theme-support-for-gutenberg\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:2655:\"<p>As Gutenberg nears a merge with WordPress, Theme Authors are running out of time to ensure that their themes are compatible. The Gutenberg handbook has an <a href=\"https://wordpress.org/gutenberg/handbook/extensibility/theme-support/\">excellent article</a> on how to opt-in and add support for enhanced features.</p>\n\n<p>Most themes will present the default blocks without any issues as the blocks themselves provide their own styles. If you use a theme that does not fully support Gutenberg such as the Wide or Full Block Alignment options, the <a href=\"https://wordpress.org/plugins/theme-support-for-gutenberg/\">Theme Support for Gutenberg</a> plugin may be for you. </p>\n\n<p>Created by <a href=\"https://weavertheme.com/about/\">Weweaver</a>, Theme Support for Gutenberg claims to allow most WordPress themes to be compatible with Gutenberg. In addition to theme support, the plugin adds a Classic Editor button to the admin bar to easily switch between Gutenberg and the Classic Editor.</p>\n\n<p>Since a default WordPress theme is used to show how this plugin is beneficial, I decided to try it for myself. I installed the plugin on a fresh install of WordPress 4.9.7, Gutenberg 3.3, and the latest version of Twenty Sixteen. I published a Gutenberg Demo post which uses many of the default blocks.<br /></p>\n\n<h2>Twenty Sixteen Looks Better Without It<br /></h2>\n\n<p>Here is what the Gallery block looks like in Twenty Sixteen with the plugin disabled. The content looks good and fits within the content column.</p>\n\n<img />Theme Support for Gutenberg Disabled\n\n<p>When the plugin is enabled, the images are so large, a horizontal scroll bar appears. </p>\n\n<img />Theme Support for Gutenberg Enabled\n\n<p>The plugin includes additional styling for default blocks. One block that doesn&#8217;t benefit from the enhanced styles is the Video block. With the plugin disabled, the video block appears normally. </p>\n\n<img />Video Block With The Plugin Disabled<br />\n\n<p>With the plugin enabled, the video block overflows the content column and breaches into the left sidebar.</p>\n\n<img />Video Block With The Plugin Enabled \n\n<p>Although the plugin initially shipped with no options, version 0.2 includes new settings that provide better compatibility for some themes. I checked the box to disable Fitvids support which solved the video block issue. However, the other options had no effect on the oversized image blocks. <br /></p>\n\n<img />Theme Support Options\n\n<p>Twenty Sixteen and Twenty Seventeen do not work well with this plugin. There may be themes, particularly older ones that benefit, but more modern themes will likely be ok without it. </p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Fri, 27 Jul 2018 22:42:22 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:13:\"Jeff Chandler\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:27;a:6:{s:4:\"data\";s:13:\"\n	\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:73:\"WPTavern: WooSesh Virtual WooCommerce Conference to be Held October 18-19\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"https://wptavern.com/?p=82508\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:84:\"https://wptavern.com/woosesh-virtual-woocommerce-conference-to-be-held-october-18-19\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:4964:\"<p><a href=\"https://i2.wp.com/wptavern.com/wp-content/uploads/2018/07/woosesh-2018.png?ssl=1\"><img /></a></p>\n<p><a href=\"https://woosesh.com/\" rel=\"noopener noreferrer\" target=\"_blank\">WooSesh</a>, a new two-day virtual conference focused on WooCommerce topics, will be held October 18-19. While wrapping up another successful edition of WordSesh, Brian Richards announced WooSesh as the next event coming under the WordSesh brand. On Wednesday, nearly 500 attendees joined WordSesh. People tuned in from around the world, although the audience was heavily US-based due to the time the event was scheduled.</p>\n<p><a href=\"https://i2.wp.com/wptavern.com/wp-content/uploads/2018/07/wordsesh-july-2018.png?ssl=1\"><img /></a></p>\n<p>Since WooConf is not happening this year, WooSesh is an exciting alternative that will be accessible to anyone in the world. Co-organizers Brian Richards and Patrick Rauland will be hosting eight presentations each day, so the event will last between 8-10 hours both days, including breaks and announcements.</p>\n<p>&#8220;Once we learned that WooConf wouldnt be taking place this year we reached out to our friends at Automattic to see if we could work with them as well to still provide a high-quality event but for a much more global audience,&#8221; Richards said.</p>\n<p>WooCommerce is sponsoring the entire event, making it free for all live attendees. Richards said they are working with other prominent companies in the WooCommerce space who are lending their knowledgeable staff as presenters, as well as providing the event with digital swag for attendees.</p>\n<p>WooSesh organizers are employing an interesting concept for encouraging attendance and engagement. The conference will have a $200 ticket price for those who do not attend live. Those who register in advance and attend live will receive the $200 ticket for free.</p>\n<p>&#8220;That means if a person joins the mailing list and shows up for the event, theyll be able to experience the whole thing at no cost to them,&#8221; Richards said. &#8220;Similar to WordSesh, a ticket grants attendees access to the entire broadcast, chat, recordings, real-time transcriptions, and some cool digital swag. But with WooSesh theyll also get some targeted follow-up content, and perhaps a private community, to further help them succeed and increase the impact of this conference.</p>\n<p>&#8220;And I think its in everyones best interest to register and attend live  at least trying to make it to one of the sessions across the two days  so they can get all of that for free.&#8221;</p>\n<p>Richards said recordings may still make it out there for people to view afterwards but none of the other perks and follow-ups will be available without purchasing a ticket or attending live.</p>\n<p>&#8220;We talked about asking people for a credit card up front and building a mechanism that would either charge them after the fact (like a pre-order) or charge them up-front but then refund them after the fact,&#8221; Richards said. &#8220;Instead were going with the simpler route and asking only for a persons name and email address up-front. If they come to the live event theyll be able to access all of the content afterwards just as if they had paid, and if they dont attend live theyll instead be greeted with a payment form.&#8221;</p>\n<p>Building on their collective knowledge of organizing successful in-person and virtual conferences, Richards said he and his co-organizer wanted to reduce the friction as much as possible for people getting into their seats and engaged with the speakers and other attendees.</p>\n<p>&#8220;Making it a virtual event already knocks down a ton of barriers,&#8221; Richard said. &#8220;Making the content available for free eliminates even more. Except that, people will often discredit free things and we didnt want anyone to think of this content as any less valuable or serious than it really is.&#8221;</p>\n<p>WooSesh organizers plan to host compelling case studies, as well as talks about SEO, security, tips for building different kinds of e-commerce stores, and share advice from others hard-earned lessons. The event will also host sessions for developers that delve into WooCommerce architecture, performance, how to build and support custom extensions, and how to expand service offerings to better support e-commerce projects.</p>\n<p>&#8220;Our biggest goal with WooSesh is that it will help store builders as well as coders to have more impact with what they build,&#8221; Richards said. &#8220;Specifically, wed like to see them make some measurable progress in their own goals, whether thats more sales, better customer experiences, greater depth of knowledge, or otherwise. Were also hoping that some of these talks will inspire attendees to do more than they originally thought possible  either for their own e-commerce stores or for their customers/clients.&#8221;</p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Fri, 27 Jul 2018 22:34:07 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:13:\"Sarah Gooding\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:28;a:6:{s:4:\"data\";s:13:\"\n	\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:69:\"WPTavern: Slack Acquires HipChat, Moves Blog from Medium to WordPress\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"https://wptavern.com/?p=82681\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:79:\"https://wptavern.com/slack-acquires-hipchat-moves-blog-from-medium-to-wordpress\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:2732:\"<p>Slack <a href=\"https://slackhq.com/atlassian-and-slack-partnership\" rel=\"noopener noreferrer\" target=\"_blank\">announced</a> today that it has acquired HipChat from Atlassian. The friendly rivalry between the two competing group chat platforms will be laid to rest as Slack plans to retire HipChat and Stride. Atlassian will receive a stake in Slack&#8217;s business in exchange for shutting down its chat collaboration services and migrating all of its customers over.</p>\n<p><a href=\"https://www.atlassian.com/blog/archives/press-release-atlassian-acquires-hipcha\" rel=\"noopener noreferrer\" target=\"_blank\">Atlassian acquired HipChat in 2012</a> with the intention of scaling the business but found a formidable challenge in taking on the well-funded, market-dominating Slack app. As of May 2018, <a href=\"https://slackhq.com/from-tokyo-to-tallahassee-target-to-ticketmaster-slack-is-where-work-happens\" rel=\"noopener noreferrer\" target=\"_blank\">Slack reported 8 million daily active users</a> and 70,000 paid teams. More than half of the app&#8217;s users are outside the U.S. and 65% of companies in the Fortune 100 pay to use Slack.</p>\n<p>Atlassian and Slack are now joining forces to compete against Microsoft, who jumped into the enterprise collaboration market in 2016 with its Teams product. Teams&#8217; free tier offers support for up to 300 people, with unlimited chat messages and search, and is aimed squarely at competing with Slack.</p>\n<p><a href=\"https://i2.wp.com/wptavern.com/wp-content/uploads/2018/07/slack-blog-e1532659145946.png?ssl=1\"><img /></a></p>\n<p>The news was announced on &#8220;<a href=\"https://slackhq.com/\" rel=\"noopener noreferrer\" target=\"_blank\">Several People Are Typing</a>,&#8221; the official Slack blog, which has just <a href=\"https://medium.com/several-people-are-typing/weve-moved-9cf5941a66fe\" rel=\"noopener noreferrer\" target=\"_blank\">moved from Medium to WordPress</a>. It is being hosted on the WordPress.com VIP platform.</p>\n<blockquote class=\"twitter-tweet\">\n<p lang=\"en\" dir=\"ltr\"><img src=\"https://s.w.org/images/core/emoji/11/72x72/1f485.png\" alt=\"\" class=\"wp-smiley\" /> I cant really answer the question (bias, yada yada), but it sure is good to be hosting the Slack blog at <a href=\"https://twitter.com/WordPressVIP?ref_src=twsrc%5Etfw\">@WordPressVIP</a> <img src=\"https://s.w.org/images/core/emoji/11/72x72/1f60e.png\" alt=\"\" class=\"wp-smiley\" /> <a href=\"https://t.co/86aQQ5Pkaz\">https://t.co/86aQQ5Pkaz</a></p>\n<p>&mdash; Simon Wheatley (@simonwheatley) <a href=\"https://twitter.com/simonwheatley/status/1022360132053807104?ref_src=twsrc%5Etfw\">July 26, 2018</a></p></blockquote>\n<p></p>\n<p>The answer to that question, by the way, is &#8216;yes.&#8217;</p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Fri, 27 Jul 2018 02:55:34 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:13:\"Sarah Gooding\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:29;a:6:{s:4:\"data\";s:13:\"\n	\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:61:\"WPTavern: How to Create A Gutenberg Block Attributes Glossary\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"https://wptavern.com/?p=82684\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:72:\"https://wptavern.com/how-to-create-a-gutenberg-block-attributes-glossary\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:1242:\"<p>If you want to see what Gutenberg blocks are available on a site along with their attributes, check out the <a href=\"https://github.com/OIT-Design/block-attributes-glossary\">Block Attributes Glossary</a> plugin by <a href=\"https://design.oit.ncsu.edu/\">NC State&#8217;s Office of Information Technology and Design</a>.</p>\n\n<img />Block Attributes Glossary Index\n\n<p>The plugin adds a Glossary Attributes Block to Gutenberg that when added to a post or page, displays an index of blocks that are available. Clicking on a block name will display its attributes. </p>\n\n<img />Atomic Blocks Drop Cap Block Attributes\n\n<p>This is especially useful for creating block templates. Note that if you install plugins that add new blocks, you&#8217;ll need to visit the post or page that has the glossary, remove the Glossary block, and re-add it. </p>\n\n<p>You can see a live demo of this plugin in action by visiting <a href=\"https://design.oit.ncsu.edu/docs/gutenberg/block-attributes/\">NC State&#8217;s OIT Block Attributes Glossary</a>. The plugin is not available from the WordPress plugin directory but you can download it for free from the <a href=\"https://github.com/OIT-Design/block-attributes-glossary\">project&#8217;s Github page</a>. </p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Thu, 26 Jul 2018 20:47:34 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:13:\"Jeff Chandler\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:30;a:6:{s:4:\"data\";s:13:\"\n	\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:93:\"WPTavern: Font Awesome 5.2 Adds 372 New Icons, Introduces Automotive and Education Categories\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"https://wptavern.com/?p=82617\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:103:\"https://wptavern.com/font-awesome-5-2-adds-372-new-icons-introduces-automotive-and-education-categories\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:3091:\"<a href=\"https://i1.wp.com/wptavern.com/wp-content/uploads/2018/07/Screen-Shot-2018-07-25-at-5.15.52-PM.png?ssl=1\"><img /></a>image credit: <a href=\"https://fontawesome.com/\">Font Awesome</a>\n<p><a href=\"https://blog.fontawesome.com/font-awesome-5-2-372-new-icons-adfb42c6c35\" rel=\"noopener noreferrer\" target=\"_blank\">Font Awesome</a> 5.2 was released yesterday with two new categories and 372 new icons, bringing the total number of free icons to 1,295. The open source vector icon font is used on more than <a href=\"https://trends.builtwith.com/widgets/Font-Awesome\" rel=\"noopener noreferrer\" target=\"_blank\">22 million sites</a> across the internet. It&#8217;s also a popular icon font with WordPress theme and plugin developers.</p>\n<p>Version 5.2 introduces automotive and education categories, which should be useful to fill the gaps for designers and developers creating sites around these subjects. The release also adds 66 new and updated icons to the Medical category and 126 new and updated Maps icons.</p>\n<p><a href=\"https://i0.wp.com/wptavern.com/wp-content/uploads/2018/07/medical.png?ssl=1\"><img /></a></p>\n<p>Font Awesome, originally created by Dave Gandy, is an SIL OFL-licensed icon font, with the code under the MIT License. Thanks to its GPL-friendly license, the icon font is widely used in WordPress&#8217; theme and plugin ecosystem in both commercial and free products. Font Awesome&#8217;s <a href=\"https://github.com/FortAwesome/Font-Awesome/issues?utf8=%E2%9C%93&q=is%3Aissue+is%3Aopen+wordpress\" rel=\"noopener noreferrer\" target=\"_blank\">Github issues queue</a> is also loaded with icon requests that would be used in niche WordPress themes, as well as icons for WordPress-related company logos.</p>\n<p>Two years ago, Font Awesome announced the <a href=\"https://wptavern.com/font-awesome-cdn-now-in-beta-loads-icons-asynchronously-with-automatic-accessibility-best-practices\" rel=\"noopener noreferrer\" target=\"_blank\">beta release of its new CDN</a>, which allows developers to implement it using a single line of code to bring the icons and CSS toolkit into their projects. At that time, Font Awesome was used by more than 300 plugins on WordPress.org. In 2018, searching the official plugin directory turns up more than 800 plugins that make use of the icon font in some way. Thousands of free and commercial themes also use it to provide users with easy customization options.</p>\n<p>Font Awesome support for Gutenberg is going to be fairly important, as hundreds of thousands of websites are using plugins like <a href=\"https://wordpress.org/plugins/better-font-awesome/\" target=\"_blank\" rel=\"noopener noreferrer\">Better Font Awesome</a>, <a href=\"https://wordpress.org/plugins/font-awesome-shortcodes/\" target=\"_blank\" rel=\"noopener noreferrer\">Font Awesome Shortcodes</a>, and <a href=\"https://wordpress.org/plugins/font-awesome-4-menus/\" target=\"_blank\" rel=\"noopener noreferrer\">Font Awesome for Menus</a> to allow users to add icons to content and menus. Currently there are no Gutenberg-compatible plugins for adding Font Awesome icons to content.</p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Thu, 26 Jul 2018 18:08:30 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:13:\"Sarah Gooding\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:31;a:6:{s:4:\"data\";s:13:\"\n	\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:62:\"WPTavern: WPWeekly Episode 324  Getting NC State Gutenready\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:58:\"https://wptavern.com?p=82650&preview=true&preview_id=82650\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:69:\"https://wptavern.com/wpweekly-episode-324-getting-nc-state-gutenready\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:1804:\"<p>In this episode, <a href=\"http://jjj.me\">John James Jacoby</a> and I are joined by <a href=\"https://design.oit.ncsu.edu/people/jmriehle/\">Jen McFarland</a>, Web Services Coordinator at NC state&#8217;s Office of Information and Technology. McFarland describes how the campus is using WordPress, what they&#8217;re doing to prepare students and staff for Gutenberg, and what they&#8217;ve experienced thus far in the transition.</p>\n<p>Near the end of the show, we cover WordPress 4.9.8 RC 1 and provide an update on WP-CLI Hack Day.</p>\n<h2>Stories Discussed:</h2>\n<p><a href=\"https://wptavern.com/gutenberg-3-3-released-adds-archives-and-recent-comments-blocks\">Gutenberg 3.3 Released, Adds Archives and Recent Comments Blocks</a><br />\n<a href=\"https://make.wordpress.org/core/2018/07/24/wordpress-4-9-8-release-candidate-1/\">WordPress 4.9.8 RC 1 Released</a><br />\n<a href=\"https://wptavern.com/wp-cli-hack-day-is-a-success\">WP-CLI Hack Day Is A Success</a><br />\n<a href=\"https://wptavern.com/google-chrome-rolls-out-not-secure-warning-for-plain-http-sites\">Google Chrome Rolls Out Not Secure Warning for Plain HTTP Sites</a></p>\n<h2>WPWeekly Meta:</h2>\n<p><strong>Next Episode:</strong> Wednesday, August 1st 3:00 P.M. Eastern</p>\n<p>Subscribe to <a href=\"https://itunes.apple.com/us/podcast/wordpress-weekly/id694849738\">WordPress Weekly via Itunes</a></p>\n<p>Subscribeto <a href=\"https://www.wptavern.com/feed/podcast\">WordPress Weekly via RSS</a></p>\n<p>Subscribeto <a href=\"http://www.stitcher.com/podcast/wordpress-weekly-podcast?refid=stpr\">WordPress Weekly via Stitcher Radio</a></p>\n<p>Subscribeto <a href=\"https://play.google.com/music/listen?u=0#/ps/Ir3keivkvwwh24xy7qiymurwpbe\">WordPress Weekly via Google Play</a></p>\n<p><strong>Listen To Episode #324:</strong><br />\n</p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Thu, 26 Jul 2018 01:01:34 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:13:\"Jeff Chandler\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:32;a:6:{s:4:\"data\";s:13:\"\n	\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:63:\"WPTavern: Frontenberg Lets Users Test Gutenberg on the Frontend\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"https://wptavern.com/?p=82526\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:74:\"https://wptavern.com/frontenberg-lets-users-test-gutenberg-on-the-frontend\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:2733:\"<p>WordPress 5.0 will bring the world a brand new editor that is currently code-named Gutenberg. If you have been hearing the buzz around Gutenberg but have yet to try it, <a href=\"https://testgutenberg.com/\" rel=\"noopener noreferrer\" target=\"_blank\">Frontenberg</a> (<a href=\"https://testgutenberg.com/\" rel=\"noopener noreferrer\" target=\"_blank\">testgutenberg.com</a>) is the easiest way to check it out.</p>\n<p>Frontenberg allows visitors to try Gutenberg without having to set up a separate test site of their own. It loads an instance of WordPress plus the Gutenberg plugin on the frontend so visitors don&#8217;t have to log in to play around with the new editor.</p>\n<p><a href=\"https://i1.wp.com/wptavern.com/wp-content/uploads/2018/07/frontenberg-e1532540010736.png?ssl=1\"><img /></a></p>\n<p>Frontenberg has a limited range of capabilities for testing purposes. Users have access to a pre-populated media library but cannot upload images to the test site. It&#8217;s also not possible to create shared blocks or save the post. Attempting to save an action will trigger an &#8220;updating failed&#8221; notice. Apart from those few limitations, Frontenberg allows users to test nearly all of Gutenberg&#8217;s features.</p>\n<p>The tool was created by <a href=\"https://tomjn.com/\" rel=\"noopener noreferrer\" target=\"_blank\">Tom Nowell</a>, VIP Wrangler at <a href=\"https://automattic.com/\" rel=\"noopener noreferrer\" target=\"_blank\">Automattic</a>. He launched the frontend testing instance on his own website at <a href=\"https://frontenberg.tomjn.com/\" rel=\"noopener noreferrer\" target=\"_blank\">frontenberg.tomjn.com</a> and the WordPress VIP team built its own version to handle more traffic. Frontenberg contains links to numerous Gutenberg resources, including <a href=\"http://vipgutenberg.com\" rel=\"noopener noreferrer\" target=\"_blank\">vipgutenberg.com</a>, which has some free training videos the team created for VIP clients.</p>\n<p>Nowell has written a post called <a href=\"https://tomjn.com/2018/01/22/how-frontenberg-works/\" rel=\"noopener noreferrer\" target=\"_blank\">How Frontenberg Works</a> for developers who are interested in the tech behind the tool. In it he describes the challenges he encountered in building Frontenberg and the solutions he wrote to make it work.</p>\n<p>The <a href=\"https://wptavern.com/first-look-at-try-gutenberg-prompt-in-wordpress-4-9-8-beta-2\" rel=\"noopener noreferrer\" target=\"_blank\">&#8220;Try Gutenberg&#8221; prompt</a> will soon be going out to millions of users in WordPress 4.9.8. Those who conservatively opt to use the Classic Editor plugin can still give Gutenberg a try using the Frontenberg tool or install it on a test site to see how interacts with themes and plugins.</p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Wed, 25 Jul 2018 19:59:44 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:13:\"Sarah Gooding\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:33;a:6:{s:4:\"data\";s:13:\"\n	\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:69:\"WPTavern: WordPress Core Fields API Project is Seeking New Leadership\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"https://wptavern.com/?p=82602\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:80:\"https://wptavern.com/wordpress-core-fields-api-project-is-seeking-new-leadership\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:10702:\"<p>In 2014, <a href=\"https://pods.io/\">Pods</a> lead developer, Scott Kingsley Clark, <a href=\"https://make.wordpress.org/core/2014/02/25/metadata-project-update/\">took over</a> the primary lead role for the Metadata UI project. In 2015, the Metadata UI project <a href=\"https://make.wordpress.org/core/2015/05/27/metadata-api-project-reborn-the-new-fields-api-project/\">was reborn</a> as the <a href=\"https://github.com/sc0ttkclark/wordpress-fields-api\">Fields API</a>.</p>\n\n<p>The Fields API was developed to allow registering fields to different screens in the admin area through a single API. New meta boxes and fields within them could be added to posts while new sections and fields could be added to the profile screen.</p>\n\n<p>The goal of the API is to integrate with all of the various admin screens including, Posts, Terms, Users, Media, and Comments and provide standardization.</p>\n\n<p>Clark has been leading the project for three years and despite seeing <a href=\"https://wptavern.com/wordpress-core-fields-api-project-sees-renewed-interest\">renewed interest</a> last year, <a href=\"https://wordpress.slack.com/messages/C04MWK7PZ/\">announced</a> in the project&#8217;s Slack channel that he is stepping down. <br /></p>\n\n<blockquote class=\"wp-block-quote\"><p>It is with a heavy heart that I must pass the torch on this project. After hundreds of hours of my time, I no longer believe I can effect change within WordPress core. </p><p>The Fields API vision was too big, too much of an undertaking for any one person. I believe so deeply that WordPress needs a Fields API, but the journey to where we are at with the Fields API has been long and arduous. </p><p>The truth is, I burned out years ago while building the first and second prototypes. Not everyone agreed on how to architect the code, it went through many revisions based on core contributor feedback. I just couldn&#8217;t get enough people excited about it, I couldn&#8217;t get enough companies and people interested in supporting it.</p><p>I need to let someone else have their chance, I am dragging it down. If someone steps up to lead in the future, then I would be happy to assist where I am able to. But I am unable to continue leading the Fields API proposal/project. I am sorry, please accept my apology and I hope you can forgive me for failing to take this project over the finish line. I still believe to be such a vital part of WordPress&#8217; future success.</p><cite>Scott Kingsley Clark</cite></blockquote>\n\n<h2>The Trials and Tribulations of Leading an Open Source Project</h2>\n\n<p>In the following interview, Clark explains why he feels personally responsible for the project&#8217;s lack of progress, why the API is important for WordPress&#8217; future, and reflects on what he could have done differently.</p>\n\n<p><strong>Are you looking to pass the torch on to anyone in particular?</strong><br /><br /></p>\n\n<p>No, I&#8217;m not sure who would have the drive and the clout to see the project through. It&#8217;s a large scale project that should be approached with a long-term vision but in small enough increments to make it into WordPress core. It&#8217;s a lot to ask of somebody, it&#8217;s also not a priority for people right now since they are distracted by Gutenberg being released in the near future.<br /></p>\n\n<p><strong>Why is the Fields API a vital part of WordPress&#8217; future?</strong><br /><br /></p>\n\n<p>People look at WordPress today and wonder how they ever survived without the REST API. Well, at least I know I do! The same thing can be said about the Fields API even though it&#8217;s not there yet. There are so many cases where it&#8217;s frustrating to build solutions for WordPress across all of the different hooks.<br /></p>\n\n<p>For consistency, it&#8217;s the wild west out there. You get a meta box registered and you fill it with whatever you want. You need your own CSS to style the form fields and everyone has their own idea of how this interface should look. You are in charge of your own responsive layouts that are mobile-friendly, there&#8217;s just so much you have to handle on your own. You should be able to customize appearances, but every place you want to add a field or form to should really have a proper API.<br /></p>\n\n<p>Long-term, imagine registering fields to WordPress like you register post types. Imagine fields and their configurations being available to the REST API and accessible through the WordPress App or other custom apps. </p>\n\n<p>The whole world opens up because you have a consistent API, the whole world make sense because you have a consistent interface for those fields across the various edit screens. Posts, terms, comments, users, media, even the Customizer would all have the same underlying API to add groups, panels, and fields to their screens.<br /></p>\n\n<p>If Gutenberg was done after the Fields API was in, migration for folks wouldn&#8217;t have been as difficult. Gutenberg could have automatically shown all of the Fields API interfaces like it does for the meta box backward compatibility. It would have looked so much nicer too.<br /></p>\n\n<p><strong>Taking some time to reflect, what could you have done differently to get more core contributors to buy into the project and turn it into a higher priority?</strong><br /><br /></p>\n\n<p>I&#8217;m not sure, it&#8217;s a delicate balance of taking input and being confident in the end result. At first, the feedback was about how the API was foreign for WordPress, they asked if it could be similar in structure to other APIs such as the Customizer. </p>\n\n<p>We scrapped the code and rebuilt from the ground up as a fork of the Customizer, it even supported having the Customizer utilizing the Fields API too. At the height of development, we had all areas of the Fields API implemented.<br /></p>\n\n<p>Core releases were moving pretty fast, there was a lot of code changes from WordPress release to release that we had to keep up with because we had essentially created a project that was a giant patch for WordPress. </p>\n\n<p>There weren&#8217;t enough hooks in place to do what we needed to do, and many sections were not extensible because of code decisions that marked themselves as &#8216;final&#8217;, which means you can&#8217;t extend a specific class to customize how it works.<br /></p>\n\n<p>I wish I could have been at all the big WordCamps in the US and Europe, essentially lobbying for this feature. Gathering supporters and such, it feels like politics in a way. I hung around in Core dev meetings, trying to bring it up. I tried to legitimize the feature by having a dedicated channel in the official WordPress Slack, posting updates on<a href=\"https://make.wordpress.org/core/\"> https://make.wordpress.org/core/</a>, and holding weekly meetings.<br /></p>\n\n<p>Ultimately, I prioritized my time for development over the time to gather the troops. That was the downfall, I began to burn out quickly after the first few rewrites as I had many other responsibilities elsewhere on top of Fields API.</p>\n\n<p>Its not like companies will easily want to pay you to work on a project like this indefinitely, even though both WebDevStudios and 10up gave me time to push it forward. It wasnt a blank check, at some point I had to get back to billable work. From then on, it was all in my free time and that was difficult to manage during times of financial stress and house selling/buying.<br /></p>\n\n<p><strong>There&#8217;s demand for a Fields API in core but not enough hands to build it. Why do you think that is?</strong><br /><br /></p>\n\n<p>Everyone is focused elsewhere. Theres a lot of areas of WordPress that need peoples attention. There are things like Accessibility that deserve a lot more attention than it gets. But the focus to me, seems to be on Gutenberg and REST API. </p>\n\n<p>Gutenberg especially has been a huge time sink for people contributing and people implementing. Its a really large feature. Its definitely larger in scale than Fields API, its like a whole new app that lives in WordPress. Integration with it has required a lot of education and trial/error. Peoples focus is where it needs to be right now. Its just unfortunate that Gutenberg came before Fields API in terms of priority and interest level.<br /></p>\n\n<p><strong>What advice would you give to the next Fields API project leader?</strong><br /><br /></p>\n\n<p>This is a big project, everyone will want to say it should be a certain way. You have to evaluate the options and put forth something bite sized for core to start with. Build upon that, but never lose sight of the long-term goal of integration across all of the WordPress screens. Even the front-end comment forms could thrive with the Fields API.<br /></p>\n\n<p><strong>Why do you feel personally responsible for the project not being a core priority?</strong><br /><br /></p>\n\n<p>At one point, we had momentum. We had at least three to four people who were active. It fell apart because I ran out of time. Its my shortsightedness, its my fault. I spent hundreds of hours developing the project over a couple of years. I should have left myself much more time for organizing the feature proposal text and keeping the fires burning in our contributors hearts.<br /></p>\n\n<p><strong>Considering the time and effort you&#8217;ve put into the project the last few years, do you feel any sense of relief passing the torch on?</strong><br /><br /></p>\n\n<p>If the torch gets passed or picked up, I will feel a ton better. The main relief is that its officially not a weight I have to carry alone any longer. Its okay to try and fail, its still sad though. </p>\n\n<p>I hope that someone or some company steps up and puts time into this. They could even reignite the fire in my own heart that burned itself out. For now, I have one less major to-do item. I still have a hefty plate but its no longer as heavy of a burden.</p>\n\n<hr class=\"wp-block-separator\" />\n\n<p>While the immediate future of the project is unclear, those interested in taking it over are encouraged to read posts marked with the <a href=\"https://make.wordpress.org/core/tag/fields-api/\">Fields API tag</a> on <a href=\"https://make.wordpress.org/core\">Make.WordPress.Core</a> to learn about its history. You can also check out the <a href=\"https://github.com/sc0ttkclark/wordpress-fields-api\">project&#8217;s Github page</a>. </p>\n\n<p>If you&#8217;re interested in taking over the project, you can contact Clark on <a href=\"https://twitter.com/scottkclark\">Twitter</a>, <a href=\"https://wordpress.slack.com/team/U02RSB1LH\">Slack</a>, or through <a href=\"https://www.scottkclark.com/\">his website</a>. </p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Wed, 25 Jul 2018 16:53:09 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:13:\"Jeff Chandler\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:34;a:6:{s:4:\"data\";s:11:\"\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:1:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:23:\"HeroPress: Global Unity\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"https://heropress.com/?p=2596\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:91:\"https://heropress.com/global-unity/#utm_source=rss&utm_medium=rss&utm_campaign=global-unity\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:3268:\"<img width=\"960\" height=\"480\" src=\"https://s20094.pcdn.co/wp-content/uploads/2017/04/041217-min-1024x512.jpg\" class=\"attachment-large size-large wp-post-image\" alt=\"Pull Quote: I am quite lucky to wake up every morning and work, all on my own, in a country where 54% of the population are unemployed and over 70% are youth.\" /><p>One of the things I&#8217;ve loved most about HeroPress is getting to know people far away. It&#8217;s getting harder and harder to name a country where I can&#8217;t say &#8220;Hey, I have a friend there!&#8221;. This week I&#8217;m in Australia for WordCamp Sydney, and I&#8217;m crazy excited about all the people I&#8217;m going to meet. The global unity of the WordPress community is exhilarating.</p>\n<p>The replay essay I picked for this week is titled &#8220;<strong>A Bottomless World of Possibilities</strong>&#8221; by Jamaal Jaamac in Mogadishu, Somalia. His world is so completely different from mine that sometimes I even have a hard time asking the right questions to find out what it&#8217;s like.</p>\n<p>Jamaal doesn&#8217;t live in a place where good work comes easy or often. It&#8217;s chiseled out of society with labor and love. There&#8217;s a stereotype that people in poor countries are lazy, but I think they have to be stronger and work harder just to make it. Jamaal is one of my heroes.</p>\n<p>Check out his essay.</p>\n<blockquote class=\"wp-embedded-content\"><p><a href=\"https://heropress.com/essays/bottomless-world-possibilities/\">A Bottomless World of Possibilities</a></p></blockquote>\n<p></p>\n<div class=\"rtsocial-container rtsocial-container-align-right rtsocial-horizontal\"><div class=\"rtsocial-twitter-horizontal\"><div class=\"rtsocial-twitter-horizontal-button\"><a title=\"Tweet: Global Unity\" class=\"rtsocial-twitter-button\" href=\"https://twitter.com/share?text=Global%20Unity&via=heropress&url=https%3A%2F%2Fheropress.com%2Fglobal-unity%2F\" rel=\"nofollow\" target=\"_blank\"></a></div></div><div class=\"rtsocial-fb-horizontal fb-light\"><div class=\"rtsocial-fb-horizontal-button\"><a title=\"Like: Global Unity\" class=\"rtsocial-fb-button rtsocial-fb-like-light\" href=\"https://www.facebook.com/sharer.php?u=https%3A%2F%2Fheropress.com%2Fglobal-unity%2F\" rel=\"nofollow\" target=\"_blank\"></a></div></div><div class=\"rtsocial-linkedin-horizontal\"><div class=\"rtsocial-linkedin-horizontal-button\"><a class=\"rtsocial-linkedin-button\" href=\"https://www.linkedin.com/shareArticle?mini=true&url=https%3A%2F%2Fheropress.com%2Fglobal-unity%2F&title=Global+Unity\" rel=\"nofollow\" target=\"_blank\" title=\"Share: Global Unity\"></a></div></div><div class=\"rtsocial-pinterest-horizontal\"><div class=\"rtsocial-pinterest-horizontal-button\"><a class=\"rtsocial-pinterest-button\" href=\"https://pinterest.com/pin/create/button/?url=https://heropress.com/global-unity/&media=https://heropress.com/wp-content/uploads/2017/04/041217-min-150x150.jpg&description=Global Unity\" rel=\"nofollow\" target=\"_blank\" title=\"Pin: Global Unity\"></a></div></div><a rel=\"nofollow\" class=\"perma-link\" href=\"https://heropress.com/global-unity/\" title=\"Global Unity\"></a></div><p>The post <a rel=\"nofollow\" href=\"https://heropress.com/global-unity/\">Global Unity</a> appeared first on <a rel=\"nofollow\" href=\"https://heropress.com\">HeroPress</a>.</p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Wed, 25 Jul 2018 10:52:45 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:35;a:6:{s:4:\"data\";s:13:\"\n	\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:79:\"WPTavern: Google Chrome Rolls Out Not Secure Warning for Plain HTTP Sites\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"https://wptavern.com/?p=82489\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:84:\"https://wptavern.com/google-chrome-rolls-out-not-secure-warning-for-plain-http-sites\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:7926:\"<p>As part of a long term plan to push the web to adopt HTTPS encryption, <a href=\"https://www.blog.google/products/chrome/milestone-chrome-security-marking-http-not-secure/\" rel=\"noopener noreferrer\" target=\"_blank\">Google Chrome is now marking all plain HTTP sites as &#8220;not secure,&#8221;</a> as of July 24, 2018, with the release of <a href=\"https://chromereleases.googleblog.com/2018/07/stable-channel-update-for-desktop.html\" rel=\"noopener noreferrer\" target=\"_blank\">Chrome 68</a>. Previously, the &#8220;not secure&#8221; warning was hidden behind the security indicator in the URL bar as shown below.</p>\n<p><a href=\"https://i2.wp.com/wptavern.com/wp-content/uploads/2018/07/Screen-Shot-2018-07-23-at-8.51.52-PM.png?ssl=1\"><img /></a></p>\n<p>That warning has become more prominent with the release of Chrome 68. The browser now immediately displays the &#8220;Not secure&#8221; message in the omnibox for all HTTP pages.</p>\n<p><a href=\"https://i0.wp.com/wptavern.com/wp-content/uploads/2018/07/warning-update.jpg?ssl=1\"><img /></a></p>\n<p>Today Google announced a time frame for eventually marking HTTP sites with a red &#8220;not secure&#8221; warning:</p>\n<blockquote><p>Eventually, our goal is to make it so that the only markings you see in Chrome are when a site is not secure, and the default unmarked state is secure. We will roll this out over time, starting by removing the Secure wording in September 2018. And in October 2018, well start showing a red not secure warning when users enter data on HTTP pages.</p></blockquote>\n<a href=\"https://i0.wp.com/wptavern.com/wp-content/uploads/2018/07/not-secure-eventual-warning.png?ssl=1\"><img /></a>image source: <a href=\"https://security.googleblog.com/2016/09/moving-towards-more-secure-web.html\">Google Security Blog</a>\n<p>Google Chrome currently captures <a href=\"http://gs.statcounter.com/browser-market-share\" rel=\"noopener noreferrer\" target=\"_blank\">60% of the browser marketshare worldwide</a>, making it one of the company&#8217;s most effective vehicles for driving HTTPS adoption. <a href=\"https://letsencrypt.org/\" rel=\"noopener noreferrer\" target=\"_blank\">Let&#8217;s Encrypt</a>, the free and open certificate authority (of which Chrome is a platinum sponsor), has also been a key player in precipitating the rise in secure traffic over the past few years. Firefox Telemetry shows that HTTPS traffic is at 81% for US users and 73% for all users.</p>\n<p><a href=\"https://i1.wp.com/wptavern.com/wp-content/uploads/2018/07/Screen-Shot-2018-07-24-at-10.20.50-AM.png?ssl=1\"><img /></a></p>\n<p><a href=\"https://transparencyreport.google.com/https/overview?hl=en\" rel=\"noopener noreferrer\" target=\"_blank\">Google&#8217;s Transparency report</a> shows similar numbers for percentage of pages loaded over HTTPS in Chrome. 84% of US traffic is encrypted by HTTPS.</p>\n<p><a href=\"https://i2.wp.com/wptavern.com/wp-content/uploads/2018/07/Screen-Shot-2018-07-24-at-10.40.02-AM-e1532446916438.png?ssl=1\"><img /></a></p>\n<p>Google has even more weapons in its arsenal for compelling website owners to switch to HTTPS. Even before Chrome began flagging unencrypted sites, the search engine added <a href=\"https://webmasters.googleblog.com/2014/08/https-as-ranking-signal.html\" rel=\"noopener noreferrer\" target=\"_blank\">HTTPS as a ranking signal in 2014</a>. It started as a lightweight signal that affected fewer than 1% of global queries. Google has also indicated that <a href=\"https://searchengineland.com/googles-gary-illyes-https-may-break-ties-between-two-equal-search-results-230691\" rel=\"noopener noreferrer\" target=\"_blank\">HTTPS may break ties between two equal search results</a>, making a difference for competitive niches. With more sites adopting HTTPS as the norm, the company may choose to strengthen the signal in the future.</p>\n<p>Not everyone is comfortable with a for-profit company making an aggressive push to require websites to deliver content over HTTPS. Some fear that prioritizing encryption in search results, while also using Chrome to cast doubt on websites&#8217; security, is just the beginning.</p>\n<p>Dave Winer, one of Google&#8217;s most vocal critics regarding this initiative, sees the push towards HTTPS as <a href=\"http://this.how/googleAndHttp/\" rel=\"noopener noreferrer\" target=\"_blank\">the company&#8217;s attempt to take control of the open web</a>. His concern is that if Google succeeds, it might &#8220;make a lot of the web&#8217;s history inaccessible.&#8221;</p>\n<p>&#8220;Google makes a popular browser and is a tech industry leader,&#8221; Winer said. &#8220;They can, they believe, encircle the web, and at first warn users as they access HTTP content. Very likely they will do more, requiring the user to consent to open a page, and then to block the pages outright.&#8221;</p>\n<blockquote class=\"twitter-tweet\">\n<p lang=\"en\" dir=\"ltr\">BTW, if I had to guess, the *real* reason Google hates HTTP has to do with Google\'s ad revenue, and not wanting Verizon and Comcast to remove their ads and replace them with their own. <img src=\"https://s.w.org/images/core/emoji/11/72x72/1f61c.png\" alt=\"\" class=\"wp-smiley\" /></p>\n<p>&mdash; scripting.com (@davewiner) <a href=\"https://twitter.com/davewiner/status/1021500417241427968?ref_src=twsrc%5Etfw\">July 23, 2018</a></p></blockquote>\n<p></p>\n<p>Others have speculated that another driving factor behind Google&#8217;s push for HTTPS adoption may be its investment in <a href=\"https://www.i-programmer.info/news/87-web-development/11367-google-starts-its-push-to-progressive-web-apps-bye-bye-chrome-apps.html\" rel=\"noopener noreferrer\" target=\"_blank\">advancing PWA technologies</a>, which require HTTPS to be enabled. Last year <a href=\"https://www.zdnet.com/article/google-removes-chrome-apps-from-chrome-web-store/\" rel=\"noopener noreferrer\" target=\"_blank\">Google dumped Chrome apps from the Chrome Web Store</a> in favor of building PWAs that can be installed on the desktop. HTTPS is a requirement for the permission workflows, new features, and updated APIs that the company is using to build its future products.</p>\n<p>It&#8217;s easy to see how HTTPS is critical for e-commerce, banking, and other sites that collect highly sensitive data from users, but many wonder if it is necessary for simple blogs and content websites. Google contends that <a href=\"https://developers.google.com/web/fundamentals/security/encrypt-in-transit/why-https\" rel=\"noopener noreferrer\" target=\"_blank\">all websites need HTTPS protection</a> to prevent intruders from injecting ads or exploits.</p>\n<p>Few would dispute the value of HTTPS but critics are wary of Google establishing itself as the arbiter of safe browsing on the web.</p>\n<blockquote class=\"twitter-tweet\">\n<p lang=\"en\" dir=\"ltr\">My blog, <a href=\"https://t.co/gPQ9VPYOLB\">https://t.co/gPQ9VPYOLB</a>, is not not secure. It uses the standard protocol of the web, HTTP.  It will not hurt you.</p>\n<p>&mdash; scripting.com (@davewiner) <a href=\"https://twitter.com/davewiner/status/1021713459380187136?ref_src=twsrc%5Etfw\">July 24, 2018</a></p></blockquote>\n<p></p>\n<p>For the moment, Winer seems to be committed to using HTTP to deliver his content. In Google&#8217;s feverish quest to push the entire web to adopt HTTPS, sites that are holding fast to HTTP on principle now appear as a sort of protest.</p>\n<p><a href=\"https://i0.wp.com/wptavern.com/wp-content/uploads/2018/07/Screen-Shot-2018-07-24-at-5.49.33-PM.png?ssl=1\"><img /></a></p>\n<p>&#8220;This blog and all my other sites use HTTP,&#8221; Winer said. &#8220;I don&#8217;t see that changing. I expect this will make writing for the web more of a chore. That&#8217;s life I guess. I don&#8217;t want Google to be able to mold the web to its needs. I never signed on to being a Google developer, and never would. Basic rule: Google is a guest on the web, as we all are, and guests don&#8217;t make the rules.&#8221;</p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Tue, 24 Jul 2018 23:07:54 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:13:\"Sarah Gooding\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:36;a:6:{s:4:\"data\";s:13:\"\n	\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:32:\"Mark Jaquith: Page Links To v3.0\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:40:\"http://markjaquith.wordpress.com/?p=5675\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:59:\"https://markjaquith.wordpress.com/2018/07/23/page-links-to/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:2482:\"<p>Today I pushed an update to my redirect and repointing plugin, <a href=\"https://wordpress.org/plugins/page-links-to/\">Page Links To</a>. Tomorrow, this plugin will have been in the WordPress.org Plugin Directory for 13 years (it was the 339th plugin in the WordPress plugin repository; there are now over 75,000!).</p>\n<p>To celebrate its transition to a teenager, I&#8217;ve added some new features and UI enhancements.</p>\n<p>Last month, I received survey responses from over 800 Page Links To users and learned a lot about how it&#8217;s being put to work. One of the most interesting things I found was how many people are using it for URL redirects. For example, they might have a really long URL on their own site or someone else&#8217;s site that they want to be nice. <em>example.com/summer-sale</em> instead of<em>example.com/store/specials.aspx?season=summer&amp;_utm_source=internal</em>. But in order to create these redirects, you have to go through the cluttered and sometimes slow post creation screen. All you really need to create a redirect is a title, a destination URL, and a local short URL.</p>\n<p>You&#8217;ll now find a menu item &#8220;Add Page Link&#8221; that will allow you to quickly add a redirected Page without having to wait for the entire WordPress post editing interface to load. It&#8217;s <em>super</em> fast, and it doesn&#8217;t redirect you away from the screen you&#8217;re on.</p>\n<p><img /></p>\n<p>Since short URLs are better for sharing (and remembering), the UI will give you a little push to shorten the URL if the one generated from your title is too long. From there, you can Save Draft or Publish.</p>\n<img />Hey, that URL is getting a bit long\n<img />Custom slug, for a better short URL\n<p>Additionally, this release includes a &#8220;link&#8221; indicator on post and page list screens, so you can easily see what items have been re-pointed with Page Links To. When hovered, the link icon will reveal the destination URL for a quick view.</p>\n<img />The &#8220;link&#8221; icon means that this item has been pointed elsewhere.\n<p>If you want to grab the &#8220;local&#8221; short URL (which will be redirected to your chosen URL when someone visits it), just click &#8220;Copy Short URL&#8221; from the actions, and it&#8217;ll be in your clipboard.</p>\n<img />Hover the &#8220;link&#8221; icon to see where it&#8217;s pointing.\n<p>That&#8217;s it for version 3.0, but I&#8217;ll have more to announce soon  stay tuned!</p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Mon, 23 Jul 2018 22:02:18 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:12:\"Mark Jaquith\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:37;a:6:{s:4:\"data\";s:13:\"\n	\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:81:\"WPTavern: Thoughts From Two Founders Who Recently Sold Their WordPress Businesses\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"https://wptavern.com/?p=82531\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:92:\"https://wptavern.com/thoughts-from-two-founders-who-recently-sold-their-wordpress-businesses\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:2299:\"<p>Last month, <a href=\"https://wptavern.com/wp-engine-acquires-studiopress\">WP Engine acquired StudioPress</a>.Brian Gardner, founder of StudioPress, recently <a href=\"https://authentik.com/sell/\">shared his thoughts</a> on why he didn&#8217;t want to sell the business, but did. </p>\n\n<blockquote class=\"wp-block-quote\"><p>Making a decision that not only affects your livelihood, the livelihood of your partners and employees, and the livelihood of an entire community isnt for the faint of heart. It certainly wasnt a responsibility I took lightly and spent quite a bit of time wrestling with.</p><p>The fact of the matter is this: When you make a decision that affects thousands of people, you <em>have</em> to wrestle with it. And then wrestle with it some more.</p><cite>Brian Gardner</cite></blockquote>\n\n<p>I appreciate Gardner&#8217;s down-to-earth perspective on how he reached the decision to sell. </p>\n\n<p>Ryan Sullivan, founder of WP Site Care, <a href=\"https://www.wpsitecare.com/wp-site-care-joins-forces-with-southern-web/\">sold his WordPress maintenance and support business</a> to Southern Web. On his personal site, <a href=\"https://www.ryandsullivan.com/changes/\">Sullivan describes</a> what it has been like to run a business the last seven years and drifting away from the reason he created the business in the first place. </p>\n\n<blockquote class=\"wp-block-quote\"><p>As the team grew, I slowly moved further and further away from the reason I started my business in the first place. Its a trend thats talked about extensively in the E-Myth. </p><p>People start a business because they love what they do, but then the growth of the business, management of people, and demands of administrivia become too much, and the person who started the business finds themselves in a totally foreign land with no roadmap for how to get back to the thing they once loved.</p><cite>Ryan Sullivan</cite></blockquote>\n\n<p>Merging with Southern Web allows Sullivan to get back to what he enjoys doing most, helping people and businesses with their sites. Sullivan also shared a great piece of advice, &#8220;Ask for help before you think you need it.&#8221;</p>\n\n<p>Both articles are great reads and provide insight into the tough decisions founders inevitably have to make. </p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Mon, 23 Jul 2018 21:08:26 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:13:\"Jeff Chandler\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:38;a:6:{s:4:\"data\";s:13:\"\n	\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:38:\"WPTavern: WP-CLI Hack Day Is A Success\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"https://wptavern.com/?p=82458\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:49:\"https://wptavern.com/wp-cli-hack-day-is-a-success\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:2239:\"<p>Alain Schlesser hosted the first <a href=\"https://wptavern.com/wp-cli-hack-day-friday-july-20th\">WP-CLI Hack Day</a> last Friday and by all accounts, it was a smashing success.</p>\n\n<p>The main goal was to merge 20 pull requests during the event but due to technical issues with <a href=\"https://travis-ci.org/\">Travis CI</a>, only 12 were merged.Travis CI is open source software used to perform automated testing. </p>\n\n<p>In addition to the 12 pull requests that were merged, 13 more were submitted. The last two hours of the event featured a video call where contributors could talk through issues or receive help on submitting pull requests. </p>\n\n<p>&#8220;We peaked at 13 participants to that video chat,&#8221; Schlesser said. &#8220;It allowed for people to make a more personal connection, which added another dimension to the act of contributing.&#8221;</p>\n\n<p>Despite not reaching the goal, Schlesser is pleased with how it turned out.</p>\n\n<p>&#8220;The event was also an experiment, to see whether a more direct, interactive form of contribution would attract more contributors,&#8221; he said. &#8220;I think it did succeed in doing so, and I think it was a positive experience for the participants.&#8221;</p>\n\n<p><a href=\"https://make.wordpress.org/cli/2018/07/21/wp-cli-hack-day-results/\">Feedback from participants</a> is positive and folks enjoyed having Schlesser available to answer questions and walk them through how to set up an environment for testing. </p>\n\n<blockquote class=\"wp-block-quote\"><p>Though we fell a little short of that goal, it was heartening to be a part of the communal experience, learn how to write my first Behat test (something that has been on my to-do list for a long time!), and get immediate feedback on my submitted pull request. And, of course, getting that PR merged was a thrill as well, as I can now say that Ive contributed back to the project.</p><cite><a href=\"https://make.wordpress.org/cli/2018/07/21/wp-cli-hack-day-results/#comment-133\">Jeremy Ward</a></cite></blockquote>\n\n<p>You can find links to all of the merged and submitted pull requests on the <a href=\"https://make.wordpress.org/cli/2018/07/21/wp-cli-hack-day-results/\">WP-CLI Hack Day summary page</a>.</p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Mon, 23 Jul 2018 19:43:19 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:13:\"Jeff Chandler\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:39;a:6:{s:4:\"data\";s:13:\"\n	\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:94:\"WPTavern: WordSesh Returns Wednesday, July 25, Experiments with Charging Attendees for Tickets\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"https://wptavern.com/?p=82496\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:103:\"https://wptavern.com/wordsesh-returns-wednesday-july-25-experiments-with-charging-attendees-for-tickets\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:6630:\"<p><a href=\"https://i1.wp.com/wptavern.com/wp-content/uploads/2018/07/wordsesh.png?ssl=1\"><img /></a></p>\n<p><a href=\"https://wordsesh.com/\" rel=\"noopener noreferrer\" target=\"_blank\">WordSesh</a>, a virtual conference dedicated to WordPress topics, is returning for a 5th edition on Wednesday, July 25, 2018. <a href=\"https://twitter.com/scottbasgaard\" rel=\"noopener noreferrer\" target=\"_blank\">Scott Basgaard</a>, the original organizer of the event, has passed the torch this year to <a href=\"https://twitter.com/rzen\" rel=\"noopener noreferrer\" target=\"_blank\">Brian Richards</a>, who has co-organized previous WordSesh events.</p>\n<p>The online-only event will feature 12 hours of sessions on a variety of tech and business topics, including e-commerce, freelancing, and security. In the past, WordSesh has been a marathon 24-hour event, but Richards decided to pare it down to 12 hours this year in order to organize the details quickly and get the next event on the calendar as soon as possible.</p>\n<h3>WordSesh Experiments with Charging Attendees for Tickets, Adds Real-Time Transcription</h3>\n<p>For the first time in WordSesh history, the event is charging attendees for tickets (rather than making it free for everyone as in years past). Richards is using the ticket money to pay speakers and have every video professionally transcribed in real-time. He has received some pushback on the decision but wants to see how things fare with this arrangement.</p>\n<p>&#8220;I thought the community might get behind that so we can stop asking speakers to completely volunteer their time, and the transcriptions are a huge accessibility bonus for non-native English speakers as well as the hard of hearing,&#8221; Richards said. &#8220;There have been just a few people pipe up to voice their distaste that I converted a free event to a paid event, which suggests there are many others stewing about it quietly, and Im guessing its because they dont realize where the moneys going (not that it should matter in either case).&#8221;</p>\n<p>Richards said tickets are dynamically priced to be the operational equivalent of $25 USD in every person&#8217;s home country. In India, for example, the ticket price is closer to $9 USD. He has kept quiet about the tech until now because he didn&#8217;t want people gaming the system but has since decided that it&#8217;s not a major concern.</p>\n<p>&#8220;The site accounts for purchase power parity so that the cost is not more than a couple of billable hours (or, ideally, much less) for everyone,&#8221; Richards said. &#8220;And for people whom the cost of a ticket is still a stretch, well, thats where the scholarships come in.&#8221;</p>\n<p>WordSesh still has 18 donated scholarships to give away but Richards said he is struggling to find people willing to apply to watch the event for free. Prospective attendees can <a href=\"https://wordsesh.com/product/wordsesh-scholarship/\" rel=\"noopener noreferrer\" target=\"_blank\">apply for a scholarship or nominate others to receive one</a>.</p>\n<p>A ticket to the event grants attendees access to the sessions, both live and after it has been recorded. Attendees will also receive virtual swag. A few samples include:</p>\n<ul>\n<li>A steep discount on WPSessions annual memberships (33%)</li>\n<li>Stripe is providing an exclusive link to skip the invitation process on their Atlas program and cover the cost for processing their first $5k of payments</li>\n<li>CorgiBytes is offering a $1k discount (~20%) on their code auditing services</li>\n</ul>\n<p>Previous WordSesh events averaged 1,000 online attendees and a good portion of them showed up the same day the events aired. This year there are approximately 400 registered so far. Richards said he won&#8217;t rule out the possibility of making the event free again for the next edition.</p>\n<p>&#8220;Asking more from sponsors could make a free event possible again without paying for everything personally (right now I&#8217;m still about $1000 in the red), but I really want to put the onus on the community at large,&#8221; Richard said. &#8220;Quality events take a lot of time and effort to accomplish, and most of that cost is on the backs of the organizers, speakers, and sponsors rather than attendees (at least in the WP community). Having been in all of those seats I&#8217;d really like to see more responsibility shift to the attendees. I would personally be happy to pay more for an event and be glad knowing that both speakers and organizers weren&#8217;t burning all this time in hopes of a return.&#8221;</p>\n<p>Virtual conferences have started gaining more traction in the global WordPress community after the success of previous WordSesh events. WordPress educator Zac Gordon recently hosted more than 1,000 attendees at his free <a href=\"https://wptavern.com/free-javascript-for-wordpress-conference-to-stream-live-friday-june-29\" rel=\"noopener noreferrer\" target=\"_blank\">JavaScript for WordPress conference</a> in June. The Polyglots&#8217; virtual <a href=\"https://wptranslationday.org/\" rel=\"noopener noreferrer\" target=\"_blank\">Global WordPress Translation day</a> has also attracted hundreds of participants at each event (448 attendees in April 2016, 780 attendees in November 2016, and 1,300 local event RSVPs in September 2017).</p>\n<p>Virtual events often reach far more people than an average local WordCamp, and many also inspire in-person meetups. Watch parties for WordSesh&#8217;s 5th edition are happening in cities across the globe, including Antwerp, Lagos, Minneapolis, Mumbai, and Bar Harbor (Maine).</p>\n<p>&#8220;Ive always enjoyed seeing people pour in to the event each year, and there was a delightful hum throughout the WordPress community every time a WordSesh took place,&#8221; Richards said.</p>\n<p>WordSesh has been a valuable addition to the virtual event space around WordPress, and attendee numbers at the upcoming event will be important for measuring how much traction the event can retain with its conversion to a paid event. Richards says he is committed to keeping the event alive and hopes to host it two times per year, which is one of the reasons for cutting it back to 12 hours.</p>\n<p>&#8220;Im already excited for the next event,&#8221; Richards said. &#8220;Im a little concerned at the moment, only because I dont expect this one will sell enough tickets to break even, but Im absolutely committed to hosting WordSesh again in the future. Im more interested in hosting an incredible event and providing a ton of value to attendees than I am in breaking even this time around.&#8221;</p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Mon, 23 Jul 2018 17:41:19 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:13:\"Sarah Gooding\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:40;a:6:{s:4:\"data\";s:13:\"\n	\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:74:\"WPTavern: Gutenberg 3.3 Released, Adds Archives and Recent Comments Blocks\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"https://wptavern.com/?p=82478\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:84:\"https://wptavern.com/gutenberg-3-3-released-adds-archives-and-recent-comments-blocks\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:1354:\"<p><a href=\"https://wordpress.org/plugins/gutenberg/\"></a></p>\n\n<p><a href=\"https://wordpress.org/plugins/gutenberg/\">Gutenberg 3.3</a> is available and <a href=\"https://make.wordpress.org/core/2018/07/20/whats-new-in-gutenberg-20th-july/\">continues the trend</a> of refining the user experience, user interface, and tools. Two new Widget blocks have been added, Post Archives and Recent Comments.<br /></p>\n\n<img />Archive and Recent Comments Widget Blocks\n\n<p>If your archives span across multiple months and years, you can configure the block to display as a drop-down menu. Otherwise, the list may be too long and look unwieldy on your site.</p>\n\n<p>Video blocks now have attributes users can can configure for Autoplay, Loop, Muted, and display Playback Controls. </p>\n\n<img />Video Block Attributes\n\n<p>There are a number of enhancements in this release that you can view via <a href=\"https://make.wordpress.org/core/2018/07/20/whats-new-in-gutenberg-20th-july/\">the change log</a>. </p>\n\n<p>Considering Gutenberg 3.2 <a href=\"https://make.wordpress.org/core/2018/07/06/whats-new-in-gutenberg-6th-july/\">released earlier this month</a> generally completed the MVP orminimum viable product feature set, users can expect more of these types of releases to tie up loose ends and prepare Gutenberg for merge into WordPress 5.0 later this year. </p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Sat, 21 Jul 2018 01:31:49 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:13:\"Jeff Chandler\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:41;a:6:{s:4:\"data\";s:13:\"\n	\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:94:\"WPTavern: Karachi to Host First WordCamp in Pakistan Following Cancellation of WordCamp Lahore\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"https://wptavern.com/?p=82062\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:105:\"https://wptavern.com/karachi-to-host-first-wordcamp-in-pakistan-following-cancellation-of-wordcamp-lahore\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:9479:\"<a href=\"https://i0.wp.com/wptavern.com/wp-content/uploads/2018/06/Karachi_from_above.jpg?ssl=1\"><img /></a>photo credit: <a href=\"https://commons.wikimedia.org/wiki/File:Karachi_from_above.jpg\">Bilalhassan88</a>\n<p><a href=\"https://2018.karachi.wordcamp.org/\" rel=\"noopener noreferrer\" target=\"_blank\">WordCamp Karachi</a> is happening on August 4, 2018, and organizers are expecting more than 400 attendees. Karachi is the largest city in Pakistan with a population of more than 30 million people. Its airport connects the country to other international cities and smaller cities within Pakistan are available by rail or bus. Organizers behind the camp are overcoming a rocky history that has plagued the planning of WordPress-related events in the country for the past several years.</p>\n<p>At the close of 2015, Pakistan was on track to host its first WordCamp in Lahore, building on momentum from the <a href=\"https://wptavern.com/wordpress-community-growing-in-pakistan-500-attend-lahore-meetup\" rel=\"noopener noreferrer\" target=\"_blank\">phenomenal growth of the local meetup group</a>. <a href=\"https://profiles.wordpress.org/xpertone\" rel=\"noopener noreferrer\" target=\"_blank\">Muhammad Kashif</a>, one of the organizers, was averaging 200-500 attendees at meetups and his team&#8217;s application to host <a href=\"https://wptavern.com/wordcamp-pakistan-application-approved-for-2016\" rel=\"noopener noreferrer\" target=\"_blank\">WordCamp Lahore was approved for 2016</a>.</p>\n<p>The meetup events, which attracted young students, developers, freelancers, and entrepreneurs, had grown so large that Kashif was having to close registration ahead of time because they were exceeding the capacity of the venue. Organizers expected 400-500 attendees at the WordCamp before it was cancelled after several delays near the end of the planning stage.</p>\n<p><a href=\"https://i2.wp.com/wptavern.com/wp-content/uploads/2015/12/lahore-pakistan-wordpress-meetup-featured.jpg?ssl=1\"><img /></a></p>\n<p>After contacting the WordPress Community team for the reason why it was cancelled, I received an official response from representative Hugh Lashbrooke:</p>\n<blockquote><p>Sometimes a WordCamp doesnt make it all the way from pre-planning to being an actual event  occasionally due to logistical challenges, but other times because of broader concerns. In this case, once the WordCamp planning was underway, some tense dynamics arose within the local team. Everyone worked really hard to reach a positive solution, but we werent able to move forward with the event in the end.</p>\n<p>Cancelling an event is never anyones preference, to be clear, but it is also a customary response when its clear that collaboration isnt possible in the short term. Local organizers are always encouraged to reapply as soon as the barriers to collaboration have been resolved.</p></blockquote>\n<p>Lashbrooke also cited issues with the camp&#8217;s budget for the venue organizers selected. He said the situation was thoroughly investigated before the camp was cancelled and that they would never take that kind of decision lightly.</p>\n<p>WordCamp Lahore organizers gave me a different report on what happened and were disappointed with the decision from the Community Team.</p>\n<p>&#8220;Representatives from the Foundation cancelled the camp in the last stage based on a complaint from one person out of a community of 3,000 people,&#8221; Kashif said. &#8220;The Foundation made a direct decision of cancellation without giving me a chance for explanation as the Primary Organizer. I suggested the Foundation conduct a survey from the whole Lahore community to know the facts but they had already made the decision to cancel.&#8221;</p>\n<p>Kashif claims there was no disagreement between the organizers, because the community member in question, <a href=\"https://profiles.wordpress.org/mrahmadawais\" rel=\"noopener noreferrer\" target=\"_blank\">Ahmad Awais</a>, was never part of the original organizing team.</p>\n<p>One member of the planning team agreed to speak to me anonymously about why he believes the camp was canceled. He claims that Awais fractured the team after his request to be given a prominent position in the organization was denied.</p>\n<p>&#8220;Ahmad Awais started hijacking Kashif and his team,&#8221; he said. &#8220;He was not a lead organizer at all, but on the planning team. Instead of contributing, Ahmad wanted to speak at every meetup and didnt spend time with organizing team. He broke the team and started pointing fingers at Kashif and the existing organizing team. He was NOT there until WC Lahore was approved. He joined the team after that.&#8221;</p>\n<p>Multiple leaders from WordCamp Karachi&#8217;s 14-person organizing team, who wish to remain anonymous, have reported that Awais also requested to be named keynote speaker and was unhappy with the team after they told him that speaker decisions do not work like that. Several said they feared his retaliation against the camp, given they previously had no recourse with the Community Team after the experience of WordCamp Lahore getting shut down.</p>\n<p>Awais is not attending WordCamp Karachi and has declined to answer any questions about WordCamp Lahore due to personal reasons.</p>\n<p>Kashif received hundreds of messages from the community after the event in Lahore was cancelled. He had been working since 2013 to bring a WordCamp to the area. He said that while the Community Team was helpful with other queries he had, he was disappointed with how quickly the camp was cancelled.</p>\n<p>&#8220;I worked tirelessly for years to get a WordCamp to Lahore and grew the community from 430 to 2600 in one year but the Foundation didnt even give me a chance to explain before canceling WC Lahore, Kashif said. &#8220;I am a bit disheartened by that action.&#8221;</p>\n<p>Kashif is now helping with other WordCamps, including Karachi and NYC. He is also willing to work to re-establish a WordCamp Lahore in the future.</p>\n<p>&#8220;Like me there are lots of other WordPress enthusiasts in Pakistan who want to participate in WordCamps but not everyone can do that outside of Pakistan due to financial or visa reasons,&#8221; Kashif said. &#8220;I was accepted as a volunteer in WordCamp Europe but couldnt join due to visa issues. I have seen so much excitement for WordCamp Karachi in the WordPress community across the whole country. Everyone is willing to help to make it a successful event so that there are more frequent WordCamps in Pakistan. The first one is so important as it will set expectations of outcomes of a WordCamp for people who are new to these events.&#8221;</p>\n<h3>WordCamp Karachi Organizers Aim to Make a Positive Impact on Pakistan</h3>\n<p>WordCamp Karachi organizers are attempting to move past the Pakistani WordPress community&#8217;s checkered history of contention. They are also eager to distance themselves from the conflict that took down WordCamp Lahore.</p>\n<p><a href=\"https://profiles.wordpress.org/usmankhalid/\" rel=\"noopener noreferrer\" target=\"_blank\">Usman Khalid</a>, lead organizer of the camp, said he has put in a lot of effort asking organizers and volunteers to work together, regardless of the credit they will receive, and focus on the community.</p>\n<p>&#8220;I want to spend time on having a positive impact for this country, avoiding any kind of noise,&#8221; Khalid said. &#8220;The Pakistani WordPress community is vibrant in many ways. We have around 6,000 plus WordPressers in our community, with people who are selling their products and services worldwide.&#8221;</p>\n<p>Khalid said there are many users in Karachi, Lahore, Islamabad, Peshawer, and Faisalabad who are doing well with WordPress but are not yet connected to the community or participating in meetups and events. People from all over Pakistan will be attending the event as their first exposure to the WordPress community. Sessions will mostly be conducted in English but a few will be mixed with Urdu, the regional language.</p>\n<p>Khalid has experienced many growing pains in bringing the event to a community that is new to understanding how WordCamps work. In a country that is <a href=\"https://wptavern.com/pakistan-is-blocking-sites-hosted-on-wordpress-com\" rel=\"noopener noreferrer\" target=\"_blank\">notorious for its censorship</a>, WordPress&#8217; mission of democratizing publishing has the potential to change many lives.</p>\n<p>&#8220;Since this is first Wordcamp, many people even don&#8217;t know about it,&#8221; Khalid said. &#8220;Those who know want to be a speaker, or any lead role that can promote them. When we tell them it doesn&#8217;t work like this, they are mostly get annoyed.&#8221;</p>\n<p>Tech publications around Pakistan have not been exposed to WordCamps either. Khalid said several of them have asked for money in order to cover the story of the event, unaware that the purpose of the camps and meetups is not to translate everything into business but to give back to the community.</p>\n<p>&#8220;I am simply working to actually deliver first and prove that this community can create a positive impact,&#8221; Khalid said. &#8220;I am sure after Wordcamp Karachi, the entire community within Pakistan will flourish, regardless of what happened in Lahore.&#8221; He said he hopes the next camps will be even more interesting in the future and that WordCamp Karachi will help to create new jobs and more businesses in Pakistan.</p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Fri, 20 Jul 2018 23:14:27 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:13:\"Sarah Gooding\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:42;a:6:{s:4:\"data\";s:13:\"\n	\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:69:\"Post Status: Why the makers of Ninja Forms are getting into eCommerce\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"https://poststatus.com/?p=46627\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:80:\"https://poststatus.com/why-the-makers-of-ninja-forms-are-getting-into-ecommerce/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:1982:\"<p>Welcome to the Post Status <a href=\"https://poststatus.com/category/draft\">Draft podcast</a>, which you can find <a href=\"https://itunes.apple.com/us/podcast/post-status-draft-wordpress/id976403008\">on iTunes</a>, <a href=\"https://play.google.com/music/m/Ih5egfxskgcec4qadr3f4zfpzzm?t=Post_Status__Draft_WordPress_Podcast\">Google Play</a>, <a href=\"http://www.stitcher.com/podcast/krogsgard/post-status-draft-wordpress-podcast\">Stitcher</a>, and <a href=\"http://simplecast.fm/podcasts/1061/rss\">via RSS</a> for your favorite podcatcher. Post Status Draft is hosted by Brian Krogsgard and co-host Brian Richards.</p>\n<p>In this episode, I bring on James Laws and Kevin Stover &#8212; cofounders of Saturday Drive, the parent company of Ninja Forms &#8212; to discuss why they are entering the world of eCommerce. I found this especially interesting given the market dominance of WooCommerce for general WordPress-based eCommerce needs.</p>\n<p>Saturday Drive purchased Exchange, the plugin initially developed by iThemes and then handed off to A.J. Morris, with the intention of making a play for the eCommerce market, much like they did successfully once already for the somewhat-saturated forms market.</p>\n<p></p>\n<h3>Episode Links</h3>\n<ul>\n<li>Ninja Shop <a href=\"https://wordpress.org/plugins/ninja-shop/\">plugin</a></li>\n<li>Ninja Shop <a href=\"https://ninjashop.site/\">website</a></li>\n<li>James Laws <a href=\"https://wordpress.org/plugins/ninja-shop/\">on Twitter</a></li>\n<li>Kevin Stover <a href=\"https://twitter.com/kstover\">on Twitter</a></li>\n</ul>\n<h3>Sponsor: Sandhills Development</h3>\n<p><a href=\"http://sandhillsdev.com/\">Sandhills Development</a> makes a suite of excellent plugins to power your WordPress website. Whether you need to sell digital downloads, restrict content, create an affiliate program, or manage an events calendar, they&#8217;ve got you covered. Thanks to <a href=\"http://sandhillsdev.com/\">Sandhills</a> for being a Post Status partner.</p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Fri, 20 Jul 2018 22:29:03 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:15:\"Brian Krogsgard\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:43;a:6:{s:4:\"data\";s:13:\"\n	\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:63:\"WPTavern: WordPress 4.9.8 Will Significantly Reduce Memory Leak\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"https://wptavern.com/?p=82423\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:74:\"https://wptavern.com/wordpress-4-9-8-will-significantly-reduce-memory-leak\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:2723:\"<p><a href=\"https://make.wordpress.org/core/2018/07/19/wordpress-4-9-8-beta-2/\">WordPress 4.9.8 Beta two</a> recently shipped and although much of the focus will be on the <a href=\"https://wptavern.com/first-look-at-try-gutenberg-prompt-in-wordpress-4-9-8-beta-2\">&#8220;Try Gutenberg&#8221;  call-out</a>, there&#8217;s a patch included that addresses a memory leak that was quite a problem for some users. </p>\n\n<p>When <a href=\"https://wordpress.org/news/2018/07/wordpress-4-9-7-security-and-maintenance-release/\">WordPress 4.9.7 shipped</a>,the WordPress.org support forums saw an <a href=\"https://wordpress.org/support/topic/wp_is_stream-crashing-the-server/\">increase</a> in <a href=\"https://wordpress.org/support/topic/fatal-error-allowed-memory-size-of-11/\">reports</a> of memory exhausted errors due to the <a href=\"https://developer.wordpress.org/reference/functions/wp_is_stream/\">wp_is_stream function</a>. </p>\n\n<p>The bug is actually in PHP and is related to the <a href=\"http://php.net/manual/en/function.stream-get-wrappers.php\">stream-get-wrappers</a> function. WordPress 4.9.7 increased the number of times it calls the wp_is_stream() function generating the error more often.</p>\n\n<p>After thoughtful conversations within the <a href=\"https://core.trac.wordpress.org/ticket/44532#comment:46\">trac ticket</a>, a patch was created that after further testing, was committed to core. <a href=\"https://core.trac.wordpress.org/ticket/44532#comment:46\">According to Gary Pendergast</a>, the patch does the following.</p>\n\n<blockquote class=\"wp-block-quote\">\n	<p>Bailing if the path isn&#8217;t a stream. This isn&#8217;t perfect: it&#8217;s still possible to trigger the PHP bug, but it does significantly reduce the chances of the bug being encountered. For many WordPress sites (those that don&#8217;t have plugins that use streams), it reduces the chance to zero.</p>\n</blockquote>\n\n<p>If you encountered memory exhaustion errors after installing or upgrading to WordPress 4.9.7, try <a href=\"https://make.wordpress.org/core/2018/07/19/wordpress-4-9-8-beta-2/\">WordPress 4.9.8 Beta 2</a> to see if it fixes the issue.</p>\n\n<p>Aside from coming up with a solution, <a href=\"https://core.trac.wordpress.org/ticket/44532\">Trac ticket #44532</a> is a bit refreshing to see. In open source, tickets or issues can get bogged down with suggestions, hypothetical scenarios, and bike-shed commentary.</p>\n\n<p>In this instance, the issue was identified and folks from the community along with core contributors worked together in a focused fashion to create a fix in time for the next point release. The ticket is a good example of what it&#8217;s like when open source is firing on all cylinders. <br /></p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Fri, 20 Jul 2018 20:33:11 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:13:\"Jeff Chandler\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:44;a:6:{s:4:\"data\";s:13:\"\n	\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:42:\"Donncha: WP Super Cache and Cookie Banners\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:28:\"https://odd.blog/?p=89501949\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:62:\"https://odd.blog/2018/07/20/wp-super-cache-and-cookie-banners/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:5289:\"<p>More sites use cookie banners now that the GDPR is active but some are finding that their banners are misbehaving once they enable caching.</p>\n\n<p>This is a similar issue to the one that happened to some page counter plugins in the past. The page counter wouldn&#8217;t increment.</p>\n\n<p>When a cookie banner is clicked a cookie is set in the browser so the website knows this visitor has agreed to accept cookies. If the cookie is set then the cookie banner html is not sent to the browser.</p>\n\n<p>I suspect the main issue is that the code that sets and checks if the cookie is set is PHP. Unfortunately because the page is cached then no PHP code is executed, and the cookie banner is displayed because it was originally cached that way.<br /></p>\n\n<p>Since WP Super Cache only knows about certain WordPress cookies it assumes everyone who doesn&#8217;t have those cookies is a first time &#8220;anonymous&#8221; visitor. It doesn&#8217;t know about your cookie banner cookie.<br /></p>\n\n<p>You have two options:</p>\n\n<ol>\n	<li>Rewrite your cookie banner so it&#8217;s completely in Javascript. Do the cookie detection in Javascript and also set the cookie in Javascript. If the cookie banner has been clicked then you need to trigger an action, and other Javascript that is hooked on to that trigger will run and load the tracking cookies.</li>\n	<li>Modify WP Super Cache so it knows about the cookie your cookie banner uses. Caching won&#8217;t work quite as well as before as it&#8217;ll be split between visitors who have clicked the cookie banner and those that haven&#8217;t. One cached file will display the cookie banner, and the other will not but it will have ad tracking Javascript.<br /></li>\n</ol>\n\n<p>Using Javascript completely is a better solution because it runs in the browser on every page load but that might not be possible every time.</p>\n\n<p>Otherwise, use PHP to get WP Super Cache to play nicely with your existing code:</p>\n\n<ol>\n	<li>You&#8217;ll need to write a <a href=\"https://odd.blog/2017/10/25/writing-wp-super-cache-plugins/\">WP Super Cache plugin</a>.</li>\n	<li>You need to hook into the wp_cache_get_cookies_values cacheaction and add the value of the cookie banner cookie to the end of that string.</li>\n	<li>Caching can only be performed by <em>simple</em> caching now, unless you&#8217;re willing to edit mod_rewrite rules in your .htaccess file.</li>\n</ol>\n\n<p>Something like this will do. Make sure you note the warning about <code>$wp_cache_plugins_dir</code> in the link above about writing these plugins.<br /></p>\n\n<pre class=\"wp-block-code\"><code>function add_cookie_banner_to_cache_cookie( $string ) {\n    if ( isset( $_COOKIE[\'cookie_banner\'] ) ) {\n        $string .= \'cb,\';\n    }\n    return $string\n}\nadd_cacheaction( \'wp_cache_get_cookies_values\', \'add_cookie_banner_to_cache_cookie\' );</code></pre>\n\n<p>Substitute the name of the cookie for your cookie name, change the name of the function, and the text it adds to the string. There is an intentional PHP fatal error in the code above to discourage copy/pasting.<br /></p>\n\n<p>Your cookie banner plugin could automate setting this up, but it may have unforeseen consequences if not done correctly. It should check if <code>$wp_cache_plugins_dir</code> is set already, and use that location, otherwise it will have to make a directory and update the WP Super Cache configuration, where ABC is the new location for the plugins.<br /></p>\n\n<p><code>wp_cache_setting( \'wp_cache_plugins_dir\', ABSPATH . \'wp-content/ABC\' );</code></p>\n\n<p>The new code can be copied into a file in that directory. The files in the original WP Super Cache plugins directory (found at <code>WPCACHEHOME . \'plugins\'</code>) should be copied into that directory too and a warning shown to the user. They may need to set up one of those plugins again.<br /></p>\n\n<p>The reason it is this convoluted is because this code will run before all of WordPress loads. You can&#8217;t rely on blog options or most of the nice configuration tools WordPress provides.<br /></p>\n\n<p>When your plugin is uninstalled it should of course restore the plugins directory to the way it was before.</p>\n\n<p>For future reference, since cookie banners will hopefully not be around forever, here&#8217;s what they looked like in the deep, distant past of 2018. <img src=\"https://s.w.org/images/core/emoji/11/72x72/1f642.png\" alt=\"\" class=\"wp-smiley\" /></p>\n\n<img />\n\n<img />\n	The LA Times just gave up and don&#8217;t show anything to EU visitors.\n\n\n<img />\n\n<img />\n\n<img />\n\n<p><strong>Related Posts</strong><ul><li> <a href=\"https://odd.blog/2003/08/12/linuxie-and-free-software-projects/\" rel=\"bookmark\" title=\"Permanent Link: Linux.ie and free software projects\">Linux.ie and free software projects</a></li><li> <a href=\"https://odd.blog/2008/10/24/wp-super-cache-084-the-garbage-collector/\" rel=\"bookmark\" title=\"Permanent Link: WP Super Cache 0.8.4, the garbage collector\">WP Super Cache 0.8.4, the garbage collector</a></li><li> <a href=\"https://odd.blog/2008/03/06/please-sir-can-i-have-more/\" rel=\"bookmark\" title=\"Permanent Link: Please sir, can I have more?\">Please sir, can I have more?</a></li></ul></p>\n<p><a href=\"https://odd.blog/2018/07/20/wp-super-cache-and-cookie-banners/\" rel=\"nofollow\">Source</a></p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Fri, 20 Jul 2018 13:33:08 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:7:\"Donncha\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:45;a:6:{s:4:\"data\";s:13:\"\n	\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:76:\"WPTavern: First Look at Try Gutenberg Prompt in WordPress 4.9.8 Beta 2\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"https://wptavern.com/?p=82425\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:81:\"https://wptavern.com/first-look-at-try-gutenberg-prompt-in-wordpress-4-9-8-beta-2\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:3384:\"<p><a href=\"https://make.wordpress.org/core/2018/07/19/wordpress-4-9-8-beta-2/\" rel=\"noopener noreferrer\" target=\"_blank\">WordPress 4.9.8 Beta 2</a> was released today, featuring the new &#8220;<a href=\"https://core.trac.wordpress.org/ticket/41316\" rel=\"noopener noreferrer\" target=\"_blank\">Try Gutenberg</a>&#8221; prompt that will appear in user dashboards when the official release drops at the end of the month.</p>\n<p>The prompt invites users to install Gutenberg if they want to try the new editor or install the <a href=\"https://wordpress.org/plugins/classic-editor/\" rel=\"noopener noreferrer\" target=\"_blank\">Classic Editor</a> to keep using the current editor until they are ready to make the change. WordPress contributors discussed variations on the design and wording of the callout and finally settled on what you see in 4.9.8 Beta 2.</p>\n<p><a href=\"https://i2.wp.com/wptavern.com/wp-content/uploads/2018/07/Screen-Shot-2018-07-19-at-4.02.56-PM.png?ssl=1\"><img /></a></p>\n<p>Even if users don&#8217;t get involved in Gutenberg testing, the callout serves to inform them that the new editor will be enabled by default in the next major release of WordPress. It includes a link to the <a href=\"https://wordpress.org/gutenberg/\" rel=\"noopener noreferrer\" target=\"_blank\">Gutenberg information page</a> so users can learn more about the project.</p>\n<p>Contributors agreed that they wanted to clearly communicate three important points in the callout, as per designer @kjellr&#8217;s <a href=\"https://core.trac.wordpress.org/ticket/41316#comment:148\" rel=\"noopener noreferrer\" target=\"_blank\">suggestions on trac</a>:</p>\n<ol>\n<li>Gutenberg is coming in the next major release.</li>\n<li>If youre worried about compatibility, theres a plugin to help ease the transition.</li>\n<li>The plugin lets you use the editor youre used to until youre ready to switch.</li>\n</ol>\n<p>The prompt is clearly geared towards encouraging users to test Gutenberg, as that section has a more prominent, colored button. If your clients&#8217; installations are not ready for users to act on the &#8220;Try Gutenberg&#8221; prompt, now is the time to install a plugin that will disable it. Clients with free-range of the WordPress admin, in sites that are running Gutenberg-compatible extensions, are better candidates for testing the new editor.</p>\n<p>The <a href=\"https://wptavern.com/new-classic-editor-addon-plugin-disables-the-try-gutenberg-prompt-coming-in-wordpress-4-9-8\" rel=\"noopener noreferrer\" target=\"_blank\">Classic Editor Addon</a> is one option that will suppress the prompt and automatically suppress Gutenberg when it ships in WordPress 5.0. It was also recently updated to auto-install the Classic Editor plugin as a dependency so users don&#8217;t have to install two plugins as part of the process.</p>\n<p>A release candidate is slated for July 24, and the official 4.9.8 release is scheduled for July 31st. The <a href=\"https://wordpress.org/plugins/gutenberg/\" rel=\"noopener noreferrer\" target=\"_blank\">Gutenberg plugin</a> is currently sitting at 10,000+ active installations and the <a href=\"https://wordpress.org/plugins/classic-editor/\" rel=\"noopener noreferrer\" target=\"_blank\">Classic Editor</a> at 5,000+. After 4.9.8 is released, changes in these numbers will demonstrate how WordPress users across the globe are responding to the call for testing.</p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Thu, 19 Jul 2018 21:48:51 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:13:\"Sarah Gooding\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:46;a:6:{s:4:\"data\";s:13:\"\n	\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:74:\"WPTavern: Insight Into How North Carolina State Is Preparing for Gutenberg\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"https://wptavern.com/?p=82410\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:85:\"https://wptavern.com/insight-into-how-north-carolina-state-is-preparing-for-gutenberg\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:1838:\"<p>Jennifer McFarland, the web services coordinator for the North Carolina State Office of Information Technology (OIT), recently <a href=\"http://www.technicianonline.com/news/article_878f4bb0-7d8f-11e8-a5ac-4bf05738fe93.html\">did an interview</a> with <a href=\"http://www.technicianonline.com/\">Technician</a>, the official student newspaper of NC State University.</p>\n\n<p>Since Gutenberg&#8217;s announcement in December of 2017, McFarland has published a <a href=\"https://design.oit.ncsu.edu/tag/gutenberg/\">series of articles</a> on the NC State Office of Information Technology website educating staff and students on what to expect.</p>\n\n<p>McFarland was asked how the team plans to help staff and students after Gutenberg ships with WordPress.</p>\n\n<p>&#8220;A couple of times a year, we go and speak at various classes, professors will have us come out and do demos of WordPress,&#8221; McFarland told the Technician. </p>\n\n<p>&#8220;We are expecting an uptake of people requesting us to come out and do a demo of WordPress or something like that, but generally our plan right now, at least for students, is mostly just offer the sort of self-help, like the video tutorials and things like that, and we figure that the students will mostly try and solve their own problems.&#8221;</p>\n\n<p>Speaking of McFarland, she and Brian DeConinck <a href=\"https://2018.wpcampus.org/schedule/gutenready-for-the-gutenpocalypse/\">presented on Gutenberg</a> at WPCampus 2018. The videos from the event are still being processed but we&#8217;ll add a link to the presentation once it becomes available. </p>\n\n<p>It&#8217;s pretty cool to see people in McFarland&#8217;s position in higher education already have a firm grasp of Gutenberg and doing what they can to ease the transition for staff and students</p>\n\n<p></p>\n\n<p><br /></p><br /></p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Thu, 19 Jul 2018 00:27:00 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:13:\"Jeff Chandler\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:47;a:6:{s:4:\"data\";s:13:\"\n	\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:83:\"WPTavern: Meet Bernhard Kau, Local Lead Organizer of WordCamp Europe 2019 in Berlin\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"https://wptavern.com/?p=82335\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:93:\"https://wptavern.com/meet-bernhard-kau-local-lead-organizer-of-wordcamp-europe-2019-in-berlin\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:4788:\"<p><a href=\"https://twitter.com/2ndkauboy?lang=en\" rel=\"noopener noreferrer\" target=\"_blank\">Bernhard Kau</a>, a WordPress developer, meetup organizer, and four-time organizer of WordCamp Berlin, is the local lead organizer for the next WordCamp Europe. Kau will join Milan Ivanovi, the global lead, at the helm of WordCamp Europe in Berlin next June.</p>\n<p>Kau submitted an application with his team to host WCEU and won out over a competing application from the WordPress community in Barcelona. The selection team cited Berlin&#8217;s accessibility, reasonable prices, and its strong community as the final deciding factors.</p>\n<p>Berlin&#8217;s monthly WordPress meetups have 1,300 members. The community also holds dedicated meetups for beginners, developers, and women in WordPress. Five WordCamps have been organized in Berlin since 2010.</p>\n<blockquote class=\"twitter-tweet\">\n<p lang=\"en\" dir=\"ltr\">Attending <a href=\"https://twitter.com/hashtag/WPBerlin?src=hash&ref_src=twsrc%5Etfw\">#WPBerlin</a> and they are already preparing WordPress community for next <a href=\"https://twitter.com/hashtag/WCEU?src=hash&ref_src=twsrc%5Etfw\">#WCEU</a> <a href=\"https://t.co/MWLwTZ4hrf\">pic.twitter.com/MWLwTZ4hrf</a></p>\n<p>&mdash; Milan Ivanovi (@lanche86) <a href=\"https://twitter.com/lanche86/status/1012396086944813057?ref_src=twsrc%5Etfw\">June 28, 2018</a></p></blockquote>\n<p></p>\n<p>&#8220;Compared to other communities in Europe, we are a community that is very likely to travel to other cities to attend meetups and other WordCamps,&#8221; Kau said. &#8220;We usually only have one or two WordCamps per year in Germany but then usually all the German community members travel to that city to attend the WordCamp. Whereas in Spain, for example, they had 11 WordCamps this year that are more local and smaller. As a German community we are more used to traveling to a central place and meeting there.&#8221;</p>\n<h3>What to Expect at WCEU in Berlin: A Diverse Community, More Workshops, and a Unique After Party</h3>\n<p>The <a href=\"https://www.estrel.com/en/home.html\" rel=\"noopener noreferrer\" target=\"_blank\">Estrel Hotel and Congress Center</a> will host the entire event, including both conference days, the contributor day, and the after party. Although the venue has a max capacity of 12,000 people, Kau said organizers are planning for 2,500 &#8211; 3,000 attendees. The local team is excited to introduce the European WordPress community to their home city.</p>\n<p>&#8220;Berlin is one of the most diverse cities in Europe,&#8221; Kau said. &#8220;When I prepared the application, I figured out that there are people from 191 countries living in Berlin. It&#8217;s a very international, very diverse city, so you can be just as you are and feel quite comfortable and welcome in Berlin.&#8221;</p>\n<p>Kau said organizers intend to continue with workshops as a part of the event in 2019 but they are planning to make the signup experience more efficient.</p>\n<p>&#8220;This was the first year we tried workshops,&#8221; Kau said. &#8220;We had three workshop tracks and workshops of 60 minutes, 90 minutes, and three hours. We are not sure how many workshops we want to have in Berlin. The idea was new but turned out quite well. There were workshops with many people waiting to get in and from what I&#8217;ve heard it was quite good.&#8221;</p>\n<p>Kau said he wants to improve the process for workshops, because there was no easy way for attendees to sign up and managing waiting lists was a lot of manual work for the content team. This is one bottleneck from the most recent WCEU experience that he hopes to rectify.</p>\n<p>&#8220;There is also something special planned but I don&#8217;t want to spoil it,&#8221; Kau said. &#8220;It&#8217;s going to be a very unique after party to say the least.&#8221; Although Berlin is renowned for its legendary nightlife, Kau said he doesn&#8217;t anticipate the party lasting all night.</p>\n<p>&#8220;We&#8217;re probably not going to make it Berlin-typical until 10 in the morning but it&#8217;s going to be a bit longer than maybe here [Belgrade] or in Paris where people were kicked at at 3:30,&#8221; he said.</p>\n<p>The <a href=\"https://2019.europe.wordcamp.org/2018/06/16/call-wordcamp-europe-2019-organizers/\" rel=\"noopener noreferrer\" target=\"_blank\">call for applications for organizers</a> is still open. Within 24 hours of announcing Berlin as the next host city, the team had already <a href=\"https://twitter.com/lanche86/status/1008419688009863169\" rel=\"noopener noreferrer\" target=\"_blank\">received 27 applications</a>. The application window closes July 31, 2018.</p>\n<p>Check out the full interview below to learn more about the German WordPress community and what they have planned for WCEU 2019.</p>\n<p></p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Wed, 18 Jul 2018 20:18:28 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:13:\"Sarah Gooding\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:48;a:6:{s:4:\"data\";s:13:\"\n	\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:43:\"WPTavern: WP-CLI Hack Day Friday, July 20th\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"https://wptavern.com/?p=82269\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:53:\"https://wptavern.com/wp-cli-hack-day-friday-july-20th\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:1541:\"<p><a href=\"https://wp-cli.org/\">WP-CLI</a> or WordPress Command Line Interface has become an integral tool for developers to launch and manage sites. To encourage new contributors to the project, Alain Schlesser is organizing the first <a href=\"https://make.wordpress.org/cli/2018/07/04/wp-cli-hack-day/\">WP-CLI Hack Day</a>, Friday, July 20th beginning at 08:00 CEST.</p>\n\n<p>Schlesser and other contributors will be available in the <a href=\"https://wordpress.slack.com/messages/C02RP4T41\">WP-CLI Slack channel</a> all day and on the project&#8217;s <a href=\"https://github.com/wp-cli/\">GitHub site</a> to answer questions and help people contribute to the project. </p>\n\n<p>From 16:00-18:00 CEST, Schlesser will host a video call that&#8217;s open to everyone where people can join in, discuss issues, and visually work through pull requests. The goal is to reach 20 pull requests that have been merged during the event. A post on <a href=\"https://make.wordpress.org/cli/\">Make/CLI blog</a> will be published once WP-CLI Hack Day concludes summarizing any progress that was made.</p>\n\n<p>To prepare prospecting contributors for the event, Schlesser has published a <a href=\"https://make.wordpress.org/cli/2018/07/14/contributing-to-wp-cli/\">detailed guide</a> on how to contribute to WP-CLI.</p>\n\n<p>Folks can follow along on Twitter using the hashtag <a href=\"https://twitter.com/hashtag/hackwpcli?src=hash\">#hackwpcli</a>. If Hack CLI Day is successful, more events will likely be created in the future to cover more time zones. </p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Wed, 18 Jul 2018 16:47:21 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:13:\"Jeff Chandler\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:49;a:6:{s:4:\"data\";s:11:\"\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:1:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:30:\"HeroPress: Making A Safe Place\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"https://heropress.com/?p=2589\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:105:\"https://heropress.com/making-a-safe-place/#utm_source=rss&utm_medium=rss&utm_campaign=making-a-safe-place\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:2907:\"<img width=\"960\" height=\"480\" src=\"https://s20094.pcdn.co/wp-content/uploads/2017/07/071917-1024x512.jpg\" class=\"attachment-large size-large wp-post-image\" alt=\"Pull Quote: The WordPress community is, slowly but surely, helping me get rid of my ingrown fear of the unknown, of others.\" /><p>Being a remote worker makes it easy to hide from a world that you find scary or dangerous. If you&#8217;re never in the presence of other people they can&#8217;t hit you. Retreat becomes so easy. Marius Jensen from Sola, Norway grew up in a society that did not care for him, and made him want to hide from the world forever.</p>\n<p>The WordPress community gave him a safe place to talk to people online, and after time, in person. WordCamps became a place of safety and compassion. This isn&#8217;t the case for all people, but it is for many people, in many places. Check out Marius&#8217; essay from last July, about finding a safe place.</p>\n<blockquote class=\"wp-embedded-content\"><p><a href=\"https://heropress.com/essays/becoming-myself-again/\">Becoming Myself Again</a></p></blockquote>\n<p></p>\n<div class=\"rtsocial-container rtsocial-container-align-right rtsocial-horizontal\"><div class=\"rtsocial-twitter-horizontal\"><div class=\"rtsocial-twitter-horizontal-button\"><a title=\"Tweet: Making A Safe Place\" class=\"rtsocial-twitter-button\" href=\"https://twitter.com/share?text=Making%20A%20Safe%20Place&via=heropress&url=https%3A%2F%2Fheropress.com%2Fmaking-a-safe-place%2F\" rel=\"nofollow\" target=\"_blank\"></a></div></div><div class=\"rtsocial-fb-horizontal fb-light\"><div class=\"rtsocial-fb-horizontal-button\"><a title=\"Like: Making A Safe Place\" class=\"rtsocial-fb-button rtsocial-fb-like-light\" href=\"https://www.facebook.com/sharer.php?u=https%3A%2F%2Fheropress.com%2Fmaking-a-safe-place%2F\" rel=\"nofollow\" target=\"_blank\"></a></div></div><div class=\"rtsocial-linkedin-horizontal\"><div class=\"rtsocial-linkedin-horizontal-button\"><a class=\"rtsocial-linkedin-button\" href=\"https://www.linkedin.com/shareArticle?mini=true&url=https%3A%2F%2Fheropress.com%2Fmaking-a-safe-place%2F&title=Making+A+Safe+Place\" rel=\"nofollow\" target=\"_blank\" title=\"Share: Making A Safe Place\"></a></div></div><div class=\"rtsocial-pinterest-horizontal\"><div class=\"rtsocial-pinterest-horizontal-button\"><a class=\"rtsocial-pinterest-button\" href=\"https://pinterest.com/pin/create/button/?url=https://heropress.com/making-a-safe-place/&media=https://heropress.com/wp-content/uploads/2017/07/071917-150x150.jpg&description=Making A Safe Place\" rel=\"nofollow\" target=\"_blank\" title=\"Pin: Making A Safe Place\"></a></div></div><a rel=\"nofollow\" class=\"perma-link\" href=\"https://heropress.com/making-a-safe-place/\" title=\"Making A Safe Place\"></a></div><p>The post <a rel=\"nofollow\" href=\"https://heropress.com/making-a-safe-place/\">Making A Safe Place</a> appeared first on <a rel=\"nofollow\" href=\"https://heropress.com\">HeroPress</a>.</p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Wed, 18 Jul 2018 08:00:42 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}}}}}}}}}}}}s:4:\"type\";i:128;s:7:\"headers\";O:42:\"Requests_Utility_CaseInsensitiveDictionary\":1:{s:7:\"\0*\0data\";a:8:{s:6:\"server\";s:5:\"nginx\";s:4:\"date\";s:29:\"Sat, 11 Aug 2018 19:53:43 GMT\";s:12:\"content-type\";s:8:\"text/xml\";s:4:\"vary\";s:15:\"Accept-Encoding\";s:13:\"last-modified\";s:29:\"Sat, 11 Aug 2018 19:30:27 GMT\";s:15:\"x-frame-options\";s:10:\"SAMEORIGIN\";s:4:\"x-nc\";s:9:\"HIT ord 1\";s:16:\"content-encoding\";s:4:\"gzip\";}}s:5:\"build\";s:14:\"20130911040210\";}", "no");
